 INSERT INTO `bk_article` VALUES ('18','PHP给源代码加密的几种方法','虽说分享是传统的美德，但我们有时候辛辛苦苦写一些程序只是为了卖点小钱，挣点辛苦费，也防止有些没有道德的人倒卖，所以我们不得不对我们的程序进行加密，以下我们来介绍一下如何通过PHP的自定义函数来加密我们的PHP源代码。','33','&lt;p&gt;虽说分享是传统的美德，但我们有时候辛辛苦苦写一些程序只是为了卖点小钱，挣点辛苦费，也防止有些没有道德的人倒卖，所以我们不得不对我们的程序进行加密，以下我们来介绍一下如何通过PHP的自定义函数来加密我们的PHP源代码。&lt;/p&gt;&lt;p&gt;加密方法一：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php&amp;nbsp;&amp;nbsp;
&amp;nbsp;function&amp;nbsp;encode_file_contents($filename)&amp;nbsp;{&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$type=strtolower(substr(strrchr($filename,&amp;#39;.&amp;#39;),1));&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(&amp;#39;php&amp;#39;&amp;nbsp;==&amp;nbsp;$type&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;is_file($filename)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;is_writable($filename))&amp;nbsp;{&amp;nbsp;//&amp;nbsp;如果是PHP文件&amp;nbsp;并且可写&amp;nbsp;则进行压缩编码&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$contents&amp;nbsp;=&amp;nbsp;file_get_contents($filename);&amp;nbsp;//&amp;nbsp;判断文件是否已经被编码处理&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$contents&amp;nbsp;=&amp;nbsp;php_strip_whitespace($filename);&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;去除PHP头部和尾部标识&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$headerPos&amp;nbsp;=&amp;nbsp;strpos($contents,&amp;#39;&amp;lt;?php&amp;#39;);&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$footerPos&amp;nbsp;=&amp;nbsp;strrpos($contents,&amp;#39;?&amp;gt;&amp;#39;);&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$contents&amp;nbsp;=&amp;nbsp;substr($contents,&amp;nbsp;$headerPos&amp;nbsp;+&amp;nbsp;5,&amp;nbsp;$footerPos&amp;nbsp;-&amp;nbsp;$headerPos);&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$encode&amp;nbsp;=&amp;nbsp;base64_encode(gzdeflate($contents));&amp;nbsp;//&amp;nbsp;开始编码&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$encode&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;lt;?php&amp;#39;.&amp;quot;n&amp;nbsp;eval(gzinflate(base64_decode(&amp;quot;.&amp;quot;&amp;#39;&amp;quot;.$encode.&amp;quot;&amp;#39;&amp;quot;.&amp;quot;)));nn?&amp;gt;&amp;quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;file_put_contents($filename,&amp;nbsp;$encode);&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;false;&amp;nbsp;&amp;nbsp;
&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;
&amp;nbsp;//调用函数&amp;nbsp;&amp;nbsp;
&amp;nbsp;$filename&amp;nbsp;=&amp;nbsp;&amp;#39;dam.php&amp;#39;;&amp;nbsp;&amp;nbsp;
&amp;nbsp;encode_file_contents($filename);&amp;nbsp;&amp;nbsp;
&amp;nbsp;echo&amp;nbsp;&amp;quot;OK,加密完成！&amp;quot;&amp;nbsp;
&amp;nbsp;?&amp;gt;&lt;/pre&gt;&lt;p&gt;加密方法二：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php&amp;nbsp;&amp;nbsp;
&amp;nbsp;
&amp;nbsp;function&amp;nbsp;RandAbc($length&amp;nbsp;=&amp;nbsp;&amp;quot;&amp;quot;)&amp;nbsp;{&amp;nbsp;//&amp;nbsp;返回随机字符串&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;&amp;quot;ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz&amp;quot;;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;str_shuffle($str);&amp;nbsp;&amp;nbsp;
&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;
&amp;nbsp;$filename&amp;nbsp;=&amp;nbsp;&amp;#39;index.php&amp;#39;;&amp;nbsp;//要加密的文件&amp;nbsp;&amp;nbsp;
&amp;nbsp;$T_k1&amp;nbsp;=&amp;nbsp;RandAbc();&amp;nbsp;//随机密匙1&amp;nbsp;&amp;nbsp;
&amp;nbsp;$T_k2&amp;nbsp;=&amp;nbsp;RandAbc();&amp;nbsp;//随机密匙2&amp;nbsp;&amp;nbsp;
&amp;nbsp;$vstr&amp;nbsp;=&amp;nbsp;file_get_contents($filename);&amp;nbsp;&amp;nbsp;
&amp;nbsp;$v1&amp;nbsp;=&amp;nbsp;base64_encode($vstr);&amp;nbsp;&amp;nbsp;
&amp;nbsp;$c&amp;nbsp;=&amp;nbsp;strtr($v1,&amp;nbsp;$T_k1,&amp;nbsp;$T_k2);&amp;nbsp;//根据密匙替换对应字符。&amp;nbsp;&amp;nbsp;
&amp;nbsp;$c&amp;nbsp;=&amp;nbsp;$T_k1.$T_k2.$c;&amp;nbsp;&amp;nbsp;
&amp;nbsp;$q1&amp;nbsp;=&amp;nbsp;&amp;quot;O00O0O&amp;quot;;&amp;nbsp;&amp;nbsp;
