&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$enc_data&amp;nbsp;.=&amp;nbsp;$this-&amp;gt;_long2str($w[0]);&amp;nbsp;&amp;nbsp;
