 INSERT INTO `bk_article` VALUES ('26','快速高效遍历文件和文件夹','之前php代码遍历文件和文件夹代码，之前用的是比较常见的代码单个函数遍历文件夹，快速高效遍历文件和文件夹glob和scandir遍历文件夹和文件PHP代码','121','&lt;p&gt;之前php代码遍历文件和文件夹代码，之前用的是比较常见的代码&lt;/p&gt;&lt;p&gt;单个函数遍历文件夹&lt;/p&gt;&lt;p&gt;glob和scandir&lt;/p&gt;&lt;p&gt;遍历文件夹和文件PHP代码&lt;/p&gt;&lt;style type=&quot;text/css&quot;&gt;p.p5 {margin: 0.0px 0.0px 0.0px 0.0px; font: 16.0px Helvetica; color: #555555; -webkit-text-stroke: #555555; background-color: #ffffff}
li.li1 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica; color: #66d9ef; -webkit-text-stroke: #66d9ef; background-color: #272822}
li.li2 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica; color: #f8f8f2; -webkit-text-stroke: #bebec5; background-color: #272822}
li.li3 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica; color: #f92659; -webkit-text-stroke: #f92659; background-color: #272822}
li.li4 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica; color: #f92659; -webkit-text-stroke: #66d9ef; background-color: #272822}
li.li6 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica; color: #a6e22e; -webkit-text-stroke: #a6e22e; background-color: #272822}
span.s1 {color: #f92659; -webkit-text-stroke: 0px #000000}
span.s2 {font-kerning: none; color: #f92659; -webkit-text-stroke: 0px #f92659}
span.s3 {font-kerning: none}
span.s4 {font-kerning: none; color: #f8f8f2; -webkit-text-stroke: 0px #f8f8f2}
span.s5 {font-kerning: none; -webkit-text-stroke: 0px #f8f8f2}
span.s6 {-webkit-text-stroke: 0px #bebec5}
span.s7 {color: #f92659; -webkit-text-stroke: 0px #bebec5}
span.s8 {font-kerning: none; color: #e6db74; -webkit-text-stroke: 0px #e6db74}
span.s9 {color: #f8f8f2; -webkit-text-stroke: 0px #bebec5}
span.s10 {font-kerning: none; color: #66d9ef; -webkit-text-stroke: 0px #66d9ef}
span.s11 {font-kerning: none; -webkit-text-stroke: 0px #f92659}
span.s12 {font-kerning: none; color: #66d9ef}
span.s13 {-webkit-text-stroke: 0px #555555}
span.s14 {font-kerning: none; color: #a6e22e; -webkit-text-stroke: 0px #a6e22e}
span.s15 {color: #f92659; -webkit-text-stroke: 0px #555555}
span.s16 {color: #66d9ef; -webkit-text-stroke: 0px #bebec5}
ul.ul1 {list-style-type: none}&lt;/style&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;pre&gt;function&amp;nbsp;my_scandir($dir)
{
$files&amp;nbsp;=&amp;nbsp;array();
if&amp;nbsp;(&amp;nbsp;$handle&amp;nbsp;=&amp;nbsp;opendir($dir)&amp;nbsp;)&amp;nbsp;{
while&amp;nbsp;(&amp;nbsp;($file&amp;nbsp;=&amp;nbsp;readdir($handle))&amp;nbsp;!==&amp;nbsp;false&amp;nbsp;)
{
if&amp;nbsp;(&amp;nbsp;$file&amp;nbsp;!=&amp;nbsp;&amp;quot;..&amp;quot;&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$file&amp;nbsp;!=&amp;nbsp;&amp;quot;.&amp;quot;&amp;nbsp;)&amp;nbsp;{
if&amp;nbsp;(&amp;nbsp;is_dir($dir&amp;nbsp;.&amp;nbsp;&amp;quot;/&amp;quot;&amp;nbsp;.&amp;nbsp;$file)&amp;nbsp;)&amp;nbsp;{
$files[$file]&amp;nbsp;=&amp;nbsp;my_scandir($dir&amp;nbsp;.&amp;nbsp;&amp;quot;/&amp;quot;&amp;nbsp;.&amp;nbsp;$file);
}&amp;nbsp;&amp;nbsp;else&amp;nbsp;{
$files[]&amp;nbsp;=&amp;nbsp;$file;
}
}
}
closedir($handle);
return&amp;nbsp;$files;
}
}&lt;/pre&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;上面的代码在文件很多的时候就会有问题，没有办法正常执行，&lt;/p&gt;&lt;p&gt;快速高效遍历文件和文件夹方法&lt;/p&gt;&lt;p&gt;遍历文件&lt;/p&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;pre&gt;$dir&amp;nbsp;=&amp;nbsp;new&amp;nbsp;DirectoryIterator(dirname(__FILE__));
foreach&amp;nbsp;($dir&amp;nbsp;as&amp;nbsp;$fileinfo)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$fileinfo-&amp;gt;getFilename()&amp;nbsp;.&amp;nbsp;&amp;quot;n&amp;quot;;
}&lt;/pre&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;遍历文件夹和文件&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;test($path&amp;nbsp;=&amp;nbsp;&amp;#39;./&amp;#39;){&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$file&amp;nbsp;=&amp;nbsp;new&amp;nbsp;FilesystemIterator($path);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach&amp;nbsp;($file&amp;nbsp;as&amp;nbsp;$fileinfo)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$fileinfo-&amp;gt;getFilename()&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;lt;/br&amp;gt;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($fileinfo-&amp;gt;isDir()){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;test($path&amp;nbsp;.&amp;nbsp;$fileinfo-&amp;gt;getFilename()&amp;nbsp;.&amp;nbsp;&amp;#39;/&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','1','2016-11-09 18:16:43','0','0','36','0');/* MySQLReback Separation */
