 INSERT INTO `bk_article` VALUES ('141','作用取得客户端的ip、地理信息、浏览器、本地真实IP','作用取得客户端的ip、地理信息、浏览器、本地真实IP','115','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//作用取得客户端的ip、地理信息、浏览器、本地真实IP
&amp;nbsp;class&amp;nbsp;get_gust_info&amp;nbsp;{&amp;nbsp;
&amp;nbsp;&amp;nbsp;////获得访客浏览器类型
&amp;nbsp;&amp;nbsp;function&amp;nbsp;GetBrowser(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;])){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(preg_match(&amp;#39;/MSIE/i&amp;#39;,$br))&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;MSIE&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Firefox/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Firefox&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Chrome/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Chrome&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Safari/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Safari&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Opera/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Opera&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Other&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$br;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;获取浏览器信息失败！&amp;quot;;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;////获得访客浏览器语言
&amp;nbsp;&amp;nbsp;function&amp;nbsp;GetLang(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_ACCEPT_LANGUAGE&amp;#39;])){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;HTTP_ACCEPT_LANGUAGE&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;substr($lang,0,5);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(preg_match(&amp;quot;/zh-cn/i&amp;quot;,$lang)){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;&amp;quot;简体中文&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif(preg_match(&amp;quot;/zh/i&amp;quot;,$lang)){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;&amp;quot;繁体中文&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;&amp;quot;English&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$lang;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;获取浏览器语言失败！&amp;quot;;}
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;////获取访客操作系统
&amp;nbsp;&amp;nbsp;function&amp;nbsp;GetOs(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;])){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(preg_match(&amp;#39;/win/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Windows&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/mac/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;MAC&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/linux/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Linux&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/unix/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Unix&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/bsd/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;BSD&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Other&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$OS;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;获取访客操作系统信息失败！&amp;quot;;}&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;////获得访客真实ip
&amp;nbsp;&amp;nbsp;function&amp;nbsp;Getip(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;quot;HTTP_CLIENT_IP&amp;quot;])){&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;quot;HTTP_CLIENT_IP&amp;quot;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;])){&amp;nbsp;//获取代理ip
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ips&amp;nbsp;=&amp;nbsp;explode(&amp;#39;,&amp;#39;,$_SERVER[&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;if($ip){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ips&amp;nbsp;=&amp;nbsp;array_unshift($ips,$ip);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;count($ips);
&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=0;$i&amp;lt;$count;$i++){&amp;nbsp;ip=&amp;quot;&amp;quot;&amp;nbsp;tip=&amp;quot;=&amp;quot;127.0.0.1&amp;quot;){&amp;quot;&amp;nbsp;:=&amp;quot;&amp;quot;&amp;nbsp;return=&amp;quot;&amp;quot;&amp;nbsp;this-=&amp;quot;&amp;quot;&amp;gt;get_onlineip();&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$tip;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;////获得本地真实IP
&amp;nbsp;&amp;nbsp;function&amp;nbsp;get_onlineip()&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mip&amp;nbsp;=&amp;nbsp;file_get_contents(&amp;quot;http://city.ip138.com/city0.asp&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($mip){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;preg_match(&amp;quot;/[.*]/&amp;quot;,$mip,$sip);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$p&amp;nbsp;=&amp;nbsp;array(&amp;quot;/[/&amp;quot;,&amp;quot;/]/&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;preg_replace($p,&amp;quot;&amp;quot;,$sip[0]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;获取本地IP失败！&amp;quot;;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;////根据ip获得访客所在地地名
&amp;nbsp;&amp;nbsp;function&amp;nbsp;Getaddress($ip=&amp;#39;&amp;#39;){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(empty($ip)){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;$this-&amp;gt;Getip();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;$ipadd&amp;nbsp;=&amp;nbsp;file_get_contents(&amp;quot;http://int.dpool.sina.com.cn/iplookup/iplookup.php?ip=&amp;quot;.$ip);//根据新浪api接口获取
&amp;nbsp;&amp;nbsp;&amp;nbsp;if($ipadd){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$charset&amp;nbsp;=&amp;nbsp;iconv(&amp;quot;gbk&amp;quot;,&amp;quot;utf-8&amp;quot;,$ipadd);&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;preg_match_all(&amp;quot;/[x{4e00}-x{9fa5}]+/u&amp;quot;,$charset,$ipadds);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$ipadds;&amp;nbsp;&amp;nbsp;&amp;nbsp;//返回一个二维数组
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;addree&amp;nbsp;is&amp;nbsp;none&amp;quot;;}&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;}
&amp;nbsp;$gifo&amp;nbsp;=&amp;nbsp;new&amp;nbsp;get_gust_info();
&amp;nbsp;echo&amp;nbsp;&amp;quot;你的ip:&amp;quot;.$gifo-&amp;gt;Getip();
&amp;nbsp;echo&amp;nbsp;&amp;quot;
所在地：&amp;quot;;
&amp;nbsp;$ipadds&amp;nbsp;=&amp;nbsp;$gifo-&amp;gt;Getaddress();
&amp;nbsp;foreach($ipadds[0]&amp;nbsp;as&amp;nbsp;$value){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;rn&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;.iconv(&amp;quot;utf-8&amp;quot;,&amp;quot;gbk&amp;quot;,$value);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;}
&amp;nbsp;
&amp;nbsp;echo&amp;nbsp;&amp;quot;
