 INSERT INTO `bk_article` VALUES ('142','php获取图片详细信息的方法（宽 高 格式 大小）','php获取图片信息的方法','114','&lt;p&gt;&amp;lt;?php&lt;br/&gt;function getImageInfo($img) //$img为图像文件绝对路径&lt;br/&gt;{&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $img_info = getimagesize($img);&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; switch ($img_info[2]) {&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; case 1:&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $imgtype = &amp;quot;GIF&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; break;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; case 2:&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $imgtype = &amp;quot;JPG&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; break;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; case 3:&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $imgtype = &amp;quot;PNG&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; break;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $img_type = $imgtype . &amp;quot;图像&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $img_size = ceil(filesize($img) / 1000) . &amp;quot;k&amp;quot;; //获取文件大小&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $new_img_info = array(&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;quot;width&amp;quot; =&amp;gt; $img_info[0],&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;quot;height&amp;quot; =&amp;gt; $img_info[1],&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;quot;type&amp;quot; =&amp;gt; $img_type,&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;quot;size&amp;quot; =&amp;gt; $img_size&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; );&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print &amp;quot; width&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print $img_info[0];&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print &amp;quot; height&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print $img_info[1];&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print &amp;quot; format&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print $img_type;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print &amp;quot; size&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print $img_size;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; print $new_img_info;&lt;br/&gt;}&lt;br/&gt;&amp;nbsp;&lt;br/&gt;$img = &amp;quot;/www/htdocs/images/jf.gif&amp;quot;;&lt;br/&gt;getImageInfo($img);&lt;br/&gt;?&amp;gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;','20','2016-11-15 14:33:57','0','0','31','0');/* MySQLReback Separation */
