//echo&amp;nbsp;&amp;nbsp;$db-&amp;gt;toExtJson(&amp;#39;test&amp;#39;,0,20);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;

//备份&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
