?&amp;gt;&lt;/pre&gt;&lt;p&gt;整表备份：&lt;/p&gt;&lt;pre&gt;$link&amp;nbsp;=&amp;nbsp;mysql_connect(DB_HOST,DB_USER,DB_PASS);
$tables&amp;nbsp;=&amp;nbsp;mysql_list_tables(DB_NAME);
$cachetables&amp;nbsp;=&amp;nbsp;array();&amp;nbsp;$tableselected&amp;nbsp;=&amp;nbsp;array();
while&amp;nbsp;($table&amp;nbsp;=&amp;nbsp;mysql_fetch_row($tables))
{
&amp;nbsp;&amp;nbsp;&amp;nbsp;$cachetables[$table[0]]&amp;nbsp;=&amp;nbsp;$table[0];
&amp;nbsp;&amp;nbsp;&amp;nbsp;$tableselected[$table[0]]&amp;nbsp;=&amp;nbsp;1;
}
$table&amp;nbsp;=&amp;nbsp;$cachetables;
$filename&amp;nbsp;=&amp;nbsp;&amp;nbsp;DB_NAME&amp;nbsp;.&amp;nbsp;&amp;quot;_&amp;quot;&amp;nbsp;.&amp;nbsp;date(&amp;quot;Y_m_d_H_i_s&amp;quot;)&amp;nbsp;.&amp;nbsp;&amp;quot;.sql&amp;quot;;
$path&amp;nbsp;=&amp;nbsp;&amp;quot;sql/&amp;quot;&amp;nbsp;.&amp;nbsp;$filename;
$filehandle&amp;nbsp;=&amp;nbsp;fopen($path,&amp;nbsp;&amp;quot;w&amp;quot;);
$result&amp;nbsp;=&amp;nbsp;mysql_query(&amp;quot;SHOW&amp;nbsp;tables&amp;quot;);
while&amp;nbsp;($currow&amp;nbsp;=&amp;nbsp;mysql_fetch_array($result))
{
&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(isset($table[$currow[0]]))
&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;sqldumptable($currow[0],&amp;nbsp;$filehandle);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fwrite($filehandle,&amp;nbsp;&amp;quot;nnn&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
}
fclose($filehandle);
$update_data&amp;nbsp;=&amp;nbsp;array(&amp;#39;filename&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$filename,&amp;nbsp;&amp;#39;postdate&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;mktime());
$db-&amp;gt;insert(&amp;#39;backup_db&amp;#39;,&amp;nbsp;$update_data);
//&amp;nbsp;data&amp;nbsp;dump&amp;nbsp;functions
function&amp;nbsp;sqldumptable($table,&amp;nbsp;$fp&amp;nbsp;=&amp;nbsp;0)
{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;=&amp;nbsp;&amp;quot;DROP&amp;nbsp;TABLE&amp;nbsp;IF&amp;nbsp;EXISTS&amp;nbsp;&amp;quot;&amp;nbsp;.&amp;nbsp;$table&amp;nbsp;.&amp;nbsp;&amp;quot;;n&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;mysql_fetch_array(mysql_query(&amp;quot;SHOW&amp;nbsp;CREATE&amp;nbsp;TABLE&amp;nbsp;&amp;quot;&amp;nbsp;.&amp;nbsp;$table));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//echo&amp;nbsp;&amp;quot;SHOW&amp;nbsp;CREATE&amp;nbsp;TABLE&amp;nbsp;$table&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;.=&amp;nbsp;$result[1]&amp;nbsp;.&amp;nbsp;&amp;quot;;rn&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($fp)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fwrite($fp,&amp;nbsp;$tabledump);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$tabledump;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;get&amp;nbsp;data
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$rows&amp;nbsp;=&amp;nbsp;mysql_query(&amp;quot;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;&amp;quot;&amp;nbsp;.&amp;nbsp;$table);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;$numfields=$DB-&amp;gt;num_fields($rows);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$numfields&amp;nbsp;=&amp;nbsp;mysql_num_fields($rows);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;($row&amp;nbsp;=&amp;nbsp;mysql_fetch_array($rows))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;=&amp;nbsp;&amp;quot;INSERT&amp;nbsp;INTO&amp;nbsp;&amp;quot;&amp;nbsp;.&amp;nbsp;$table&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;VALUES(&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$fieldcounter&amp;nbsp;=&amp;nbsp;-1;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$firstfield&amp;nbsp;=&amp;nbsp;1;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;get&amp;nbsp;each&amp;nbsp;field&amp;#39;s&amp;nbsp;data
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;(++$fieldcounter&amp;nbsp;&amp;lt;&amp;nbsp;$numfields)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!$firstfield)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;.=&amp;nbsp;&amp;quot;,&amp;nbsp;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$firstfield&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!isset($row[$fieldcounter]))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;.=&amp;nbsp;&amp;quot;NULL&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;.=&amp;nbsp;&amp;quot;&amp;#39;&amp;quot;&amp;nbsp;.&amp;nbsp;mysql_escape_string($row[$fieldcounter])&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;#39;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tabledump&amp;nbsp;.=&amp;nbsp;&amp;quot;);n&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($fp)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fwrite($fp,&amp;nbsp;$tabledump);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$tabledump;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;mysql_free_result($rows);
}&lt;/pre&gt;&lt;p&gt;导入数据库&lt;/p&gt;&lt;pre&gt;&amp;lt;?php
