 INSERT INTO `bk_article` VALUES ('132','PHP文件操作','php文件操作函数：
fopen:打开文件或URL
fclose:关闭已打开的文件
fgetc:获取一个字符
fgets:获取一行字符串
fgetss：同上，过滤掉HTML和PHP标记
file:读整个文件到数组中
fread:读文件','76','&lt;p&gt;&lt;span style=&quot;font-family: sans-serif;&quot;&gt;php文件操作函数：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fopen:打开文件或URL&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fclose:关闭已打开的文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fgetc:获取一个字符&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fgets:获取一行字符串&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fgetss：同上，过滤掉HTML和PHP标记&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;file:读整个文件到数组中&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fread:读文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fwrite:写文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;copy:拷贝文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;unlink：删除文件&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;参数说明：&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;resource fopen(文件名，文件打开模式)&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;打开一个文件或者URL，成功返回资源指针，失败返回false。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;r&amp;#39; 开文件方式为只读，文件指针指到开始处。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;r+&amp;#39; 开文件方式为可读写，文件指针指到开始处。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;w&amp;#39; 开文件方式为写入，文件指针指到开始处，并将原文件的长度设为 0。若文件不存在，则建立新文件。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;w+&amp;#39; 开文件方式为可读写，文件指针指到开始处，并将原文件的长度设为 0。若文件不存在，则建立新文件。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;a&amp;#39; 开文件方式为写入，文件指针指到文件最后。若文件不存在，则建立新文件。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;a+&amp;#39; 开文件方式为可读写，文件指针指到文件最后。若文件不存在，则建立新文件。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;#39;b&amp;#39; 若操作系统的文字及二进位文件不同，则可以用此参数，UNIX 系统不需要使用本参数。&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$handle=fopen(&amp;quot;/home/file.txt&amp;quot;,&amp;quot;r&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$handle=fopen(&amp;quot;c:\\homefile.gif&amp;quot;,&amp;quot;wb&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$handle=fopen(&amp;quot;http://www.baidu.com&amp;quot;,&amp;quot;r&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$handle=fopen(&amp;quot;ftp://192.168.102.16/php/a.txt&amp;quot;,&amp;quot;w&amp;quot;);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;//bool fclose(resource handle);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;//关闭一个已经打开的文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;//示例&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$handle=fopen(“a.txt”,”r”);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fclose($handle);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;/*string fgetc(resource handle)&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;从文件指针所指向的文件中读取一个字符，返回包含一个字符的字符串。如果读取到文件结束符EOF，则返回FALSE*/&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;if(file_exists(&amp;quot;a.txt&amp;quot;)){&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$fp=fopen(&amp;quot;a.txt&amp;quot;,&amp;quot;r&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;while($c=fgetc($fp)){&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;echo nl2br($c);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;}&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;/*fgets(resource handle[,int length])&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;读取一行。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;当指定length时，返回最多length-1个字符。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;读取到换行符、EOF、length-1字节后停止。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;fgetss读取时去掉HTML和PHP标记*/&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;if(file_exists(&amp;quot;a.txt&amp;quot;)){&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$fp=fopen(&amp;quot;a.txt&amp;quot;,&amp;quot;r&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;while($c=fgets($fp)){&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;echo nl2br($c);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;/*array file(string 文件名)&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;把整个文件读取到一个数组中。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;数组中每个元素存放一行。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;示例：&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$a=file(&amp;quot;a.txt&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;print_r($a);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;注：var_dump函数显示关于一个或多个表达式的结构信息，包括表达式的类型与值。数组将递归展开值，通过缩进显示其结构。&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;print_r 函数打印关于变量的易于理解的信息。 */&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;/*fread(handle,length)&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;读取文件最多Length个字节&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;示例：*/&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$fp=fopen(&amp;quot;a.txt&amp;quot;,&amp;quot;r&amp;quot;);&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;$contents=fread($fp,filesize(&amp;quot;a.txt&amp;quot;));&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;echo $contents;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;/*fwrite(resource handle,string str[,int length])&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;把str内容写入到文件指针里，指定length时，写入length个字节停止&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;课堂练习：创建一个文件num.txt，往文件里面写入1-100，数字之间用空格间隔。*/&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;/*bool copy(string sourcefile,string destfile)&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;示例：copy(“a.txt”,”b.txt”);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;unlink(string 文件名)&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;删除文件&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;示例：unlink(&amp;quot;b.txt&amp;quot;);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;*/&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre style=&quot;text-align: left;&quot;&gt;$num&amp;nbsp;=&amp;nbsp;count(glob(&amp;quot;/www/*&amp;quot;,GLOB_ONLYDIR));
//统计匹配指定路径下的文件&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:54','0','0','14','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('133','08CMS系统本地测试授权方法','总体思路：
要分四个大步骤完成，改两个文件，执行授权，后台系统设置-&gt;网站参数页面提交一下。','75','&lt;p style=&quot;text-align: left;&quot;&gt;总体思路：&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;要分四个大步骤完成，改两个文件，执行授权，后台系统设置-&amp;gt;网站参数页面提交一下。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;一、改mconfigs.cac.php文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;1、打开网站目录下 &amp;quot;dynamiccachemconfigs.cac.php&amp;quot; 这个文件；&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;2、搜索关键字 hosturl 找到：&amp;#39;hosturl&amp;#39; =&amp;gt; &amp;#39;http://www.old-domain.com&amp;#39;，将&amp;quot;www.old-domain.com&amp;quot;改成您要启用的新域名，比如：&amp;#39;hosturl&amp;#39; =&amp;gt; &amp;#39;http://www.new-domain.com&amp;#39;；&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;3、搜索关键字 cms_top 找到：&amp;#39;cms_top&amp;#39; =&amp;gt; &amp;#39;old-domain.com&amp;#39;，将&amp;quot;old-domain.com&amp;quot;删除，变成：&amp;#39;cms_top&amp;#39; =&amp;gt; &amp;#39;&amp;#39;。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;二、改base.inc.php文件&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;1、打开网站目录下 &amp;quot;base.inc.php&amp;quot; 这个文件；&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;2、搜索关键字 ckpre 找到：$ckpre = &amp;#39;LMf_&amp;#39;，将&amp;quot;LMf_&amp;quot;修改、添加或者删除几个字符都可以，比如：$ckpre = &amp;#39;xLMf_&amp;#39;；&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;3、搜索关键字 excache_prefix 找到：$excache_prefix = &amp;#39;k8r7oW_&amp;#39;，将&amp;quot;k8r7oW_&amp;quot;修改、添加或者删除几个字符都可以，比如：$excache_prefix = &amp;#39;7oW_&amp;#39;。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;三、执行授权&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;执行http://www.new-domain.com/install/license.php&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;显示license updated !说明执行成功。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;注意：&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;执行成功不等于就是授权成功，这里主要确定下域名是否有授权，一般会在程序安装包里集成了授权文件，也有的是另外发的一个授权包，如果是另外发的授权包需要将授权 包中的dynamic文件夹覆盖到程序安装目录。&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;四、登录后台提交&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;现在正常的话就可以登录后台了，打开【系统设置-&amp;gt;网站参数】页面，点击下面的的【提交】按钮，完成域名更改。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:54','0','0','16','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('134','php5.2 openssl 扩展库开启不了的原因','php5.2 openssl 扩展库开启不了的原因，网上找了许多方法，但安装网上的说去处理了，但是还解决不了，最后自己摸索才发现一个extras文件夹里面也有openssl的文件，才解决了问题。','74','&lt;p style=&quot;text-align: left;&quot;&gt;php5.2 openssl 扩展库开启不了的原因，网上找了许多方法，但安装网上的说去处理了，但是还解决不了，最后自己摸索才发现一个extras文件夹里面也有openssl的文件，才解决了问题。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;今天安装一个08cms程序，发现需要开启openssl扩展库，但是弄了半天就是启动不了，后来乱点了之后，才发现丢失了一个dll文件。修复了一下重启了一下apache就OK了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;下面是正常开启openssl的方法：&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;1、打开PHP目录，找到配置文件php.ini&amp;nbsp;在中把“&amp;nbsp;;extension=php_openssl.dll”前面的“;”去掉&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;2、复制php安装目录中的libeay32.dll和ssleay32.dll复制到系统盘里的system32目录下、复制php_openssl.dll到系统盘里的system32目录下、重启IIS或者apache环境 这样就可以打开php的openssl_open支持

一般正常的情况下，根据上面的方法就可以开启了。&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;但PHP5.2中，还有一个文件夹，里面的openssl文件夹中的openssl.exe文件，点击可以启动。其实我也不知道它是干嘛的，但我点击运行的时候，电脑提示我libeay32.dll文件丢失了，然后电脑管家提示我叫我修复一下，我修复一下，重启一下apache就可以正常开启了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:54','0','0','15','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('135','Apache2.2.x+PHP5.3.x+MySQL5.1.x 搭建WMAP运行环境','今天第二次搭建PHP运行环境，记得半年前第一次搭建的时候非常顺利，但是今天却遇到不少麻烦，在此记录下来，留以备用。','73','&lt;h3 dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;今天第二次搭建PHP运行环境，记得半年前第一次搭建的时候非常顺利，但是今天却遇到不少麻烦，在此记录下来，留以备用。&lt;br/&gt;&lt;/span&gt;&lt;/h3&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;开始搭建PHP运行环境之前，首先准备以下软件：&lt;br/&gt;1.Apache&amp;nbsp;HTTP&amp;nbsp;Server&amp;nbsp;2.2.17&amp;nbsp;：&lt;/span&gt;&lt;a href=&quot;http://mirror.bjtu.edu.cn/apache/httpd/binaries/win32/httpd-2.2.17-win32-x86-no_ssl.msi&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;httpd-2.2.17-win32-x86-no_ssl.msi&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://httpd.apache.org/&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;官方下载地址&lt;br/&gt;&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;2.PHP&amp;nbsp;5.3.4：&lt;/span&gt;&lt;a href=&quot;http://windows.php.net/downloads/releases/php-5.3.4-Win32-VC6-x86.zip&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;php-5.3.4-Win32-VC6-x86.zip&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://windows.php.net/download/&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;官方下载地址&lt;br/&gt;&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;3.MySql&amp;nbsp;5.1.53：&lt;/span&gt;&lt;a href=&quot;http://mirror.services.wisc.edu/mysql/Downloads/MySQL-5.1/mysql-5.1.53-win32.msi&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;mysql-5.1.53-win32.msi&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://www.mysql.com/downloads/mysql/&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;官方下载地址&lt;br/&gt;&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;4.PhpMyAdmin&amp;nbsp;3.3.8.1：&lt;/span&gt;&lt;a href=&quot;http://nchc.dl.sourceforge.net/project/phpmyadmin/phpMyAdmin/3.3.8.1/phpMyAdmin-3.3.8.1-all-languages.zip&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;phpMyAdmin-3.3.8.1-all-languages.zip&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://www.phpmyadmin.net/home_page/downloads.php&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;官方下载地址&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第一步.创建相应目录&lt;br/&gt;D:Apache2.2&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;用于安装Apache&amp;nbsp;HTTP&amp;nbsp;Server&amp;nbsp;2.2.17&lt;br/&gt;D:php&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;用于安装PHP&amp;nbsp;5.3.4&lt;br/&gt;D:mysql&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;用于安装MySql&amp;nbsp;5.1.53&lt;br/&gt;D:www&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;作为网站根目录&lt;br/&gt;D:wwwphpMyAdmin&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;作为phpMyAdmin根目录&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第二步.安装Apache&amp;nbsp;HTTP&amp;nbsp;Server&amp;nbsp;2.2.17&lt;br/&gt;1.运行下载好的&amp;quot;httpd-2.2.17-win32-x86-no_ssl.msi&amp;quot;，进入Apache&amp;nbsp;HTTP&amp;nbsp;Server&amp;nbsp;2.2.17的安装向导界面，开始进行安装，安装到路径&amp;quot;D:Apache2.2&amp;quot;。&lt;br/&gt;2.设置Server&amp;nbsp;Information时，Network&amp;nbsp;Domain填写localhost，Server&amp;nbsp;Name填写localhost，Administrator&amp;#39;s&amp;nbsp;Email&amp;nbsp;Address填写任意邮箱，其中联系电子邮件地址会在当系统故障时提供给访问者，三条信息均可任意填写，选择&amp;quot;for&amp;nbsp;All&amp;nbsp;Users,on&amp;nbsp;Port&amp;nbsp;80,as&amp;nbsp;a&amp;nbsp;Service&amp;nbsp;--Recommended&amp;quot;。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;3.安装完成后，测试Apache&amp;nbsp;&lt;br/&gt;HTTP&amp;nbsp;Server是否安装成功，在桌面右下角的Apache&amp;nbsp;Service&amp;nbsp;Monitor中启动Apache2.2服务，然后打开浏览器,访问&amp;quot;&lt;/span&gt;&lt;a href=&quot;http://localhost/&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;http://localhost&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;quot;或者&amp;quot;&lt;/span&gt;&lt;a href=&quot;http://127.0.0.1/&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;http://127.0.0.1&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;quot;，页面显示&amp;quot;It&amp;nbsp;Works!&amp;quot;，则表示安装成功。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第三步.安装PHP&amp;nbsp;5.3.4&lt;br/&gt;1.将下载好的&amp;quot;php-5.3.4-Win32-VC6-x86.zip&amp;quot;解压到&amp;quot;D:php&amp;quot;目录下。&lt;br/&gt;2.将php.ini-development文件名修改为php.ini。&lt;br/&gt;3.找到;default_charset&amp;nbsp;=&amp;nbsp;&amp;quot;iso-8859-1&amp;quot;，并在下面一行添加default_charset&amp;nbsp;=&amp;nbsp;&amp;quot;utf-8&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;;&amp;nbsp;PHP&amp;#39;s&amp;nbsp;default&amp;nbsp;character&amp;nbsp;set&amp;nbsp;is&amp;nbsp;set&amp;nbsp;to&amp;nbsp;empty.&lt;br/&gt;;&amp;nbsp;http://php.net/default-charset&lt;br/&gt;;default_charset&amp;nbsp;=&amp;nbsp;&amp;quot;iso-8859-1&amp;quot;&lt;br/&gt;default_charset&amp;nbsp;=&amp;nbsp;&amp;quot;utf-8&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;4.找到;&amp;nbsp;extension_dir&amp;nbsp;=&amp;nbsp;&amp;quot;./&amp;quot;，并将其修改为extension_dir&amp;nbsp;=&amp;nbsp;&amp;quot;D:/php/ext&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;;&amp;nbsp;Directory&amp;nbsp;in&amp;nbsp;which&amp;nbsp;the&amp;nbsp;loadable&amp;nbsp;extensions&amp;nbsp;(modules)&amp;nbsp;reside.&lt;br/&gt;;&amp;nbsp;http://php.net/extension-dir&lt;br/&gt;extension_dir&amp;nbsp;=&amp;nbsp;&amp;quot;D:/php/ext&amp;quot;&lt;br/&gt;;&amp;nbsp;On&amp;nbsp;windows:&lt;br/&gt;;&amp;nbsp;extension_dir&amp;nbsp;=&amp;nbsp;&amp;quot;ext&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;5.找到;extension=php_bz2.dll，其下一长串类似的列表，即为可供选择加载的模块，可以根据需要加载，加载方法为去掉前面的;号，这里我只加载了几个模块，如下。&lt;br/&gt;;&amp;nbsp;Windows&amp;nbsp;Extensions&lt;br/&gt;;&amp;nbsp;Note&amp;nbsp;that&amp;nbsp;ODBC&amp;nbsp;support&amp;nbsp;is&amp;nbsp;built&amp;nbsp;in,&amp;nbsp;so&amp;nbsp;no&amp;nbsp;dll&amp;nbsp;is&amp;nbsp;needed&amp;nbsp;for&amp;nbsp;it.&lt;br/&gt;;&amp;nbsp;Note&amp;nbsp;that&amp;nbsp;many&amp;nbsp;DLL&amp;nbsp;files&amp;nbsp;are&amp;nbsp;located&amp;nbsp;in&amp;nbsp;the&amp;nbsp;extensions/&amp;nbsp;(PHP&amp;nbsp;4)&amp;nbsp;ext/&amp;nbsp;(PHP&amp;nbsp;5)&lt;br/&gt;;&amp;nbsp;extension&amp;nbsp;folders&amp;nbsp;as&amp;nbsp;well&amp;nbsp;as&amp;nbsp;the&amp;nbsp;separate&amp;nbsp;PECL&amp;nbsp;DLL&amp;nbsp;download&amp;nbsp;(PHP&amp;nbsp;5).&lt;br/&gt;;&amp;nbsp;Be&amp;nbsp;sure&amp;nbsp;to&amp;nbsp;appropriately&amp;nbsp;set&amp;nbsp;the&amp;nbsp;extension_dir&amp;nbsp;directive.&lt;br/&gt;;&lt;br/&gt;;extension=php_bz2.dll&lt;br/&gt;;extension=php_curl.dll&lt;br/&gt;;extension=php_fileinfo.dll&lt;br/&gt;extension=php_gd2.dll&lt;br/&gt;;extension=php_gettext.dll&lt;br/&gt;;extension=php_gmp.dll&lt;br/&gt;;extension=php_intl.dll&lt;br/&gt;;extension=php_imap.dll&lt;br/&gt;;extension=php_interbase.dll&lt;br/&gt;;extension=php_ldap.dll&lt;br/&gt;extension=php_mbstring.dll&lt;br/&gt;;extension=php_exif.dll&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;&amp;nbsp;Must&amp;nbsp;be&amp;nbsp;after&amp;nbsp;mbstring&amp;nbsp;as&amp;nbsp;it&amp;nbsp;depends&amp;nbsp;on&amp;nbsp;it&lt;br/&gt;extension=php_mysql.dll&lt;br/&gt;;extension=php_mysqli.dll&lt;br/&gt;;extension=php_oci8.dll&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;&amp;nbsp;Use&amp;nbsp;with&amp;nbsp;Oracle&amp;nbsp;10gR2&amp;nbsp;Instant&amp;nbsp;Client&lt;br/&gt;;extension=php_oci8_11g.dll&amp;nbsp;&amp;nbsp;;&amp;nbsp;Use&amp;nbsp;with&amp;nbsp;Oracle&amp;nbsp;11g&amp;nbsp;Instant&amp;nbsp;Client&lt;br/&gt;;extension=php_openssl.dll&lt;br/&gt;;extension=php_pdo_firebird.dll&lt;br/&gt;;extension=php_pdo_mssql.dll&lt;br/&gt;;extension=php_pdo_mysql.dll&lt;br/&gt;;extension=php_pdo_oci.dll&lt;br/&gt;;extension=php_pdo_odbc.dll&lt;br/&gt;;extension=php_pdo_pgsql.dll&lt;br/&gt;;extension=php_pdo_sqlite.dll&lt;br/&gt;;extension=php_pgsql.dll&lt;br/&gt;;extension=php_phar.dll&lt;br/&gt;;extension=php_pspell.dll&lt;br/&gt;;extension=php_shmop.dll&lt;br/&gt;;extension=php_snmp.dll&lt;br/&gt;;extension=php_soap.dll&lt;br/&gt;;extension=php_sockets.dll&lt;br/&gt;;extension=php_sqlite.dll&lt;br/&gt;;extension=php_sqlite3.dll&lt;br/&gt;;extension=php_sybase_ct.dll&lt;br/&gt;;extension=php_tidy.dll&lt;br/&gt;extension=php_xmlrpc.dll&lt;br/&gt;;extension=php_xsl.dll&lt;br/&gt;;extension=php_zip.dll&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;6.找到memory_limit&amp;nbsp;=&amp;nbsp;,修改数值可以限定php脚本最多可占用的内存大小。&lt;br/&gt;;&amp;nbsp;Maximum&amp;nbsp;amount&amp;nbsp;of&amp;nbsp;memory&amp;nbsp;a&amp;nbsp;script&amp;nbsp;may&amp;nbsp;consume&amp;nbsp;(128MB)&lt;br/&gt;;&amp;nbsp;http://php.net/memory-limit&lt;br/&gt;memory_limit&amp;nbsp;=&amp;nbsp;128M&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;7.修改环境变量，将php安装文件夹位置添加到windows系统路径中，我的电脑-&amp;gt;属性-&amp;gt;高级-&amp;gt;环境变量-&amp;gt;系统变量-&amp;gt;Path-&amp;gt;编辑,在已有变量值的最后添加;D:php;D:phpext。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第四步.编辑Apache的配置文件httpd.conf以加载php模块&lt;br/&gt;1.打开D:Apache2.2confhttpd.conf，记得先备份。&lt;br/&gt;2.找到DocumentRoot&amp;nbsp;&amp;quot;D:/Apache2.2/htdocs&amp;quot;，修改为DocumentRoot&amp;nbsp;&amp;quot;D:/www&amp;quot;，此目录作为网站所在文件夹。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;#&amp;nbsp;DocumentRoot:&amp;nbsp;The&amp;nbsp;directory&amp;nbsp;out&amp;nbsp;of&amp;nbsp;which&amp;nbsp;you&amp;nbsp;will&amp;nbsp;serve&amp;nbsp;your&lt;br/&gt;#&amp;nbsp;documents.&amp;nbsp;By&amp;nbsp;default,&amp;nbsp;all&amp;nbsp;requests&amp;nbsp;are&amp;nbsp;taken&amp;nbsp;from&amp;nbsp;this&amp;nbsp;directory,&amp;nbsp;but&lt;br/&gt;#&amp;nbsp;symbolic&amp;nbsp;links&amp;nbsp;and&amp;nbsp;aliases&amp;nbsp;may&amp;nbsp;be&amp;nbsp;used&amp;nbsp;to&amp;nbsp;point&amp;nbsp;to&amp;nbsp;other&amp;nbsp;locations.&lt;br/&gt;#&lt;br/&gt;DocumentRoot&amp;nbsp;&amp;quot;D:/www&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;3.找到&lt;/span&gt;&lt;directory&gt;&lt;/directory&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;，修改为&lt;/span&gt;&lt;directory&gt;&lt;/directory&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;#&amp;nbsp;This&amp;nbsp;should&amp;nbsp;be&amp;nbsp;changed&amp;nbsp;to&amp;nbsp;whatever&amp;nbsp;you&amp;nbsp;set&amp;nbsp;DocumentRoot&amp;nbsp;to.&lt;br/&gt;#&lt;br/&gt;&amp;lt;Directory&amp;nbsp;&amp;quot;D:/www&amp;quot;&amp;gt;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;4.找到DirectoryIndex&amp;nbsp;index.html，修改为DirectoryIndex&amp;nbsp;index.php&amp;nbsp;index.html。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;#&amp;nbsp;DirectoryIndex:&amp;nbsp;sets&amp;nbsp;the&amp;nbsp;file&amp;nbsp;that&amp;nbsp;Apache&amp;nbsp;will&amp;nbsp;serve&amp;nbsp;if&amp;nbsp;a&amp;nbsp;directory&lt;br/&gt;#&amp;nbsp;is&amp;nbsp;requested.&lt;br/&gt;#&lt;br/&gt;&lt;/span&gt;&lt;ifmodule dir_module=&quot;&quot;&gt;&lt;/ifmodule&gt;&lt;br/&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;DirectoryIndex&amp;nbsp;index.php&amp;nbsp;index.html&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;5.在配置文件最后添加以下内容：&lt;br/&gt;LoadModule&amp;nbsp;php5_module&amp;nbsp;D:/php/php5apache2_2.dll&lt;br/&gt;PHPiniDir&amp;nbsp;&amp;quot;D:/php&amp;quot;&lt;br/&gt;AddType&amp;nbsp;application/x-httpd-php&amp;nbsp;.php&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;(注意：这里php与.php之间有一个小空格，为了这个空格我查了两个小时，最后才找到Apache无法重新启动的原因就在这里)&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;6.保存配置文件httpd.conf，并重启Apache。&lt;br/&gt;7.配置完成后，在D:www目录下新建index.php&lt;br/&gt;&amp;lt;?php&lt;br/&gt;phpinfo();&lt;br/&gt;?&amp;gt;&lt;br/&gt;8.测试配置是否成功，访问&amp;quot;&lt;/span&gt;&lt;a href=&quot;http://localhost/&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;http://localhost&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;&amp;quot;，若显示php相关信息，则配置成功。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第五步.安装MySql&amp;nbsp;5.1.53&amp;nbsp;&lt;br/&gt;1.运行下载好的mysql-5.1.53-win32.msi，自定义完全安装到&amp;quot;D:mysql&amp;quot;。&lt;br/&gt;&lt;br/&gt;2.出现MySQL.com&amp;nbsp;Sign-Up页面，询问是否要注册一个mysql.com的账号，或是使用已有的账号登陆mysql.com，选择跳过注册&amp;quot;Skip&amp;nbsp;Sign-Up&amp;quot;。&lt;br/&gt;&lt;br/&gt;3.然后提示完成安装&amp;quot;Wizard&amp;nbsp;Completed&amp;quot;，勾选&amp;quot;Configure&amp;nbsp;the&amp;nbsp;MySQL&amp;nbsp;Server&amp;nbsp;now&amp;quot;,然后&amp;quot;Finish&amp;quot;。&lt;br/&gt;&lt;br/&gt;4.选择配置方式，&amp;quot;Detailed&amp;nbsp;Configuration(手动精确配置)&amp;quot;、&amp;quot;Standard&amp;nbsp;Configuration(标准配置)&amp;quot;，这里选择&amp;quot;Detailed&amp;nbsp;Configuration&amp;quot;，方便熟悉配置过程。&lt;br/&gt;&lt;br/&gt;5.选择服务器类型，&amp;quot;Developer&amp;nbsp;Machine(开发测试类，mysql占用很少资源)&amp;quot;、&amp;quot;Server&amp;nbsp;Machine(服务器类型，mysql占用较多资源)&amp;quot;、&amp;quot;Dedicated&amp;nbsp;MySQL&amp;nbsp;Server&amp;nbsp;Machine(专门的数据库服务器，mysql占用所有可用资源)&amp;quot;，大家根据自己的类型选择了，一般选&amp;quot;Server&amp;nbsp;Machine&amp;quot;，不会太少，也不会占满。&lt;br/&gt;&lt;br/&gt;6.选择mysql数据库的大致用途，&amp;quot;Multifunctional&amp;nbsp;Database(通用多功能型，好)&amp;quot;、&amp;quot;Transactional&amp;nbsp;Database&amp;nbsp;Only(服务器类型，专注于事务处理，一般)&amp;quot;、&amp;quot;Non-Transactional&amp;nbsp;Database&amp;nbsp;Only(非事务处理型，较简单，主要做一些监控、记数用，对MyISAM数据类型的支持仅限于non-transactional)，随自己的用途而选择了，我这里选择&amp;quot;Transactional&amp;nbsp;Database&amp;nbsp;Only&amp;quot;。&lt;br/&gt;&lt;br/&gt;7.对InnoDB&amp;nbsp;Tablespace进行配置，就是为InnoDB&amp;nbsp;数据库文件选择一个存储空间，如果修改了，要记住位置，重装的时候要选择一样的地方，否则可能会造成数据库损坏，当然，对数据库做个备份就没问题了，这里不详述。这里不作修改，使用用默认位置。&lt;br/&gt;&lt;br/&gt;8.选择您的网站的一般mysql访问量，同时连接的数目，&amp;quot;Decision&amp;nbsp;Support(DSS)/OLAP(20个左右)&amp;quot;、&amp;quot;Online&amp;nbsp;Transaction&amp;nbsp;Processing(OLTP)(500个左右)&amp;quot;、&amp;quot;Manual&amp;nbsp;Setting(手动设置，自己输一个数)&amp;quot;，这里选择&amp;quot;Online&amp;nbsp;Transaction&amp;nbsp;Processing(OLTP)&amp;quot;。&lt;br/&gt;&lt;br/&gt;9.选择是否启用TCP/IP连接，设定端口，如果不启用，就只能在自己的机器上访问mysql数据库了，这里选择启用，把前面的勾打上，Port&amp;nbsp;Number：3306。&lt;br/&gt;&lt;br/&gt;10.这里比较重要，是对mysql默认数据库语言编码进行设置，第一个是西文编码，第二个是多字节的通用utf8编码，都不是我们通用的编码，这里选择第三个，然后在Character&amp;nbsp;Set那里选择或填入&amp;quot;gbk&amp;quot;，当然也可以用&amp;quot;gb2312&amp;quot;，区别就是gbk的字库容量大，包括了gb2312的所有汉字，并且加上了繁体字、和其它乱七八糟的字——使用mysql的时候，在执行数据操作命令之前运行一次&amp;quot;SET&amp;nbsp;NAMES&amp;nbsp;GBK;&amp;quot;(运行一次就行了，GBK可以替换为其它值，视这里的设置而定)，就可以正常的使用汉字(或其它文字)了，否则不能正常显示汉字。&lt;br/&gt;&lt;br/&gt;11.选择是否将mysql安装为windows服务，还可以指定Service&amp;nbsp;Name(服务标识名称)，是否将mysql的bin目录加入到Windows&amp;nbsp;PATH(加入后，就可以直接使用bin下的文件，而不用指出目录名，比如连接，&amp;quot;mysql.exe&amp;nbsp;-uusername&amp;nbsp;-ppassword;&amp;quot;就可以了，不用指出mysql.exe的完整地址，很方便)，这里全部勾选，Service&amp;nbsp;Name不变。&lt;br/&gt;&lt;br/&gt;12.询问是否要修改默认root用户(超级管理)的密码(默认为空)，&amp;quot;New&amp;nbsp;root&amp;nbsp;password&amp;quot;如果要修改，就在此填入新密码(如果是重装，并且之前已经设置了密码，在这里更改密码可能会出错，请留空，并将&amp;quot;Modify&amp;nbsp;Security&amp;nbsp;Settings&amp;quot;前面的勾去掉，安装配置完成后另行修改密码)，&amp;quot;Confirm(再输一遍)&amp;quot;内再填一次，防止输错。&amp;quot;Enable&amp;nbsp;root&amp;nbsp;access&amp;nbsp;from&amp;nbsp;remote&amp;nbsp;machines(是否允许root用户在其它的机器上登陆，如果要安全，就不要勾上，如果要方便，就勾上它)&amp;quot;。最后&amp;quot;Create&amp;nbsp;An&amp;nbsp;Anonymous&amp;nbsp;Account(新建一个匿名用户，匿名用户可以连接数据库，不能操作数据，包括查询)&amp;quot;，一般就不用勾了，设置完毕。&lt;br/&gt;&lt;br/&gt;13.确认设置无误，如果有误，选择&amp;quot;Back&amp;quot;返回检查。选择&amp;quot;Execute&amp;quot;使设置生效。&lt;br/&gt;&lt;br/&gt;14.设置完毕，按&amp;quot;Finish&amp;quot;结束mysql的安装与配置——这里有一个比较常见的错误，就是不能&amp;quot;Start&amp;nbsp;service&amp;quot;，一般出现在以前有安装mysql的服务器上，解决的办法，先保证以前安装的mysql服务器彻底卸载掉了；不行的话，检查是否按上面一步所说，之前的密码是否有修改，照上面的操作；如果依然不行，将mysql安装目录下的data文件夹备份，然后删除，在安装完成后，将安装生成的&amp;nbsp;data文件夹删除，备份的data文件夹移回来，再重启mysql服务就可以了，这种情况下，可能需要将数据库检查一下，然后修复一次，防止数据出错。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第六步.测试php是否能够连接MySQL&lt;br/&gt;在D:www目录下新建testconn.php文件&lt;br/&gt;&amp;lt;?php&lt;br/&gt;mysql_connect(&amp;quot;localhost&amp;quot;,&amp;quot;root&amp;quot;,&amp;quot;&amp;quot;)&amp;nbsp;or&amp;nbsp;die(mysql_error());&lt;br/&gt;echo&amp;quot;Connected&amp;nbsp;to&amp;nbsp;MySql&lt;br/&gt;&amp;quot;;&lt;br/&gt;?&amp;gt;&lt;br/&gt;然后访问&lt;/span&gt;&lt;a href=&quot;http://localhost/testconn.php&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;http://localhost/testconn.php&lt;/span&gt;&lt;/a&gt;&lt;br/&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;若显示&amp;quot;Connected&amp;nbsp;to&amp;nbsp;MySql&amp;quot;，则表示连接成功。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;第七步.安装PhpMyAdmin&amp;nbsp;3.3.8.1&lt;br/&gt;1.将下载好的phpMyAdmin-3.3.8.1-all-languages.zip解压到&amp;quot;D:wwwphpMyAdmin&amp;quot;。&lt;br/&gt;2.打开编辑D:wwwphpMyAdminlibrariesconfig.default.php。&lt;br/&gt;3.修改访问网址&lt;br/&gt;$cfg[&amp;#39;PmaAbsoluteUri&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;http://localhost/phpMyAdmin&amp;#39;;&lt;br/&gt;4.修改主机信息&lt;br/&gt;$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;host&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;localhost&amp;#39;;&lt;br/&gt;填写&amp;nbsp;localhost&amp;nbsp;&amp;nbsp;或&amp;nbsp;MySQL&amp;nbsp;&amp;nbsp;所在服务器的&amp;nbsp;ip&amp;nbsp;地址，如果&amp;nbsp;MySQL&amp;nbsp;和该&amp;nbsp;phpMyAdmin&amp;nbsp;在同一服务器，则按默认&amp;nbsp;localhost。&lt;br/&gt;$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;port&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;&lt;br/&gt;MySQL&amp;nbsp;端口，默认为&amp;nbsp;3306，保留为空即可，如果您安装&amp;nbsp;MySQL&amp;nbsp;时使用了其它的端口，需要在这里填写。&lt;br/&gt;5.修改用户名和密码&lt;br/&gt;$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;user&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;root&amp;#39;;&lt;br/&gt;$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;password&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;&lt;br/&gt;6.修改认证方法&lt;br/&gt;$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;auth_type&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;cookie&amp;#39;;&lt;br/&gt;在此有四种模式可供选择，cookie，http，HTTP，config&lt;br/&gt;config方式即输入，phpMyAdmin的访问网址即可直接进入，无需输入用户名和密码，是不安全的，不推荐使用。当该项设置为cookie，http或HTTP时，登录phpMyAdmin需要数据用户名和密码进行验证，具体如下：&lt;br/&gt;PHP安装模式为Apache，可以使用HTTP和cookie；&lt;br/&gt;PHP安装模式为CGI，可以使用cookie。&lt;br/&gt;7.修改短语密码&lt;br/&gt;$cfg[&amp;#39;blowfish_secret&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;eric_wan&amp;#39;;&lt;br/&gt;如果认证方法设置为cookie，就需要设置短语密码，不能留空，否则会在登录phpMyAdmin&amp;nbsp;时提示错误&amp;quot;配置文件现在需要绝密的短语密码(blowfish_secret)&amp;quot;。&lt;br/&gt;8.安装配置完成后，访问&lt;/span&gt;&lt;a href=&quot;http://localhost/phpMyAdmin&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;http://localhost/phpMyAdmin&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;，能够成功登录即安装成功。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;至此，Apache2.2.x+PHP5.3.x+MySQL5.1.x&amp;nbsp;在Windows下搭建PHP运行环境成功。&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p dir=&quot;ltr&quot; style=&quot;text-align: left; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-decoration: none;&quot;&gt;原文来自：&lt;/span&gt;&lt;a href=&quot;http://wenku.baidu.com/view/9ba171f1f90f76c661371a73.html?re=view&quot; target=&quot;_blank&quot; title=&quot;Apache2.2.x+PHP5.3.x+MySQL5.1.x 搭建WMAP运行环境&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;http://wenku.baidu.com/view/9ba171f1f90f76c661371a73.html?re=view&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;','19','2016-11-15 10:57:54','0','0','34','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('136','A​p​a​c​h​e​2​.​4​.​7​_​+​_​P​H​P​5​.​5​.​7​_​+​_​M​y​S​Q​L​','到官网下载最新版的：
http://www.apache.org/    httpd-2.4.7-win32-VC11.zip
http://www.php.net/       php-5.5.7-Win32-VC11-x86.zip
http://www.mysql.com/     mysql-5.6.15-win32.zip','72','&lt;p&gt;到官网下载最新版的：&lt;/p&gt;&lt;p&gt;http://www.apache.org/&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;httpd-2.4.7-win32-VC11.zip&lt;/p&gt;&lt;p&gt;http://www.php.net/&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;php-5.5.7-Win32-VC11-x86.zip&lt;/p&gt;&lt;p&gt;http://www.mysql.com/&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;mysql-5.6.15-win32.zip&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;#################&lt;/p&gt;&lt;p&gt;Apache配置&lt;/p&gt;&lt;p&gt;#################&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;在Apache根目录中的conf目录中找到&lt;span style=&quot;font-family: sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;httpd.conf文件。复制备份到当前目录。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;用记事本或Editplus打开httpd.conf。&lt;/p&gt;&lt;p&gt;（注意：在下面配置中涉及到路径时。记得把&amp;quot;&amp;quot;全部换成&amp;quot;/&amp;quot;）&lt;span style=&quot;font-family: sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;ServerRoot&amp;nbsp;&lt;/p&gt;&lt;p&gt;后面的&amp;nbsp;&amp;quot;c:/Apache24&amp;quot;&amp;nbsp;&lt;/p&gt;&lt;p&gt;改成&amp;nbsp;安装apache的目录。（如：&amp;quot;D:/apache&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;#LoadModule&lt;/p&gt;&lt;p&gt;在一系列#LoadModule行的最后一行下面加上&lt;/p&gt;&lt;p&gt;LoadModule&amp;nbsp;php5_module&amp;nbsp;D:/php/php5apache2_4.dll&amp;nbsp;&amp;nbsp;#注意：php5apache2_4.dll中的2_4是对应apache的版本号的，如果是apache2.2版本则是php5apache2_2.dll&lt;/p&gt;&lt;p&gt;PHPIniDir&amp;nbsp;D:/php&amp;nbsp;&amp;nbsp;#该目录是php.ini所在目录即安装PHP的根目录。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;#ServerName&lt;/p&gt;&lt;p&gt;把后面的www.example.com:80&lt;/p&gt;&lt;p&gt;改为：127.0.0.1:80&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;DocumentRoot&lt;/p&gt;&lt;p&gt;把后面的&amp;quot;c:/Apache24/htdocs&amp;quot;&lt;/p&gt;&lt;p&gt;改为你的网站根目录（如：&amp;quot;D:/Workspace/MyWeb/htdocs&amp;quot;）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;&amp;lt;Directory&lt;/p&gt;&lt;p&gt;把后面的&amp;quot;c:/Apache24/htdocs&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;改为你的网站根目录（如：&amp;quot;D:/Workspace/MyWeb/htdocs&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;&lt;ifmodule dir_module=&quot;&quot;&gt;&lt;/ifmodule&gt;&lt;/p&gt;&lt;p&gt;在&amp;nbsp;DirectoryIndex&amp;nbsp;index.html　后面加上index.php&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;ScriptAlias&lt;/p&gt;&lt;p&gt;把后面的路径&amp;quot;c:/Apache24/cgi-bin/&amp;quot;&lt;/p&gt;&lt;p&gt;改为安装apache的cgi-bin目录（如：&amp;quot;D:/apache/cgi-bin/&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;AddType&amp;nbsp;application/x-gzip&amp;nbsp;.gz&amp;nbsp;.tgz&lt;/p&gt;&lt;p&gt;在下面一行加上&lt;/p&gt;&lt;p&gt;AddType&amp;nbsp;application/x-httpd-php&amp;nbsp;.php&lt;/p&gt;&lt;p&gt;;;;;;;;;;;;;;;;;;;;;;;;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PHP配置&lt;/p&gt;&lt;p&gt;;;;;;;;;;;;;;;;;;;;;;;;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到PHP根目录里的php.ini-development文件，复制备份到根目录。&lt;/p&gt;&lt;p&gt;把备份文件重命名为php.ini&lt;/p&gt;&lt;p&gt;用记事本或Editplus打开。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;;extension=php_mysql.dll&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;使PHP可使用mysql&lt;/p&gt;&lt;p&gt;;extension=php_mysqli.dll&amp;nbsp;&amp;nbsp;&amp;nbsp;;使PHP可使用mysqli&lt;/p&gt;&lt;p&gt;去掉前边的&amp;nbsp;;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;;&amp;nbsp;extension_dir&lt;/p&gt;&lt;p&gt;去掉前面的;并把后面的路径改成php的ext目录&lt;/p&gt;&lt;p&gt;（如：&amp;quot;D:phpext&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;;date.timezone&lt;/p&gt;&lt;p&gt;改为：&lt;/p&gt;&lt;p&gt;date.timezone&amp;nbsp;=&amp;nbsp;Asia/Shanghai&amp;nbsp;&amp;nbsp;;如果不改为这个出现日期会报错&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;error_reporting&amp;nbsp;=&amp;nbsp;E_ALL&amp;nbsp;&lt;/p&gt;&lt;p&gt;加上前边的&amp;nbsp;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;这个是错误报告。如果不注释掉总是报错（此处参考APMServ的配置）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;######################&lt;/p&gt;&lt;p&gt;MySQL配置&lt;/p&gt;&lt;p&gt;######################&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;在mysql的跟目录中找到my-default.ini文件&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;复制备份到当前目录。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;把备份文件重命名为my.ini，&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;用记事本或Editplus打开。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;#&amp;nbsp;&amp;nbsp;basedir&amp;nbsp;=&amp;nbsp;.....&lt;/p&gt;&lt;p&gt;#&amp;nbsp;&amp;nbsp;datadir&amp;nbsp;=&amp;nbsp;.....&lt;/p&gt;&lt;p&gt;#&amp;nbsp;&amp;nbsp;port&amp;nbsp;=&amp;nbsp;.....&lt;/p&gt;&lt;p&gt;改为：&lt;/p&gt;&lt;p&gt;basedir&amp;nbsp;=&amp;nbsp;D:/mysql&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql的根目录&lt;/p&gt;&lt;p&gt;datadir&amp;nbsp;=&amp;nbsp;D:/mysql/data&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql的数据目录&lt;/p&gt;&lt;p&gt;port&amp;nbsp;=&amp;nbsp;3306&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql的端口&lt;/p&gt;&lt;p&gt;并添加：&lt;/p&gt;&lt;p&gt;character_set_server&amp;nbsp;=&amp;nbsp;utf8&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql字符集&lt;/p&gt;&lt;p&gt;[mysql]&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;default-character-set&amp;nbsp;=&amp;nbsp;utf8&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql字符集&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;原文来自：&lt;a href=&quot;http://wenku.baidu.com/view/d9490f1b804d2b160b4ec0a3.html&quot;&gt;http://wenku.baidu.com/view/d9490f1b804d2b160b4ec0a3.html&lt;/a&gt;&lt;/p&gt;','19','2016-11-15 10:57:54','0','0','23','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('341','PHP获取客户端IP的方法','其实在网上使用PHP获取客户端IP的方法有很多种，我之前也收集了一些，不过我这里主要推荐的是thinkphp框架带的方法，毕竟我现在一直使用thinkphp框架，也一直使用它的方法','119','&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;其实在网上使用PHP获取客户端IP的方法有很多种，我之前也收集了一些，不过我这里主要推荐的是thinkphp框架带的方法，毕竟我现在一直使用thinkphp框架，也一直使用它的方法&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;获取客户端IP地址
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;integer&amp;nbsp;$type&amp;nbsp;返回类型&amp;nbsp;0&amp;nbsp;返回IP地址&amp;nbsp;1&amp;nbsp;返回IPV4地址数字
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;mixed
&amp;nbsp;*/
function&amp;nbsp;get_client_ip($type&amp;nbsp;=&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;$type&amp;nbsp;?&amp;nbsp;1&amp;nbsp;:&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;static&amp;nbsp;$ip&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;NULL;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($ip&amp;nbsp;!==&amp;nbsp;NULL)&amp;nbsp;return&amp;nbsp;$ip[$type];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(isset($_SERVER[&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;]))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;explode(&amp;#39;,&amp;#39;,&amp;nbsp;$_SERVER[&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pos&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;array_search(&amp;#39;unknown&amp;#39;,$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(false&amp;nbsp;!==&amp;nbsp;$pos)&amp;nbsp;unset($arr[$pos]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;trim($arr[0]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(isset($_SERVER[&amp;#39;HTTP_CLIENT_IP&amp;#39;]))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;$_SERVER[&amp;#39;HTTP_CLIENT_IP&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(isset($_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;]))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;$_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;IP地址合法验证
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$long&amp;nbsp;=&amp;nbsp;sprintf(&amp;quot;%u&amp;quot;,ip2long($ip));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;$long&amp;nbsp;?&amp;nbsp;array($ip,&amp;nbsp;$long)&amp;nbsp;:&amp;nbsp;array(&amp;#39;0.0.0.0&amp;#39;,&amp;nbsp;0);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$ip[$type];
}&lt;/pre&gt;&lt;p&gt;以上就是thinkphp框架自带的获取客户端IP地址的函数，调用方法&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;get_client_ip();&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;里面的参数你自己也可以根据自己的需求选择是否添加。&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:49','0','0','44','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('342','生成PHP随机数的几种方法','我们下面来介绍多种生成php随机数的方法，大家可以根据自己的开发需求来选择适合自己的函数，如果以下函数还不能满足大家，也可以动手写自己所需要的，欢迎跟博主一起讨论研究。','119','&lt;p&gt;下面这种方式是我经常使用的，比较人性化&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;生成随机字符串
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;int&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;&amp;nbsp;要生成的随机字符串长度
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;随机码类型：0，数字+大写字母；1，数字；2，小写字母；3，大写字母；4，特殊字符；-1，数字+大小写字母+特殊字符
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string
&amp;nbsp;*/
function&amp;nbsp;randCode($length&amp;nbsp;=&amp;nbsp;5,&amp;nbsp;$type&amp;nbsp;=&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;array(1&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;0123456789&amp;quot;,&amp;nbsp;2&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;abcdefghijklmnopqrstuvwxyz&amp;quot;,&amp;nbsp;3&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;ABCDEFGHIJKLMNOPQRSTUVWXYZ&amp;quot;,&amp;nbsp;4&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;~@#$%^&amp;amp;*(){}[]|&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($type&amp;nbsp;==&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array_pop($arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;implode(&amp;quot;&amp;quot;,&amp;nbsp;$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($type&amp;nbsp;==&amp;nbsp;&amp;quot;-1&amp;quot;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;implode(&amp;quot;&amp;quot;,&amp;nbsp;$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;$arr[$type];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;strlen($string)&amp;nbsp;-&amp;nbsp;1;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$length;&amp;nbsp;$i++)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str[$i]&amp;nbsp;=&amp;nbsp;$string[rand(0,&amp;nbsp;$count)];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$code&amp;nbsp;.=&amp;nbsp;$str[$i];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$code;
}&lt;/pre&gt;&lt;p&gt;生成定长随机数：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;function&amp;nbsp;generate_code($length&amp;nbsp;=&amp;nbsp;4)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;rand(pow(10,($length-1)),&amp;nbsp;pow(10,$length)-1);&amp;nbsp;
}&lt;/pre&gt;&lt;p&gt;下面跟上面的效果一样，只是写法不一样而已。&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;function&amp;nbsp;generate_code($length&amp;nbsp;=&amp;nbsp;4)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$min&amp;nbsp;=&amp;nbsp;pow(10&amp;nbsp;,&amp;nbsp;($length&amp;nbsp;-&amp;nbsp;1));&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$max&amp;nbsp;=&amp;nbsp;pow(10,&amp;nbsp;$length)&amp;nbsp;-&amp;nbsp;1;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;rand($min,&amp;nbsp;$max);&amp;nbsp;
}&lt;/pre&gt;&lt;p&gt;如：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;echo&amp;nbsp;generate_code(5);
//输出结果：&amp;nbsp;56482&lt;/pre&gt;&lt;p&gt;再刷新一次，所以数字和顺序都会变动。&lt;/p&gt;&lt;p&gt;以上这两种代码比较简洁，但也只能生成数字，追求代码简洁的可以使用&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;$arr=range(1,10);&amp;nbsp;
shuffle($arr);&amp;nbsp;
foreach($arr&amp;nbsp;as&amp;nbsp;$values)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$values.&amp;quot;,&amp;quot;;&amp;nbsp;
}

//输出结果：10,5,7,8,6,2,9,4,1,3,1&lt;/pre&gt;&lt;p&gt;这种主要是靠php的内置函数range()来生成，生成两个参数范围的随机数，而且他们是可以生成多组数字，这种就比较呆板了，而且不是都放在方法里面，不过大家可以根据需求来使用。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;以下这种跟上面这种没多大区别，只是生成的方法不太一样，主要是靠array_unique函数来生成。&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;$arr=array();&amp;nbsp;
while(count($arr)&amp;lt;10)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr[]=rand(1,10);&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr=array_unique($arr);&amp;nbsp;
}&amp;nbsp;
echo&amp;nbsp;implode(&amp;quot;&amp;nbsp;&amp;quot;,$arr);&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;其他的生成方法我在这里就不多说了，我们只要记住常用的，而且效率比较高的几种就行了，我们在开发的过程中，可以根据需求来写适合我们自己的php随机数函数。&lt;/p&gt;','21','2016-11-15 15:09:49','0','0','88','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('340','PHP生成随机字符串自定义函数详解【支持中文】','PHP生成随机字符串自定义函数详解，其中一个是在网上收集的，另一个非常强大，是thinkphp框架中带的自定义函数rand_string()','119','&lt;p&gt;PHP生成随机字符串自定义函数详解&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;生成随机字符串
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;int&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;&amp;nbsp;要生成的随机字符串长度
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;随机码类型：0，数字+大写字母；1，数字；2，小写字母；3，大写字母；4，特殊字符；-1，数字+大小写字母+特殊字符
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string
&amp;nbsp;*/
function&amp;nbsp;randCode($length&amp;nbsp;=&amp;nbsp;5,&amp;nbsp;$type&amp;nbsp;=&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;array(1&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;0123456789&amp;quot;,&amp;nbsp;2&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;abcdefghijklmnopqrstuvwxyz&amp;quot;,&amp;nbsp;3&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;ABCDEFGHIJKLMNOPQRSTUVWXYZ&amp;quot;,&amp;nbsp;4&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;~@#$%^&amp;amp;*(){}[]|&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($type&amp;nbsp;==&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array_pop($arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;implode(&amp;quot;&amp;quot;,&amp;nbsp;$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($type&amp;nbsp;==&amp;nbsp;&amp;quot;-1&amp;quot;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;implode(&amp;quot;&amp;quot;,&amp;nbsp;$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;$arr[$type];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;strlen($string)&amp;nbsp;-&amp;nbsp;1;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$length;&amp;nbsp;$i++)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str[$i]&amp;nbsp;=&amp;nbsp;$string[rand(0,&amp;nbsp;$count)];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$code&amp;nbsp;.=&amp;nbsp;$str[$i];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$code;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;我们先来大概说一下这个函数的功能&lt;/p&gt;&lt;p&gt;用过randCode()来调用此函数，然后传入参数，$length为要生成随机字符串的长度，也是就字符个数，$type为生成字符串的类型，根据函数里面写的有4种，当然你也可以自己添加其他类型进去，也就是说，每种类型所生成字符串所组成的字符都是由数组所包含的字符而定。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;下面是thinkphp框架中的，我觉得特别强大，可以推荐给大家&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;产生随机字串，可用来自动生成密码&amp;nbsp;默认长度6位&amp;nbsp;字母和数字混合
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;$len&amp;nbsp;长度
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;$type&amp;nbsp;字串类型
&amp;nbsp;*&amp;nbsp;0&amp;nbsp;字母&amp;nbsp;1&amp;nbsp;数字&amp;nbsp;其它&amp;nbsp;混合
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;$addChars&amp;nbsp;额外字符
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string
&amp;nbsp;*/
function&amp;nbsp;rand_string($len=6,$type=&amp;#39;&amp;#39;,$addChars=&amp;#39;&amp;#39;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;#39;&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;switch($type)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;0:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars=&amp;#39;ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz&amp;#39;.$addChars;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;1:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars=&amp;nbsp;str_repeat(&amp;#39;0123456789&amp;#39;,3);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;2:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars=&amp;#39;ABCDEFGHIJKLMNOPQRSTUVWXYZ&amp;#39;.$addChars;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;3:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars=&amp;#39;abcdefghijklmnopqrstuvwxyz&amp;#39;.$addChars;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;4:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars&amp;nbsp;=&amp;nbsp;&amp;quot;们以我到他会作时要动国产的一是工就年阶义发成部民可出能方进在了不和有大这主中人上为来分生对于学下级地个用同行面说种过命度革而多子后自社加小机也经力线本电高量长党得实家定深法表着水理化争现所二起政三好十战无农使性前等反体合斗路图把结第里正新开论之物从当两些还天资事队批点育重其思与间内去因件日利相由压员气业代全组数果期导平各基或月毛然如应形想制心样干都向变关问比展那它最及外没看治提五解系林者米群头意只明四道马认次文通但条较克又公孔领军流入接席位情运器并飞原油放立题质指建区验活众很教决特此常石强极土少已根共直团统式转别造切九你取西持总料连任志观调七么山程百报更见必真保热委手改管处己将修支识病象几先老光专什六型具示复安带每东增则完风回南广劳轮科北打积车计给节做务被整联步类集号列温装即毫知轴研单色坚据速防史拉世设达尔场织历花受求传口断况采精金界品判参层止边清至万确究书术状厂须离再目海交权且儿青才证低越际八试规斯近注办布门铁需走议县兵固除般引齿千胜细影济白格效置推空配刀叶率述今选养德话查差半敌始片施响收华觉备名红续均药标记难存测士身紧液派准斤角降维板许破述技消底床田势端感往神便贺村构照容非搞亚磨族火段算适讲按值美态黄易彪服早班麦削信排台声该击素张密害侯草何树肥继右属市严径螺检左页抗苏显苦英快称坏移约巴材省黑武培著河帝仅针怎植京助升王眼她抓含苗副杂普谈围食射源例致酸旧却充足短划剂宣环落首尺波承粉践府鱼随考刻靠够满夫失包住促枝局菌杆周护岩师举曲春元超负砂封换太模贫减阳扬江析亩木言球朝医校古呢稻宋听唯输滑站另卫字鼓刚写刘微略范供阿块某功套友限项余倒卷创律雨让骨远帮初皮播优占死毒圈伟季训控激找叫云互跟裂粮粒母练塞钢顶策双留误础吸阻故寸盾晚丝女散焊功株亲院冷彻弹错散商视艺灭版烈零室轻血倍缺厘泵察绝富城冲喷壤简否柱李望盘磁雄似困巩益洲脱投送奴侧润盖挥距触星松送获兴独官混纪依未突架宽冬章湿偏纹吃执阀矿寨责熟稳夺硬价努翻奇甲预职评读背协损棉侵灰虽矛厚罗泥辟告卵箱掌氧恩爱停曾溶营终纲孟钱待尽俄缩沙退陈讨奋械载胞幼哪剥迫旋征槽倒握担仍呀鲜吧卡粗介钻逐弱脚怕盐末阴丰雾冠丙街莱贝辐肠付吉渗瑞惊顿挤秒悬姆烂森糖圣凹陶词迟蚕亿矩康遵牧遭幅园腔订香肉弟屋敏恢忘编印蜂急拿扩伤飞露核缘游振操央伍域甚迅辉异序免纸夜乡久隶缸夹念兰映沟乙吗儒杀汽磷艰晶插埃燃欢铁补咱芽永瓦倾阵碳演威附牙芽永瓦斜灌欧献顺猪洋腐请透司危括脉宜笑若尾束壮暴企菜穗楚汉愈绿拖牛份染既秋遍锻玉夏疗尖殖井费州访吹荣铜沿替滚客召旱悟刺脑措贯藏敢令隙炉壳硫煤迎铸粘探临薄旬善福纵择礼愿伏残雷延烟句纯渐耕跑泽慢栽鲁赤繁境潮横掉锥希池败船假亮谓托伙哲怀割摆贡呈劲财仪沉炼麻罪祖息车穿货销齐鼠抽画饲龙库守筑房歌寒喜哥洗蚀废纳腹乎录镜妇恶脂庄擦险赞钟摇典柄辩竹谷卖乱虚桥奥伯赶垂途额壁网截野遗静谋弄挂课镇妄盛耐援扎虑键归符庆聚绕摩忙舞遇索顾胶羊湖钉仁音迹碎伸灯避泛亡答勇频皇柳哈揭甘诺概宪浓岛袭谁洪谢炮浇斑讯懂灵蛋闭孩释乳巨徒私银伊景坦累匀霉杜乐勒隔弯绩招绍胡呼痛峰零柴簧午跳居尚丁秦稍追梁折耗碱殊岗挖氏刃剧堆赫荷胸衡勤膜篇登驻案刊秧缓凸役剪川雪链渔啦脸户洛孢勃盟买杨宗焦赛旗滤硅炭股坐蒸凝竟陷枪黎救冒暗洞犯筒您宋弧爆谬涂味津臂障褐陆啊健尊豆拔莫抵桑坡缝警挑污冰柬嘴啥饭塑寄赵喊垫丹渡耳刨虎笔稀昆浪萨茶滴浅拥穴覆伦娘吨浸袖珠雌妈紫戏塔锤震岁貌洁剖牢锋疑霸闪埔猛诉刷狠忽灾闹乔唐漏闻沈熔氯荒茎男凡抢像浆旁玻亦忠唱蒙予纷捕锁尤乘乌智淡允叛畜俘摸锈扫毕璃宝芯爷鉴秘净蒋钙肩腾枯抛轨堂拌爸循诱祝励肯酒绳穷塘燥泡袋朗喂铝软渠颗惯贸粪综墙趋彼届墨碍启逆卸航衣孙龄岭骗休借&amp;quot;.$addChars;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;default&amp;nbsp;:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;默认去掉了容易混淆的字符oOLl和数字01，要添加请使用addChars参数
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars=&amp;#39;ABCDEFGHIJKMNPQRSTUVWXYZabcdefghijkmnpqrstuvwxyz23456789&amp;#39;.$addChars;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($len&amp;gt;10&amp;nbsp;)&amp;nbsp;{//位数过长重复字符串一定次数
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars=&amp;nbsp;$type==1?&amp;nbsp;str_repeat($chars,$len)&amp;nbsp;:&amp;nbsp;str_repeat($chars,5);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($type!=4)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;str_shuffle($chars);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;substr($chars,0,$len);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;中文随机字
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=0;$i&amp;lt;$len;$i++){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str.=&amp;nbsp;msubstr($chars,&amp;nbsp;floor(mt_rand(0,mb_strlen($chars,&amp;#39;utf-8&amp;#39;)-1)),1);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$str;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:49','0','0','77','0');/* MySQLReback Separation */
