 INSERT INTO `bk_article` VALUES ('335','PHP获取时间差 距离现在的年月日时分秒','PHP获取时间差,我们在开发过程中，这是一个经常使用到的函数，比如说我们想查某个时间段的数据，那就要用到这个函数了。','119','&lt;p&gt;PHP获取时间差,我们在开发过程中，这是一个经常使用到的函数，比如说我们想查某个时间段的数据，那就要用到这个函数了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实例一：&lt;/p&gt;&lt;pre&gt;/**
&amp;nbsp;*&amp;nbsp;功能：获取时间差
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;int&amp;nbsp;$time
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string&amp;nbsp;时间差值
&amp;nbsp;*/
function&amp;nbsp;tranTime($time)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$rtime&amp;nbsp;=&amp;nbsp;date(&amp;quot;m-d&amp;nbsp;H:i&amp;quot;,$time);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$htime&amp;nbsp;=&amp;nbsp;date(&amp;quot;H:i&amp;quot;,$time);

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$time&amp;nbsp;=&amp;nbsp;time()&amp;nbsp;-&amp;nbsp;$time;

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($time&amp;nbsp;&amp;lt;&amp;nbsp;60)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;&amp;#39;刚刚&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;elseif&amp;nbsp;($time&amp;nbsp;&amp;lt;&amp;nbsp;60&amp;nbsp;*&amp;nbsp;60)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$min&amp;nbsp;=&amp;nbsp;floor($time/60);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;$min.&amp;#39;分钟前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;elseif&amp;nbsp;($time&amp;nbsp;&amp;lt;&amp;nbsp;60&amp;nbsp;*&amp;nbsp;60&amp;nbsp;*&amp;nbsp;24)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$h&amp;nbsp;=&amp;nbsp;floor($time/(60*60));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;$h.&amp;#39;小时前&amp;nbsp;&amp;#39;.$htime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;elseif&amp;nbsp;($time&amp;nbsp;&amp;lt;&amp;nbsp;60&amp;nbsp;*&amp;nbsp;60&amp;nbsp;*&amp;nbsp;24&amp;nbsp;*&amp;nbsp;3)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$d&amp;nbsp;=&amp;nbsp;floor($time/(60*60*24));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($d==1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;&amp;#39;昨天&amp;nbsp;&amp;#39;.$rtime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;else
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;&amp;#39;前天&amp;nbsp;&amp;#39;.$rtime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;=&amp;nbsp;$rtime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$str;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实例二：&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;time_tran($the_time){
&amp;nbsp;&amp;nbsp;&amp;nbsp;$now_time&amp;nbsp;=&amp;nbsp;date(&amp;quot;Y-m-d&amp;nbsp;H:i:s&amp;quot;,time()+8*60*60);
&amp;nbsp;&amp;nbsp;&amp;nbsp;$now_time&amp;nbsp;=&amp;nbsp;strtotime($now_time);
&amp;nbsp;&amp;nbsp;&amp;nbsp;$show_time&amp;nbsp;=&amp;nbsp;strtotime($the_time);
&amp;nbsp;&amp;nbsp;&amp;nbsp;$dur&amp;nbsp;=&amp;nbsp;$now_time&amp;nbsp;-&amp;nbsp;$show_time;
&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;0){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$the_time;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;60){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$dur.&amp;#39;秒前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;3600){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;floor($dur/60).&amp;#39;分钟前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;86400){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;floor($dur/3600).&amp;#39;小时前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;259200){//3天内
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;floor($dur/86400).&amp;#39;天前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$the_time;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实例三：&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;format_date($time){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$t=time()-$time;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$f=array(
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;31536000&amp;#39;=&amp;gt;&amp;#39;年&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;2592000&amp;#39;=&amp;gt;&amp;#39;个月&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;604800&amp;#39;=&amp;gt;&amp;#39;星期&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;86400&amp;#39;=&amp;gt;&amp;#39;天&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;3600&amp;#39;=&amp;gt;&amp;#39;小时&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;60&amp;#39;=&amp;gt;&amp;#39;分钟&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;1&amp;#39;=&amp;gt;&amp;#39;秒&amp;#39;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach&amp;nbsp;($f&amp;nbsp;as&amp;nbsp;$k=&amp;gt;$v)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(0&amp;nbsp;!=$c=floor($t/(int)$k))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$c.$v.&amp;#39;前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实例四：&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;formatTime($date)&amp;nbsp;{
$str&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;
$timer&amp;nbsp;=&amp;nbsp;strtotime($date);
$diff&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;REQUEST_TIME&amp;#39;]&amp;nbsp;-&amp;nbsp;$timer;
$day&amp;nbsp;=&amp;nbsp;floor($diff&amp;nbsp;/&amp;nbsp;86400);
$free&amp;nbsp;=&amp;nbsp;$diff&amp;nbsp;%&amp;nbsp;86400;
if($day&amp;nbsp;&amp;gt;&amp;nbsp;0)&amp;nbsp;{
return&amp;nbsp;$day.&amp;quot;天前&amp;quot;;
}else{
if($free&amp;gt;0){
$hour&amp;nbsp;=&amp;nbsp;floor($free&amp;nbsp;/&amp;nbsp;3600);
$free&amp;nbsp;=&amp;nbsp;$free&amp;nbsp;%&amp;nbsp;3600;
if($hour&amp;gt;0){
return&amp;nbsp;$hour.&amp;quot;小时前&amp;quot;;
}else{
if($free&amp;gt;0){
$min&amp;nbsp;=&amp;nbsp;floor($free&amp;nbsp;/&amp;nbsp;60);
$free&amp;nbsp;=&amp;nbsp;$free&amp;nbsp;%&amp;nbsp;60;
if($min&amp;gt;0){
return&amp;nbsp;$min.&amp;quot;分钟前&amp;quot;;
}else{
if($free&amp;gt;0){
return&amp;nbsp;$free.&amp;quot;秒前&amp;quot;;
}else{
return&amp;nbsp;&amp;#39;刚刚&amp;#39;;
}
}
}else{
return&amp;nbsp;&amp;#39;刚刚&amp;#39;;
}
}
}else{
return&amp;nbsp;&amp;#39;刚刚&amp;#39;;
}
}
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实例五：&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;time_tran($the_time){
$now_time&amp;nbsp;=&amp;nbsp;date(&amp;quot;Y-m-d&amp;nbsp;H:i:s&amp;quot;,time()+8*60*60);&amp;nbsp;
$now_time&amp;nbsp;=&amp;nbsp;strtotime($now_time);
$show_time&amp;nbsp;=&amp;nbsp;strtotime($the_time);
$dur&amp;nbsp;=&amp;nbsp;$now_time&amp;nbsp;-&amp;nbsp;$show_time;
if($dur&amp;nbsp;&amp;lt;&amp;nbsp;0){
return&amp;nbsp;$the_time;&amp;nbsp;
}else{
if($dur&amp;nbsp;&amp;lt;&amp;nbsp;60){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$dur.&amp;#39;秒前&amp;#39;;&amp;nbsp;
}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;3600){
&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;floor($dur/60).&amp;#39;分钟前&amp;#39;;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;86400){
&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;floor($dur/3600).&amp;#39;小时前&amp;#39;;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;if($dur&amp;nbsp;&amp;lt;&amp;nbsp;259200){//3天内
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;floor($dur/86400).&amp;#39;天前&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$the_time;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
}
}
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;实例六：&lt;/p&gt;&lt;pre&gt;/*
&amp;nbsp;*&amp;nbsp;author:&amp;nbsp;Solon&amp;nbsp;Ring
&amp;nbsp;*&amp;nbsp;time:&amp;nbsp;2011-11-02
&amp;nbsp;*&amp;nbsp;发博时间计算(年，月，日，时，分，秒)
&amp;nbsp;*&amp;nbsp;$createtime&amp;nbsp;可以是当前时间
&amp;nbsp;*&amp;nbsp;$gettime&amp;nbsp;你要传进来的时间
&amp;nbsp;*/
class&amp;nbsp;Mygettime{
function&amp;nbsp;&amp;nbsp;__construct($createtime,$gettime)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;createtime&amp;nbsp;=&amp;nbsp;$createtime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;gettime&amp;nbsp;=&amp;nbsp;$gettime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;getSeconds()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$this-&amp;gt;createtime-$this-&amp;gt;gettime;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;getMinutes()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;($this-&amp;gt;createtime-$this-&amp;gt;gettime)/(60);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;getHours()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;($this-&amp;gt;createtime-$this-&amp;gt;gettime)/(60*60);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;getDay()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;($this-&amp;gt;createtime-$this-&amp;gt;gettime)/(60*60*24);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;getMonth()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;($this-&amp;gt;createtime-$this-&amp;gt;gettime)/(60*60*24*30);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;getYear()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;($this-&amp;gt;createtime-$this-&amp;gt;gettime)/(60*60*24*30*12);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
function&amp;nbsp;index()
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($this-&amp;gt;getYear()&amp;nbsp;&amp;gt;&amp;nbsp;1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($this-&amp;gt;getYear()&amp;nbsp;&amp;gt;&amp;nbsp;2)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;date(&amp;quot;Y-m-d&amp;quot;,$this-&amp;gt;gettime);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;intval($this-&amp;gt;getYear()).&amp;quot;&amp;nbsp;年前&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
if($this-&amp;gt;getMonth()&amp;nbsp;&amp;gt;&amp;nbsp;1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;intval($this-&amp;gt;getMonth()).&amp;quot;&amp;nbsp;月前&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
if($this-&amp;gt;getDay()&amp;nbsp;&amp;gt;&amp;nbsp;1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;intval($this-&amp;gt;getDay()).&amp;quot;&amp;nbsp;天前&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
if($this-&amp;gt;getHours()&amp;nbsp;&amp;gt;&amp;nbsp;1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;intval($this-&amp;gt;getHours()).&amp;quot;&amp;nbsp;小时前&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
if($this-&amp;gt;getMinutes()&amp;nbsp;&amp;gt;&amp;nbsp;1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;intval($this-&amp;gt;getMinutes()).&amp;quot;&amp;nbsp;分钟前&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
if($this-&amp;gt;getSeconds()&amp;nbsp;&amp;gt;&amp;nbsp;1)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;intval($this-&amp;gt;getSeconds()-1).&amp;quot;&amp;nbsp;秒前&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
}
}
//类的使用实例
/*
&amp;nbsp;*
&amp;nbsp;*&amp;nbsp;调用类输出方式
&amp;nbsp;*
&amp;nbsp;*&amp;nbsp;$a&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Mygettime(time(),strtotime(&amp;#39;-25&amp;nbsp;month&amp;#39;));
&amp;nbsp;*&amp;nbsp;echo&amp;nbsp;iconv(&amp;#39;utf-8&amp;#39;,&amp;nbsp;&amp;#39;gb2312&amp;#39;,&amp;nbsp;$a-&amp;gt;index())?iconv(&amp;#39;utf-8&amp;#39;,&amp;nbsp;&amp;#39;gb2312&amp;#39;,&amp;nbsp;$a-&amp;gt;index()):iconv(&amp;#39;utf-8&amp;#39;,&amp;nbsp;&amp;#39;gb2312&amp;#39;,&amp;nbsp;&amp;#39;当前&amp;#39;);
&amp;nbsp;*
&amp;nbsp;*/&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:48','0','0','17','0');/* MySQLReback Separation */
