 INSERT INTO `bk_article` VALUES ('332','PHP原样输出内容的方法','完整实例/*** 原样输出print_r的内容* @param string    $content   待print_r的内容*/','119','&lt;p&gt;完整实例&lt;/p&gt;&lt;pre&gt;/**
&amp;nbsp;*&amp;nbsp;原样输出print_r的内容
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$content&amp;nbsp;&amp;nbsp;&amp;nbsp;待print_r的内容
&amp;nbsp;*/
function&amp;nbsp;pre($content)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;print_r($content);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;quot;;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:48','0','0','37','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('333','PHP自定义带加密因子的加密方法md5、rc4、ZxingCrypt','现在PHP加密的方法有很多总，一般简单的加密方法都是直接MD5的，但是MD5一些简单的密码也可以通过反编译出来，所以我们现在通过自定义的方法来加密更为安全一些。','119','&lt;h3 style=&quot; font-weight:bolder&quot;&gt;&lt;span style=&quot;font-family: sans-serif; font-size: 16px;&quot;&gt;&amp;nbsp; &amp;nbsp; 现在PHP加密的方法有很多总，一般简单的加密方法都是直接MD5的，但是MD5一些简单的密码也可以通过反编译出来，所以我们现在通过自定义的方法来加密更为安全一些。&lt;/span&gt;&lt;br/&gt;&lt;/h3&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;现在是本人收集和汇总一些PHP加密方法的方法，分享给大家，供大家参考。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color: rgb(192, 0, 0);&quot;&gt;方法一：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;/**
*&amp;nbsp;对用户的密码进行加密
*&amp;nbsp;@param&amp;nbsp;$password
*&amp;nbsp;@param&amp;nbsp;$encrypt&amp;nbsp;//传入加密串，在修改密码时做认证
*&amp;nbsp;@return&amp;nbsp;array/password
*/
function&amp;nbsp;password($password,&amp;nbsp;$encrypt=&amp;#39;&amp;#39;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pwd&amp;nbsp;=&amp;nbsp;array();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pwd[&amp;#39;encrypt&amp;#39;]&amp;nbsp;=&amp;nbsp;$encrypt&amp;nbsp;?&amp;nbsp;$encrypt&amp;nbsp;:&amp;nbsp;rand(1000,9999);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pwd[&amp;#39;password&amp;#39;]&amp;nbsp;=&amp;nbsp;md5(md5(trim($password)).$pwd[&amp;#39;encrypt&amp;#39;]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$encrypt&amp;nbsp;?&amp;nbsp;$pwd[&amp;#39;password&amp;#39;]&amp;nbsp;:&amp;nbsp;$pwd;
}&lt;/pre&gt;&lt;p&gt;方法说明：当我们只传入密码是，方法会返回一个加密因子和一个新生成的加密密码；而这个加密因子和密码都是随机生成的，每次一次都不一样。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;white-space: normal;&quot;&gt;&lt;span style=&quot;color: rgb(192, 0, 0);&quot;&gt;方法二：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/*&amp;nbsp;
*&amp;nbsp;rc4加密算法&amp;nbsp;
*&amp;nbsp;$pwd&amp;nbsp;密钥&amp;nbsp;
*&amp;nbsp;$data&amp;nbsp;要加密的数据&amp;nbsp;
*/&amp;nbsp;

//$pwd密钥　$data需加密字符串&amp;nbsp;
function&amp;nbsp;rc4&amp;nbsp;($pwd,&amp;nbsp;$data){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$key[]&amp;nbsp;=&amp;quot;&amp;quot;;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$box[]&amp;nbsp;=&amp;quot;&amp;quot;;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pwd_length&amp;nbsp;=&amp;nbsp;strlen($pwd);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data_length&amp;nbsp;=&amp;nbsp;strlen($data);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;256;&amp;nbsp;$i++){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$key[$i]&amp;nbsp;=&amp;nbsp;ord($pwd[$i&amp;nbsp;%&amp;nbsp;$pwd_length]);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$box[$i]&amp;nbsp;=&amp;nbsp;$i;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($j&amp;nbsp;=&amp;nbsp;$i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;256;&amp;nbsp;$i++)&amp;nbsp;{&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$j&amp;nbsp;=&amp;nbsp;($j&amp;nbsp;+&amp;nbsp;$box[$i]&amp;nbsp;+&amp;nbsp;$key[$i])&amp;nbsp;%&amp;nbsp;256;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tmp&amp;nbsp;=&amp;nbsp;$box[$i];&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$box[$i]&amp;nbsp;=&amp;nbsp;$box[$j];&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$box[$j]&amp;nbsp;=&amp;nbsp;$tmp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($a&amp;nbsp;=&amp;nbsp;$j&amp;nbsp;=&amp;nbsp;$i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$data_length;&amp;nbsp;$i++)&amp;nbsp;{&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$a&amp;nbsp;=&amp;nbsp;($a&amp;nbsp;+&amp;nbsp;1)&amp;nbsp;%&amp;nbsp;256;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$j&amp;nbsp;=&amp;nbsp;($j&amp;nbsp;+&amp;nbsp;$box[$a])&amp;nbsp;%&amp;nbsp;256;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tmp&amp;nbsp;=&amp;nbsp;$box[$a];&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$box[$a]&amp;nbsp;=&amp;nbsp;$box[$j];&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$box[$j]&amp;nbsp;=&amp;nbsp;$tmp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$k&amp;nbsp;=&amp;nbsp;$box[(($box[$a]&amp;nbsp;+&amp;nbsp;$box[$j])&amp;nbsp;%&amp;nbsp;256)];&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$cipher&amp;nbsp;.=&amp;nbsp;chr(ord($data[$i])&amp;nbsp;^&amp;nbsp;$k);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$cipher;&amp;nbsp;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;white-space: normal;&quot;&gt;&lt;span style=&quot;color: rgb(192, 0, 0);&quot;&gt;方法三：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**&amp;nbsp;
+-----------------------------------------------------&amp;nbsp;
*&amp;nbsp;Mcrypt&amp;nbsp;加密/解密&amp;nbsp;
*&amp;nbsp;@param&amp;nbsp;String&amp;nbsp;$date&amp;nbsp;要加密和解密的数据&amp;nbsp;
*&amp;nbsp;@param&amp;nbsp;String&amp;nbsp;$mode&amp;nbsp;encode&amp;nbsp;默认为加密/decode&amp;nbsp;为解密&amp;nbsp;
*&amp;nbsp;@return&amp;nbsp;String&amp;nbsp;
*&amp;nbsp;@author&amp;nbsp;zxing@97md.net&amp;nbsp;Mon&amp;nbsp;Sep&amp;nbsp;14&amp;nbsp;22:59:28&amp;nbsp;CST&amp;nbsp;2009&amp;nbsp;
+-----------------------------------------------------&amp;nbsp;
*&amp;nbsp;@example&amp;nbsp;
*/&amp;nbsp;
function&amp;nbsp;ZxingCrypt($date,$mode&amp;nbsp;=&amp;nbsp;&amp;#39;encode&amp;#39;){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$key&amp;nbsp;=&amp;nbsp;md5(&amp;#39;zxing&amp;#39;);//用MD5哈希生成一个密钥，注意加密和解密的密钥必须统一&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($mode&amp;nbsp;==&amp;nbsp;&amp;#39;decode&amp;#39;){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$date&amp;nbsp;=&amp;nbsp;base64_decode($date);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(function_exists(&amp;#39;mcrypt_create_iv&amp;#39;)){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$iv_size&amp;nbsp;=&amp;nbsp;mcrypt_get_iv_size(MCRYPT_RIJNDAEL_256,&amp;nbsp;MCRYPT_MODE_ECB);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$iv&amp;nbsp;=&amp;nbsp;mcrypt_create_iv($iv_size,&amp;nbsp;MCRYPT_RAND);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(isset($iv)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$mode&amp;nbsp;==&amp;nbsp;&amp;#39;encode&amp;#39;){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$passcrypt&amp;nbsp;=&amp;nbsp;mcrypt_encrypt(MCRYPT_RIJNDAEL_256,&amp;nbsp;$key,&amp;nbsp;$date,&amp;nbsp;MCRYPT_MODE_ECB,&amp;nbsp;$iv);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(isset($iv)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$mode&amp;nbsp;==&amp;nbsp;&amp;#39;decode&amp;#39;){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$passcrypt&amp;nbsp;=&amp;nbsp;mcrypt_decrypt(MCRYPT_RIJNDAEL_256,&amp;nbsp;$key,&amp;nbsp;$date,&amp;nbsp;MCRYPT_MODE_ECB,&amp;nbsp;$iv);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($mode&amp;nbsp;==&amp;nbsp;&amp;#39;encode&amp;#39;){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$passcrypt&amp;nbsp;=&amp;nbsp;base64_encode($passcrypt);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$passcrypt;&amp;nbsp;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:48','0','0','15','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('329','php计算年龄精准到年月日','这篇文章主要介绍了php计算年龄精准到年月日的方法，涉及php操作日期与字符串的相关技巧，非常简单实用，需要的朋友可以参考下','119','&lt;p&gt;　　这篇文章主要介绍了php计算年龄精准到年月日的方法，涉及php操作日期与字符串的相关技巧，非常简单实用，需要的朋友可以参考下&lt;/p&gt;&lt;p&gt;　　本文实例讲述了php计算年龄精准到年月日的方法。分享给大家供大家参考。具体如下：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php
&amp;nbsp;&amp;nbsp;
/*
&amp;nbsp;*&amp;nbsp;To&amp;nbsp;change&amp;nbsp;this&amp;nbsp;license&amp;nbsp;header,&amp;nbsp;choose&amp;nbsp;License&amp;nbsp;Headers&amp;nbsp;in&amp;nbsp;Project&amp;nbsp;Properties.
&amp;nbsp;*&amp;nbsp;To&amp;nbsp;change&amp;nbsp;this&amp;nbsp;template&amp;nbsp;file,&amp;nbsp;choose&amp;nbsp;Tools&amp;nbsp;|&amp;nbsp;Templates
&amp;nbsp;*&amp;nbsp;and&amp;nbsp;open&amp;nbsp;the&amp;nbsp;template&amp;nbsp;in&amp;nbsp;the&amp;nbsp;editor.
&amp;nbsp;*/
&amp;nbsp;&amp;nbsp;
class&amp;nbsp;Age&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;/**
&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;计算年龄精准到年月日
&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;type&amp;nbsp;$birthday
&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array
&amp;nbsp;&amp;nbsp;&amp;nbsp;*/
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;calAge($birthday)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($byear,&amp;nbsp;$bmonth,&amp;nbsp;$bday)&amp;nbsp;=&amp;nbsp;explode(&amp;#39;-&amp;#39;,&amp;nbsp;$birthday);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($year,&amp;nbsp;$month,&amp;nbsp;$day)&amp;nbsp;=&amp;nbsp;explode(&amp;#39;-&amp;#39;,&amp;nbsp;date(&amp;#39;Y-m-d&amp;#39;));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$bmonth&amp;nbsp;=&amp;nbsp;intval($bmonth);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$bday&amp;nbsp;=&amp;nbsp;intval($bday);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($bmonth&amp;nbsp;&amp;lt;&amp;nbsp;10)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$bmonth&amp;nbsp;=&amp;nbsp;&amp;#39;0&amp;#39;&amp;nbsp;.&amp;nbsp;$bmonth;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($bday&amp;nbsp;&amp;lt;&amp;nbsp;10)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$bday&amp;nbsp;=&amp;nbsp;&amp;#39;0&amp;#39;&amp;nbsp;.&amp;nbsp;$bday;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$bi&amp;nbsp;=&amp;nbsp;intval($byear&amp;nbsp;.&amp;nbsp;$bmonth&amp;nbsp;.&amp;nbsp;$bday);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ni&amp;nbsp;=&amp;nbsp;intval($year&amp;nbsp;.&amp;nbsp;$month&amp;nbsp;.&amp;nbsp;$day);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$not_birth&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($bi&amp;nbsp;&amp;gt;&amp;nbsp;$ni)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$not_birth&amp;nbsp;=&amp;nbsp;1;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tmp&amp;nbsp;=&amp;nbsp;array($byear,&amp;nbsp;$bmonth,&amp;nbsp;$bday);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($byear,&amp;nbsp;$bmonth,&amp;nbsp;$bday)&amp;nbsp;=&amp;nbsp;array($year,&amp;nbsp;$month,&amp;nbsp;$day);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($year,&amp;nbsp;$month,&amp;nbsp;$day)&amp;nbsp;=&amp;nbsp;$tmp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($bi,&amp;nbsp;$ni)&amp;nbsp;=&amp;nbsp;array($ni,&amp;nbsp;$bi);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$years&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;(($bi&amp;nbsp;+&amp;nbsp;10000)&amp;nbsp;&amp;lt;=&amp;nbsp;bi=&amp;quot;&amp;quot;&amp;nbsp;this-=&amp;quot;&amp;quot;&amp;gt;getMD(array($year,&amp;nbsp;$month,&amp;nbsp;$day),&amp;nbsp;array($byear,&amp;nbsp;$bmonth,&amp;nbsp;$bday));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;array(&amp;#39;year&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$years,&amp;nbsp;&amp;#39;month&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$m,&amp;nbsp;&amp;#39;day&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$d,&amp;nbsp;&amp;#39;not_birth&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$not_birth);
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;/**
&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;只能用于一年内计算
&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;type&amp;nbsp;$ymd
&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;type&amp;nbsp;$bymd
&amp;nbsp;&amp;nbsp;&amp;nbsp;*/
&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;getMD($ymd,&amp;nbsp;$bymd)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($y,&amp;nbsp;$m,&amp;nbsp;$d)&amp;nbsp;=&amp;nbsp;$ymd;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list($by,&amp;nbsp;$bm,&amp;nbsp;$bd)&amp;nbsp;=&amp;nbsp;$bymd;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(($m&amp;nbsp;.&amp;nbsp;$d)&amp;nbsp;&amp;lt;&amp;nbsp;($bm&amp;nbsp;.&amp;nbsp;$bd))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$m&amp;nbsp;+=12;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$month&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;((($bm&amp;nbsp;.&amp;nbsp;$bd)&amp;nbsp;+&amp;nbsp;100)&amp;nbsp;&amp;lt;=&amp;nbsp;($m&amp;nbsp;.&amp;nbsp;$d))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$bm++;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$month++;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($bd&amp;nbsp;&amp;lt;=&amp;nbsp;day=&amp;quot;$d&amp;quot;&amp;nbsp;-=&amp;quot;&amp;quot;&amp;nbsp;else=&amp;quot;&amp;quot;&amp;nbsp;mdays=&amp;quot;$bm&amp;quot;&amp;gt;&amp;nbsp;12&amp;nbsp;?&amp;nbsp;$this-&amp;gt;_getMothDay(&amp;nbsp;++$by,&amp;nbsp;$bm&amp;nbsp;-&amp;nbsp;12)&amp;nbsp;:&amp;nbsp;$this-&amp;gt;_getMothDay($by,&amp;nbsp;$bm);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$day&amp;nbsp;=&amp;nbsp;$mdays&amp;nbsp;-&amp;nbsp;$bd&amp;nbsp;+&amp;nbsp;$d;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;array($month,&amp;nbsp;$day);
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;private&amp;nbsp;function&amp;nbsp;_getMothDay($year,&amp;nbsp;$month)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;switch&amp;nbsp;($month)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;1:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;3:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;5:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;7:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;8:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;10:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;12:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$day&amp;nbsp;=&amp;nbsp;31;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;2:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$day&amp;nbsp;=&amp;nbsp;(intval($year&amp;nbsp;%&amp;nbsp;4)&amp;nbsp;?&amp;nbsp;28&amp;nbsp;:&amp;nbsp;29);&amp;nbsp;//能被4除尽的为29天其他28天
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;default:
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$day&amp;nbsp;=&amp;nbsp;30;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$day;
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
}
&amp;nbsp;&amp;nbsp;
$cage&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Age();
$test&amp;nbsp;=&amp;nbsp;array(
&amp;nbsp;&amp;nbsp;&amp;#39;1990-06-12&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-07-13&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-08-16&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-10-10&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-10-13&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-10-15&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-11-9&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;1990-11-22&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;2016-11-22&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;2016-8-22&amp;#39;,
&amp;nbsp;&amp;nbsp;&amp;#39;2016-10-13&amp;#39;,
);
echo&amp;nbsp;date(&amp;#39;Y-m-d&amp;#39;);
echo&amp;nbsp;&amp;#39;&amp;#39;&lt;/pre&gt;&lt;p&gt;输出：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/*
&amp;nbsp;&amp;nbsp;运行结果：
&amp;nbsp;&amp;nbsp;2015-10-13
&amp;nbsp;&amp;nbsp;1990-06-12:25年4月1天;0
&amp;nbsp;&amp;nbsp;1990-07-13:25年3月0天;0
&amp;nbsp;&amp;nbsp;1990-08-16:25年1月27天;0
&amp;nbsp;&amp;nbsp;1990-10-10:25年0月3天;0
&amp;nbsp;&amp;nbsp;1990-10-13:25年0月0天;0
&amp;nbsp;&amp;nbsp;1990-10-15:24年11月28天;0
&amp;nbsp;&amp;nbsp;1990-11-9:24年11月4天;0
&amp;nbsp;&amp;nbsp;1990-11-22:24年10月21天;0
&amp;nbsp;&amp;nbsp;2016-11-22:1年1月9天;1
&amp;nbsp;&amp;nbsp;2016-8-22:0年10月9天;1
&amp;nbsp;&amp;nbsp;2016-10-13:1年0月0天;1
&amp;nbsp;&amp;nbsp;
&amp;nbsp;*&amp;nbsp;
&amp;nbsp;*/&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:47','0','0','19','0');/* MySQLReback Separation */
