 INSERT INTO `bk_article` VALUES ('326','PHP简单生成随机字符串','PHP简单生成随机字符串自定义方法','119','&lt;h3 style=&quot; font-weight:bolder&quot;&gt;PHP简单生成随机字符串&lt;/h3&gt;&lt;p&gt;所属分类：PHP函数 编辑： 浏览：&lt;span id=&quot;clicks&quot;&gt;339&lt;/span&gt; 发布时间：2015-12-09 14:12&lt;/p&gt;&lt;!--/ .post-date--&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP简单生成随机字符串自定义方法&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;生成随机字符串
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;$lenth&amp;nbsp;长度
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string&amp;nbsp;字符串
&amp;nbsp;*/
function&amp;nbsp;get_randomstr($lenth&amp;nbsp;=&amp;nbsp;6)&amp;nbsp;{
	return&amp;nbsp;get_random($lenth,&amp;nbsp;&amp;#39;123456789abcdefghijklmnpqrstuvwxyzABCDEFGHIJKLMNPQRSTUVWXYZ&amp;#39;);
}

/**
&amp;nbsp;*&amp;nbsp;产生随机字符串
&amp;nbsp;*
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;int&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;&amp;nbsp;输出长度
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars&amp;nbsp;&amp;nbsp;&amp;nbsp;可选的&amp;nbsp;，默认为&amp;nbsp;0123456789
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;&amp;nbsp;&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;字符串
&amp;nbsp;*/
function&amp;nbsp;get_random($length,&amp;nbsp;$chars&amp;nbsp;=&amp;nbsp;&amp;#39;0123456789&amp;#39;)&amp;nbsp;{
	$hash&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;
	$max&amp;nbsp;=&amp;nbsp;strlen($chars)&amp;nbsp;-&amp;nbsp;1;
	for($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$length;&amp;nbsp;$i++)&amp;nbsp;{
		$hash&amp;nbsp;.=&amp;nbsp;$chars[mt_rand(0,&amp;nbsp;$max)];
	}
	return&amp;nbsp;$hash;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;使用方法：&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;echo&amp;nbsp;get_randomstr(6);
echo&amp;nbsp;get_randomstr(7);&lt;/pre&gt;&lt;p&gt;输出：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;vS8wZK
hQ17fEI&lt;/pre&gt;&lt;p&gt;以上的这两个结果都会随机生成，每次运行的结构都会不一样。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;解析：&lt;/p&gt;&lt;p&gt;通过参数传入生成字符串的个数给方法get_randomstr();get_randomstr()方法再通过get_random方法生成随机数返回给get_randomstr()，其实这一过程是通过两个自定义方法来完成的，我个人也觉得比较繁琐，虽然思路是清晰的。其实这种方法的好处就是当我们在公共方法把get_random方法写死的时候，如果后面发现有些功能是get_random不能满足的事情下，我们可以通过get_randomstr方法来扩展。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;所以在一般情况下，我更建议大家使用下面的方法去生成随机字符串：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','2016-11-15 15:09:47','0','0','145','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('343','代码高亮插件Syntaxhighlighter3.0.83详细使用方法','一.Syntaxhighlighter下载地址:http://alexgorbatchev.com/SyntaxHighlighter/download/二.现在的版本是3.0.83,本文也是介绍此版本的使用方法,历史版本也可以在上面的链接里下载.三.下载下来后的目录结构介绍','122','&lt;p&gt;一.Syntaxhighlighter下载地址:&lt;a href=&quot;http://alexgorbatchev.com/SyntaxHighlighter/download/&quot; _src=&quot;http://alexgorbatchev.com/SyntaxHighlighter/download/&quot;&gt;http://alexgorbatchev.com/SyntaxHighlighter/download/&lt;/a&gt;&lt;/p&gt;&lt;p&gt;二.现在的版本是3.0.83,本文也是介绍此版本的使用方法,历史版本也可以在上面的链接里下载.&lt;/p&gt;&lt;p&gt;三.下载下来后的目录结构介绍&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Uploads/assets/2016-11-17/1479376822944617.png&quot; title=&quot;1479376822944617.png&quot; alt=&quot;blob.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;我们看一个php的例子&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;生成随机字符串
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;int&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;&amp;nbsp;要生成的随机字符串长度
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;随机码类型：0，数字+大写字母；1，数字；2，小写字母；3，大写字母；4，特殊字符；-1，数字+大小写字母+特殊字符
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string
&amp;nbsp;*/
function&amp;nbsp;randCode($length&amp;nbsp;=&amp;nbsp;5,&amp;nbsp;$type&amp;nbsp;=&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;array(1&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;0123456789&amp;quot;,&amp;nbsp;2&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;abcdefghijklmnopqrstuvwxyz&amp;quot;,&amp;nbsp;3&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;ABCDEFGHIJKLMNOPQRSTUVWXYZ&amp;quot;,&amp;nbsp;4&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;quot;~@#$%^&amp;amp;*(){}[]|&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($type&amp;nbsp;==&amp;nbsp;0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array_pop($arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;implode(&amp;quot;&amp;quot;,&amp;nbsp;$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($type&amp;nbsp;==&amp;nbsp;&amp;quot;-1&amp;quot;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;implode(&amp;quot;&amp;quot;,&amp;nbsp;$arr);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;$arr[$type];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;strlen($string)&amp;nbsp;-&amp;nbsp;1;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$length;&amp;nbsp;$i++)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str[$i]&amp;nbsp;=&amp;nbsp;$string[rand(0,&amp;nbsp;$count)];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$code&amp;nbsp;.=&amp;nbsp;$str[$i];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$code;
}&lt;/pre&gt;&lt;p&gt;说下基本的配置&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;link&amp;nbsp;type=&amp;quot;text/css&amp;quot;&amp;nbsp;rel=&amp;quot;stylesheet&amp;quot;&amp;nbsp;href=&amp;quot;{$Think.const.BOKE_STATIC_PATH}/home/default/js/styles/shCore.css&amp;quot;/&amp;gt;&amp;nbsp;&amp;lt;!--核心样式表(必选)--&amp;gt;
&amp;lt;link&amp;nbsp;type=&amp;quot;text/css&amp;quot;&amp;nbsp;rel=&amp;quot;stylesheet&amp;quot;&amp;nbsp;href=&amp;quot;{$Think.const.BOKE_STATIC_PATH}/home/default/js/styles/shThemeEmacs.css&amp;quot;/&amp;gt;&amp;nbsp;&amp;lt;!--主题样式表(多重样式选一)--&amp;gt;
&amp;lt;script&amp;nbsp;type=&amp;quot;text/javascript&amp;quot;&amp;nbsp;src=&amp;quot;{$Think.const.BOKE_STATIC_PATH}/home/default/js/scripts/shCore.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&amp;nbsp;&amp;lt;!--核心js脚本(必选)--&amp;gt;
&amp;lt;script&amp;nbsp;type=&amp;quot;text/javascript&amp;quot;&amp;nbsp;src=&amp;quot;{$Think.const.BOKE_STATIC_PATH}/home/default/js/scripts/shBrushBash.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&amp;nbsp;&amp;lt;!--&amp;quot;Shell&amp;quot;脚本高亮规则js&amp;nbsp;(必选,你的代码是什么语言的,就选什么js脚本--&amp;gt;
&amp;lt;script&amp;nbsp;type=&amp;quot;text/javascript&amp;quot;&amp;nbsp;src=&amp;quot;{$Think.const.BOKE_STATIC_PATH}/home/default/js/scripts/shBrushPhp.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;

&amp;lt;script&amp;nbsp;type=&amp;quot;text/javascript&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;.all();&amp;nbsp;//通过该js脚本来进行高亮处理;

&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;p&gt;这里放你的程序代码class里面的 &amp;nbsp;brush：php 代表你要高亮的代码类型&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;lt;div&amp;nbsp;class=&amp;quot;brush:php;&amp;quot;&amp;gt;&amp;lt;php&amp;gt;echo&amp;nbsp;htmlspecialchars_decode($data[0][&amp;#39;content&amp;#39;]);&amp;nbsp;&amp;lt;/php&amp;gt;&amp;lt;/div&amp;gt;&lt;/pre&gt;&lt;p&gt;本博客就是采用此方法改造后显示的,有兴趣的朋友可以试下.&lt;/p&gt;','6','2016-11-17 18:05:42','0','0','72','0');/* MySQLReback Separation */
