 INSERT INTO `bk_article` VALUES ('346','Lnmp下 linux下添加二级域名','复制一份相同配置的配置文件2修改yk配置文件vim yk.weixinlu.net.conf，从新启动 apache service httpd restart在跟www.weixinlu.net  平级目录建立   mkdir yk.weixinlu.net 目录，测试运行是不是正常


','125','&lt;p&gt;找到你的配置文件&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;[root@iZm5e2o440hd7o4obazr0dZ&amp;nbsp;vhost]#&amp;nbsp;cat&amp;nbsp;www.weixinlu.net.conf&amp;nbsp;
&amp;lt;VirtualHost&amp;nbsp;*:80&amp;gt;
ServerAdmin&amp;nbsp;webmaster@example.com
php_admin_value&amp;nbsp;open_basedir&amp;nbsp;&amp;quot;/home/wwwroot/www.weixinlu.net:/tmp/:/var/tmp/:/proc/&amp;quot;
DocumentRoot&amp;nbsp;&amp;quot;/home/wwwroot/www.weixinlu.net&amp;quot;
ServerName&amp;nbsp;www.weixinlu.net
ServerAlias&amp;nbsp;&amp;nbsp;weixinlu.net
ErrorLog&amp;nbsp;&amp;quot;/home/wwwlogs/www.weixinlu.net-error_log&amp;quot;
CustomLog&amp;nbsp;&amp;quot;/home/wwwlogs/www.weixinlu.net-access_log&amp;quot;&amp;nbsp;combined
&amp;lt;Directory&amp;nbsp;&amp;quot;/home/wwwroot/www.weixinlu.net&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;SetOutputFilter&amp;nbsp;DEFLATE
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Options&amp;nbsp;FollowSymLinks
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;AllowOverride&amp;nbsp;All
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Order&amp;nbsp;allow,deny
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Allow&amp;nbsp;from&amp;nbsp;all
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;DirectoryIndex&amp;nbsp;index.html&amp;nbsp;index.php
&amp;lt;/Directory&amp;gt;
&amp;lt;/VirtualHost&amp;gt;&lt;/pre&gt;&lt;p&gt;复制一份相同配置的配置文件&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;-rw-r--r--&amp;nbsp;1&amp;nbsp;root&amp;nbsp;root&amp;nbsp;586&amp;nbsp;11月&amp;nbsp;23&amp;nbsp;16:23&amp;nbsp;www.weixinlu.net.conf
[root@iZm5e2o440hd7o4obazr0dZ&amp;nbsp;vhost]#&amp;nbsp;
[root@iZm5e2o440hd7o4obazr0dZ&amp;nbsp;vhost]#&amp;nbsp;cp&amp;nbsp;www.weixinlu.net.conf&amp;nbsp;yk.weixinlu.net.conf&lt;/pre&gt;&lt;p&gt;修改yk配置文件vim yk.weixinlu.net.conf&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;VirtualHost&amp;nbsp;*:80&amp;gt;
ServerAdmin&amp;nbsp;webmaster@example.com
php_admin_value&amp;nbsp;open_basedir&amp;nbsp;&amp;quot;/home/wwwroot/yk.weixinlu.net:/tmp/:/var/tmp/:/proc/&amp;quot;
DocumentRoot&amp;nbsp;&amp;quot;/home/wwwroot/yk.weixinlu.net&amp;quot;
ServerName&amp;nbsp;yk.weixinlu.net
ServerAlias&amp;nbsp;&amp;nbsp;weixinlu.net
ErrorLog&amp;nbsp;&amp;quot;/home/wwwlogs/yk.weixinlu.net-error_log&amp;quot;
CustomLog&amp;nbsp;&amp;quot;/home/wwwlogs/yk.weixinlu.net-access_log&amp;quot;&amp;nbsp;combined
&amp;lt;Directory&amp;nbsp;&amp;quot;/home/wwwroot/yk.weixinlu.net&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;SetOutputFilter&amp;nbsp;DEFLATE
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Options&amp;nbsp;FollowSymLinks
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;AllowOverride&amp;nbsp;All
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Order&amp;nbsp;allow,deny
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Allow&amp;nbsp;from&amp;nbsp;all
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;DirectoryIndex&amp;nbsp;index.html&amp;nbsp;index.php
&amp;lt;/Directory&amp;gt;
&amp;lt;/VirtualHost&amp;gt;&lt;/pre&gt;&lt;p&gt;从新启动 apache service httpd restart&lt;/p&gt;&lt;p&gt;在跟&lt;a href=&quot;http://www.weixinlu.net&quot; _src=&quot;http://www.weixinlu.net&quot;&gt;www.weixinlu.net&lt;/a&gt;&amp;nbsp; 平级目录建立 &amp;nbsp; mkdir yk.weixinlu.net 目录&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;mkdir&amp;nbsp;yk.weixinlu.net&lt;/pre&gt;&lt;p&gt;&amp;nbsp;最后添加解析域名&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Uploads/assets/2016-11-23/1479896972944527.png&quot; title=&quot;1479896972944527.png&quot; alt=&quot;blob.png&quot; width=&quot;612&quot; height=&quot;276&quot; style=&quot;width: 612px; height: 276px;&quot;/&gt;&lt;/p&gt;&lt;p&gt;测试运行是不是正常&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Uploads/assets/2016-11-23/1479898915997787.png&quot; title=&quot;1479898915997787.png&quot; alt=&quot;blob.png&quot; width=&quot;618&quot; height=&quot;252&quot; style=&quot;width: 618px; height: 252px;&quot;/&gt;&lt;/p&gt;&lt;p&gt;配置成功！李宗洋博客！&lt;/p&gt;','3','2016-11-23 18:32:29','0','0','117','0');/* MySQLReback Separation */
