 INSERT INTO `bk_article` VALUES ('349','php简单实现访客记录统计','通过php简单的实现一个访客记录统计，希望可以帮助到小伙伴！','128','&lt;p&gt;获取各个属性的方法&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;///获得访客浏览器类型
&amp;nbsp;&amp;nbsp;function&amp;nbsp;GetBrowser(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;])){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(preg_match(&amp;#39;/MSIE/i&amp;#39;,$br))&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;MSIE&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Firefox/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Firefox&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Chrome/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Chrome&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Safari/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Safari&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/Opera/i&amp;#39;,$br))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Opera&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$br&amp;nbsp;=&amp;nbsp;&amp;#39;Other&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$br;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;None&amp;quot;;}&amp;nbsp;
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;////获得访客浏览器语言
&amp;nbsp;&amp;nbsp;function&amp;nbsp;GetLang(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_ACCEPT_LANGUAGE&amp;#39;])){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;HTTP_ACCEPT_LANGUAGE&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;substr($lang,0,5);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(preg_match(&amp;quot;/zh-cn/i&amp;quot;,$lang)){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;&amp;quot;简体中文&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif(preg_match(&amp;quot;/zh/i&amp;quot;,$lang)){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;&amp;quot;繁体中文&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang&amp;nbsp;=&amp;nbsp;&amp;quot;English&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$lang;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;None&amp;quot;;}
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;////获取访客操作系统
&amp;nbsp;&amp;nbsp;function&amp;nbsp;GetOs(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!empty($_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;])){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;HTTP_USER_AGENT&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(preg_match(&amp;#39;/win/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Windows&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/mac/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;MAC&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/linux/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Linux&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/unix/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Unix&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif&amp;nbsp;(preg_match(&amp;#39;/bsd/i&amp;#39;,$OS))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;BSD&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$OS&amp;nbsp;=&amp;nbsp;&amp;#39;Other&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$OS;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;}else{return&amp;nbsp;&amp;quot;None&amp;quot;;}&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;}
//&amp;nbsp;获取ip
function&amp;nbsp;ip(){&amp;nbsp;
if(getenv(&amp;#39;HTTP_CLIENT_IP&amp;#39;)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;strcasecmp(getenv(&amp;#39;HTTP_CLIENT_IP&amp;#39;),&amp;nbsp;&amp;#39;unknown&amp;#39;))&amp;nbsp;{&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;getenv(&amp;#39;HTTP_CLIENT_IP&amp;#39;);&amp;nbsp;
}elseif(getenv(&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;strcasecmp(getenv(&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;),&amp;nbsp;&amp;#39;unknown&amp;#39;)){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;getenv(&amp;#39;HTTP_X_FORWARDED_FOR&amp;#39;);&amp;nbsp;
}elseif(getenv(&amp;#39;REMOTE_ADDR&amp;#39;)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;strcasecmp(getenv(&amp;#39;REMOTE_ADDR&amp;#39;),&amp;nbsp;&amp;#39;unknown&amp;#39;)){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;getenv(&amp;#39;REMOTE_ADDR&amp;#39;);&amp;nbsp;
}elseif(isset($_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;])&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;]&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;strcasecmp($_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;],&amp;nbsp;&amp;#39;unknown&amp;#39;)){&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;];&amp;nbsp;
}&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;preg_match(&amp;quot;/[d.]{7,15}/&amp;quot;,&amp;nbsp;$ip,&amp;nbsp;$matches)&amp;nbsp;?&amp;nbsp;$matches[0]&amp;nbsp;:&amp;nbsp;&amp;#39;unknown&amp;#39;;&amp;nbsp;
}&amp;nbsp;


&amp;nbsp;////根据ip获得访客所在地地名
&amp;nbsp;function&amp;nbsp;Getaddress($ip=&amp;#39;&amp;#39;){
&amp;nbsp;&amp;nbsp;if(empty($ip)){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ip&amp;nbsp;=&amp;nbsp;ip();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;$ipadd&amp;nbsp;=&amp;nbsp;file_get_contents(&amp;quot;http://int.dpool.sina.com.cn/iplookup/iplookup.php?ip=&amp;quot;.$ip);//根据新浪api接口获取
&amp;nbsp;&amp;nbsp;if($ipadd){
&amp;nbsp;&amp;nbsp;&amp;nbsp;$charset&amp;nbsp;=&amp;nbsp;iconv(&amp;quot;gbk&amp;quot;,&amp;quot;utf-8&amp;quot;,$ipadd);&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;preg_match_all(&amp;quot;/[x{4e00}-x{9fa5}]+/u&amp;quot;,$charset,$ipadds);
&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$ipadds;&amp;nbsp;&amp;nbsp;&amp;nbsp;//返回一个二维数组
&amp;nbsp;&amp;nbsp;}else{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;&amp;quot;addree&amp;nbsp;is&amp;nbsp;none&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;
&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;然后调用这些方法 获取参数 组装数组，插入数据库&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;M(&amp;quot;visit&amp;quot;)-&amp;gt;add(array(
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;ip&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$userip,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;createtime&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$time,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;browser&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;GetBrowser(),
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;ilanguagep&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;GetBrowser(),
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;language&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;GetLang(),
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;os_name&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;GetOs(),
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;city&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;$cityname,
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;));&lt;/pre&gt;&lt;p&gt;&lt;img src=&quot;/Uploads/assets/2016-12-02/1480667175432930.png&quot; title=&quot;1480667175432930.png&quot; alt=&quot;blob.png&quot; width=&quot;646&quot; height=&quot;227&quot; style=&quot;width: 646px; height: 227px;&quot;/&gt;&lt;/p&gt;','1','2016-12-02 16:27:18','0','0','77','0');/* MySQLReback Separation */
 /* 插入数据 `bk_article` */
 INSERT INTO `bk_article` VALUES ('350','thinkphp3.2.3数据库还原备份分享','thinkphp3.2.3数据库还原备份分享，非常的简单实用，希望能帮到小伙伴，1 数据库备份类，2 php端 加载 引入 数据库类 3，html页面详情，4测试是否正常！完成！','129','&lt;p&gt;1 数据库备份类 &amp;nbsp;详情如下：下载类&amp;nbsp;&lt;img src=&quot;http://www.lizongyang.cn/static/manager/ueditor/dialogs/attachment/fileTypeImages/icon_rar.gif&quot; style=&quot;vertical-align: middle; margin-right: 2px;&quot;/&gt;&lt;a href=&quot;http://www.lizongyang.cn/Uploads/assets/2016-12-03/1480738469735959.zip&quot; title=&quot;MySQLReback.class.php.zip&quot; style=&quot;font-size: 12px; color: rgb(0, 102, 204);&quot;&gt;MySQLReback.class.php.zip&lt;/a&gt;&amp;nbsp;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php
namespace&amp;nbsp;OrgNet;
class&amp;nbsp;MySQLReback&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$config;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$content;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$dbName&amp;nbsp;=&amp;nbsp;array();

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;const&amp;nbsp;DIR_SEP&amp;nbsp;=&amp;nbsp;DIRECTORY_SEPARATOR;

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;__construct($config)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;config&amp;nbsp;=&amp;nbsp;$config;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;quot;Content-type:&amp;nbsp;text/html;charset=utf-8&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;connect();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;function&amp;nbsp;connect()&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(mysql_connect($this-&amp;gt;config[&amp;#39;host&amp;#39;]&amp;nbsp;.&amp;nbsp;&amp;#39;:&amp;#39;&amp;nbsp;.&amp;nbsp;$this-&amp;gt;config[&amp;#39;port&amp;#39;],&amp;nbsp;$this-&amp;gt;config[&amp;#39;userName&amp;#39;],&amp;nbsp;$this-&amp;gt;config[&amp;#39;userPassword&amp;#39;]))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;mysql_query(&amp;quot;SET&amp;nbsp;NAMES&amp;nbsp;&amp;#39;{$this-&amp;gt;config[&amp;#39;charset&amp;#39;]}&amp;#39;&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;mysql_query(&amp;quot;set&amp;nbsp;interactive_timeout=24*3600&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;throwException(&amp;#39;无法连接到数据库!&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;setDBName($dbName&amp;nbsp;=&amp;nbsp;&amp;#39;*&amp;#39;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($dbName&amp;nbsp;==&amp;nbsp;&amp;#39;*&amp;#39;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$rs&amp;nbsp;=&amp;nbsp;mysql_list_dbs();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$rows&amp;nbsp;=&amp;nbsp;mysql_num_rows($rs);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($rows)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$rows;&amp;nbsp;$i++)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$dbName&amp;nbsp;=&amp;nbsp;mysql_tablename($rs,&amp;nbsp;$i);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$block&amp;nbsp;=&amp;nbsp;array(&amp;#39;information_schema&amp;#39;,&amp;nbsp;&amp;#39;mysql&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!in_array($dbName,&amp;nbsp;$block))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;dbName[]&amp;nbsp;=&amp;nbsp;$dbName;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;throwException(&amp;#39;没有任何数据库!&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;dbName&amp;nbsp;=&amp;nbsp;func_get_args();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;function&amp;nbsp;getFile($fileName)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;content&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$fileName&amp;nbsp;=&amp;nbsp;$this-&amp;gt;trimPath($this-&amp;gt;config[&amp;#39;path&amp;#39;]&amp;nbsp;.&amp;nbsp;self::DIR_SEP&amp;nbsp;.&amp;nbsp;$fileName);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(is_file($fileName))&amp;nbsp;{
