&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;加载Mysql类
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mr&amp;nbsp;=&amp;nbsp;new&amp;nbsp;OrgNetMySQLReback($config);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mr-&amp;gt;setDBName(C(&amp;#39;DB_NAME&amp;#39;));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($_GET[&amp;#39;Action&amp;#39;]&amp;nbsp;==&amp;nbsp;&amp;#39;backup&amp;#39;)&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mr-&amp;gt;backup();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;script&amp;gt;document.location.href=&amp;#39;&amp;quot;&amp;nbsp;.&amp;nbsp;U(&amp;quot;Bak/index&amp;quot;)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;#39;&amp;lt;/script&amp;gt;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;$this-&amp;gt;success(&amp;nbsp;&amp;#39;数据库备份成功！&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif($_GET[&amp;#39;Action&amp;#39;]&amp;nbsp;==&amp;nbsp;&amp;#39;RL&amp;#39;)&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mr-&amp;gt;recover($_GET[&amp;#39;File&amp;#39;].&amp;#39;.sql&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;script&amp;gt;document.location.href=&amp;#39;&amp;quot;&amp;nbsp;.&amp;nbsp;U(&amp;quot;Bak/index&amp;quot;)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;#39;&amp;lt;/script&amp;gt;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;success(&amp;nbsp;&amp;#39;数据库还原成功！&amp;#39;);exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}elseif($_GET[&amp;#39;Action&amp;#39;]&amp;nbsp;==&amp;nbsp;&amp;#39;Del&amp;#39;)&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(@unlink($DataDir&amp;nbsp;.&amp;nbsp;$_GET[&amp;#39;File&amp;#39;].&amp;#39;.sql&amp;#39;))&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;$this-&amp;gt;success(&amp;#39;删除成功！&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;script&amp;gt;document.location.href=&amp;#39;&amp;quot;&amp;nbsp;.&amp;nbsp;U(&amp;quot;Bak/index&amp;quot;)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;#39;&amp;lt;/script&amp;gt;&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;error(&amp;#39;删除失败！&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($_GET[&amp;#39;Action&amp;#39;]&amp;nbsp;==&amp;nbsp;&amp;#39;download&amp;#39;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;DownloadFile($DataDir&amp;nbsp;.&amp;nbsp;$_GET[&amp;#39;file&amp;#39;].&amp;#39;.sql&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lists&amp;nbsp;=&amp;nbsp;$this-&amp;gt;MyScandir(&amp;#39;sqldatabak/&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;datadir&amp;nbsp;=&amp;nbsp;$DataDir;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;lists&amp;nbsp;=&amp;nbsp;&amp;nbsp;$lists;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;display();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}



&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/***
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;desc&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;读取sql数据
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;Author&amp;nbsp;&amp;nbsp;lizongyang
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;web&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;www.lizongyang.cn
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;email&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;1638500136
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;***/
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Public&amp;nbsp;function&amp;nbsp;MyScandir($FilePath&amp;nbsp;=&amp;nbsp;&amp;#39;./&amp;#39;,&amp;nbsp;$Order&amp;nbsp;=&amp;nbsp;0){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$FilePath&amp;nbsp;=&amp;nbsp;opendir($FilePath);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;(false&amp;nbsp;!==&amp;nbsp;($filename&amp;nbsp;=&amp;nbsp;readdir($FilePath)))
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$FileAndFolderAyy[]&amp;nbsp;=&amp;nbsp;$filename;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$Order&amp;nbsp;==&amp;nbsp;0&amp;nbsp;?&amp;nbsp;sort($FileAndFolderAyy)&amp;nbsp;:&amp;nbsp;rsort($FileAndFolderAyy);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$FileAndFolderAyy;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/***
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;desc&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;下载
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;Author&amp;nbsp;&amp;nbsp;lizongyang
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;web&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;www.lizongyang.cn
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;email&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;1638500136
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;***/
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;DownloadFile($fileName){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ob_end_clean();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;quot;Cache-Control:&amp;nbsp;must-revalidate,&amp;nbsp;post-check=0,&amp;nbsp;pre-check=0&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;#39;Content-Description:&amp;nbsp;File&amp;nbsp;Transfer&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;#39;Content-Type:&amp;nbsp;application/octet-stream&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;#39;Content-Length:&amp;nbsp;&amp;#39;&amp;nbsp;.&amp;nbsp;filesize($fileName));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;#39;Content-Disposition:&amp;nbsp;attachment;&amp;nbsp;filename=&amp;#39;&amp;nbsp;.&amp;nbsp;basename($fileName));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;readfile($fileName);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

}
//公共函数
//获取文件修改时间
function&amp;nbsp;getfiletime($file,&amp;nbsp;$DataDir)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$a&amp;nbsp;=&amp;nbsp;filemtime($DataDir&amp;nbsp;.&amp;nbsp;$file);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$time&amp;nbsp;=&amp;nbsp;date(&amp;quot;Y-m-d&amp;nbsp;H:i:s&amp;quot;,&amp;nbsp;$a);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$time;
}

//获取文件的大小
function&amp;nbsp;getfilesize($file,&amp;nbsp;$DataDir)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$perms&amp;nbsp;=&amp;nbsp;stat($DataDir&amp;nbsp;.&amp;nbsp;$file);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$size&amp;nbsp;=&amp;nbsp;$perms[&amp;#39;size&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;单位自动转换函数
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$kb&amp;nbsp;=&amp;nbsp;1024;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Kilobyte
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mb&amp;nbsp;=&amp;nbsp;1024&amp;nbsp;*&amp;nbsp;$kb;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Megabyte
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$gb&amp;nbsp;=&amp;nbsp;1024&amp;nbsp;*&amp;nbsp;$mb;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Gigabyte
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tb&amp;nbsp;=&amp;nbsp;1024&amp;nbsp;*&amp;nbsp;$gb;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Terabyte

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$kb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$size&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;B&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$mb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$kb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;KB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$gb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$mb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;MB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$tb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$gb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;GB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$tb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;TB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
}

?&amp;gt;&lt;/pre&gt;&lt;p&gt;3 html页面 &amp;nbsp;下载html页面 &amp;nbsp;&lt;img src=&quot;http://www.lizongyang.cn/static/manager/ueditor/dialogs/attachment/fileTypeImages/icon_rar.gif&quot; style=&quot;vertical-align: middle; margin-right: 2px;&quot;/&gt;&lt;a href=&quot;/Uploads/assets/2016-12-03/1480738745511190.zip&quot; title=&quot;index.html.zip&quot; style=&quot;font-size: 12px; color: rgb(0, 102, 204);&quot;&gt;index.html.zip&lt;/a&gt;&amp;nbsp;详情如下&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;include&amp;nbsp;file=&amp;quot;Public/style&amp;quot;&amp;nbsp;/&amp;gt;
&amp;lt;form&amp;nbsp;method=&amp;quot;post&amp;quot;&amp;nbsp;action=&amp;quot;&amp;quot;&amp;nbsp;id=&amp;quot;listform&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;lt;div&amp;nbsp;class=&amp;quot;panel&amp;nbsp;admin-panel&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;div&amp;nbsp;class=&amp;quot;panel-head&amp;quot;&amp;gt;&amp;lt;strong&amp;nbsp;class=&amp;quot;icon-reorder&amp;quot;&amp;gt;&amp;nbsp;数据库备份列表&amp;lt;/strong&amp;gt;&amp;nbsp;&amp;lt;a&amp;nbsp;href=&amp;quot;&amp;quot;&amp;nbsp;style=&amp;quot;float:right;&amp;nbsp;display:none;&amp;quot;&amp;gt;添加字段&amp;lt;/a&amp;gt;&amp;lt;/div&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;table&amp;nbsp;class=&amp;quot;table&amp;nbsp;table-hover&amp;nbsp;text-center&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tr&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;th&amp;nbsp;width=&amp;quot;100&amp;quot;&amp;nbsp;style=&amp;quot;text-align:left;&amp;nbsp;padding-left:20px;&amp;quot;&amp;gt;ID&amp;lt;/th&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;th&amp;nbsp;width=&amp;quot;30%&amp;quot;&amp;gt;文件名&amp;lt;/th&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;th&amp;nbsp;width=&amp;quot;20%&amp;quot;&amp;gt;备份时间&amp;lt;/th&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;th&amp;nbsp;width=&amp;quot;10%&amp;quot;&amp;gt;文件大小&amp;lt;/th&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;th&amp;nbsp;width=&amp;quot;10%&amp;quot;&amp;gt;操作&amp;lt;/th&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/tr&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;foreach&amp;nbsp;name=&amp;quot;lists&amp;quot;&amp;nbsp;key=&amp;quot;key&amp;quot;&amp;nbsp;item=&amp;quot;row&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;if&amp;nbsp;condition=&amp;quot;$key&amp;nbsp;gt&amp;nbsp;1&amp;quot;&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tr&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;td&amp;gt;{$key-1}&amp;lt;/td&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;td&amp;nbsp;&amp;gt;&amp;lt;a&amp;nbsp;href=&amp;quot;{:U(&amp;#39;Bak/index&amp;#39;,array(&amp;#39;Action&amp;#39;=&amp;gt;&amp;#39;download&amp;#39;,&amp;#39;file&amp;#39;=&amp;gt;$row))}&amp;quot;&amp;gt;{$row}&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;td&amp;gt;{$row|getfiletime=$datadir}&amp;lt;/td&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;td&amp;gt;{$row|getfilesize=$datadir}&amp;lt;/td&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;td&amp;gt;
