  `name` varchar(255) NOT NULL DEFAULT '' COMMENT '源文件名称',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=130 DEFAULT CHARSET=latin1 ROW_FORMAT=COMPACT;/* MySQLReback Separation */
