 DROP TABLE IF EXISTS `bk_cate`;/* MySQLReback Separation */ CREATE TABLE `bk_cate` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(64) NOT NULL DEFAULT '' COMMENT '类型名称',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '等级id',
  `isdel` tinyint(4) NOT NULL DEFAULT '0' COMMENT '删除标示',
  `url` varchar(50) NOT NULL DEFAULT '0' COMMENT '属于某个用户的分类， 0是系统分类，不允许删除和修改',
  `sort_no` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;/* MySQLReback Separation */
