 INSERT INTO `bk_article` VALUES ('16','PHP中把stdClass Object转array的几个方法','我们在经常使用API接口获取数据返回json数值的时候，往往单纯通过json_decode方法解析获得得数值一般并非数组，而是带有stdClass Objec的对象字符串，这时如果我们想获取相应的PHP数组时，需通过以下几种方法来获取。','31','&lt;p&gt;我们在经常使用API接口获取数据返回json数值的时候，往往单纯通过json_decode方法解析获得得数值一般并非数组，而是带有stdClass Objec的对象字符串，这时如果我们想获取相应的PHP数组时，需通过以下几种方法来获取。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;方法一：&lt;/p&gt;&lt;pre&gt;//PHP&amp;nbsp;stdClass&amp;nbsp;Object转array&amp;nbsp;&amp;nbsp;
function&amp;nbsp;object_array($array)&amp;nbsp;{&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(is_object($array))&amp;nbsp;{&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$array&amp;nbsp;=&amp;nbsp;(array)$array;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;if(is_array($array))&amp;nbsp;{&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach($array&amp;nbsp;as&amp;nbsp;$key=&amp;gt;$value)&amp;nbsp;{&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$array[$key]&amp;nbsp;=&amp;nbsp;object_array($value);&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$array;&amp;nbsp;&amp;nbsp;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;方法二：&lt;/p&gt;&lt;pre&gt;$array&amp;nbsp;=&amp;nbsp;json_decode(json_encode(simplexml_load_string($xmlString)),TRUE);&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;方法三：&lt;/p&gt;&lt;pre&gt;&amp;nbsp;function&amp;nbsp;object2array_pre(&amp;amp;$object)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(is_object($object))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;(array)($object);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;&amp;amp;$object;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(is_array($arr))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach($arr&amp;nbsp;as&amp;nbsp;$varName&amp;nbsp;=&amp;gt;&amp;nbsp;$varValue){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr[$varName]&amp;nbsp;=&amp;nbsp;$this-&amp;gt;object2array($varValue);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$arr;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;如果是10W的数据量的话，执行要进1s，结构再复杂些，可以达到3s， 性能太差了&lt;br style=&quot;color: rgb(17, 17, 17); font-family: 宋体, 微软雅黑, Verdana, Helvetica; line-height: 25px; background-color: rgb(255, 255, 255);&quot;/&gt;可以用以下替换：&lt;br style=&quot;color: rgb(17, 17, 17); font-family: 宋体, 微软雅黑, Verdana, Helvetica; line-height: 25px; background-color: rgb(255, 255, 255);&quot;/&gt;&amp;nbsp;&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;object2array(&amp;amp;$object)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$object&amp;nbsp;=&amp;nbsp;&amp;nbsp;json_decode(&amp;nbsp;json_encode(&amp;nbsp;$object),true);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;&amp;nbsp;$object;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;pre&gt;&amp;nbsp;但是对json的特性，只能是针对utf8的，否则得先转码下。&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','1','2016-11-06 14:21:59','0','0','35','0');/* MySQLReback Separation */
