}
$realip&amp;nbsp;=&amp;nbsp;preg_match(&amp;quot;/[d.]{7,15}/&amp;quot;,&amp;nbsp;$realip,&amp;nbsp;$matches)&amp;nbsp;?&amp;nbsp;$matches[0]&amp;nbsp;:&amp;nbsp;$unknown;
return&amp;nbsp;$realip;
}
