//&amp;nbsp;作者：[Kevin]&amp;nbsp;(C)&amp;nbsp;cnulink.com
//&amp;nbsp;时间：2013-1-10
//
//===================================
function&amp;nbsp;convertip($ip)&amp;nbsp;{
$ip1num&amp;nbsp;=&amp;nbsp;0;
$ip2num&amp;nbsp;=&amp;nbsp;0;
$ipAddr1&amp;nbsp;=&amp;quot;&amp;quot;;
$ipAddr2&amp;nbsp;=&amp;quot;&amp;quot;;
$dat_path&amp;nbsp;=&amp;nbsp;&amp;#39;./QQWry.Dat&amp;#39;;
