if($ip2num&amp;nbsp;&amp;lt;&amp;nbsp;0)&amp;nbsp;$ip2num&amp;nbsp;+=&amp;nbsp;pow(2,&amp;nbsp;32);
if($ip2num&amp;nbsp;&amp;lt;&amp;nbsp;$ipNum)&amp;nbsp;{
if($Middle&amp;nbsp;==&amp;nbsp;$BeginNum)&amp;nbsp;{
fclose($fd);
return&amp;nbsp;&amp;#39;Unknown&amp;#39;;
}
$BeginNum&amp;nbsp;=&amp;nbsp;$Middle;
}
}
$ipFlag&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;1);
if($ipFlag&amp;nbsp;==&amp;nbsp;chr(1))&amp;nbsp;{
$ipSeek&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;3);
