fclose($fd);
return&amp;nbsp;&amp;#39;System&amp;nbsp;Error&amp;#39;;
}
$ipSeek&amp;nbsp;=&amp;nbsp;implode(&amp;#39;&amp;#39;,&amp;nbsp;unpack(&amp;#39;L&amp;#39;,&amp;nbsp;$ipSeek.chr(0)));
fseek($fd,&amp;nbsp;$ipSeek);
$ipFlag&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;1);
}
if($ipFlag&amp;nbsp;==&amp;nbsp;chr(2))&amp;nbsp;{
$AddrSeek&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;3);
