fclose($fd);
return&amp;nbsp;&amp;#39;System&amp;nbsp;Error&amp;#39;;
}
$ipFlag&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;1);
if($ipFlag&amp;nbsp;==&amp;nbsp;chr(2))&amp;nbsp;{
