fseek($fd,&amp;nbsp;$AddrSeek);
while(($char&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;1))&amp;nbsp;!=&amp;nbsp;chr(0))
$ipAddr1&amp;nbsp;.=&amp;nbsp;$char;
}&amp;nbsp;else&amp;nbsp;{
fseek($fd,&amp;nbsp;-1,&amp;nbsp;SEEK_CUR);
while(($char&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;1))&amp;nbsp;!=&amp;nbsp;chr(0))
$ipAddr1&amp;nbsp;.=&amp;nbsp;$char;
$ipFlag&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;1);
if($ipFlag&amp;nbsp;==&amp;nbsp;chr(2))&amp;nbsp;{
$AddrSeek2&amp;nbsp;=&amp;nbsp;fread($fd,&amp;nbsp;3);
