 INSERT INTO `bk_article` VALUES ('25','php图片下载代码','php图片下载代码收藏，要的小伙伴拿去','48','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;php图片下载代码收藏，要的小伙伴拿去
&amp;lt;?php
/**
*php实现下载远程图片到本地
*@param&amp;nbsp;$url&amp;nbsp;string&amp;nbsp;远程文件地址
*@param&amp;nbsp;$filename&amp;nbsp;string&amp;nbsp;保存后的文件名（为空时则为随机生成的文件名，否则为原文件名）
*@param&amp;nbsp;$fileType&amp;nbsp;array&amp;nbsp;允许的文件类型
*@param&amp;nbsp;$dirName&amp;nbsp;string&amp;nbsp;文件保存的路径（路径其余部分根据时间系统自动生成）
*@param&amp;nbsp;$type&amp;nbsp;int&amp;nbsp;远程获取文件的方式
*@return&amp;nbsp;json&amp;nbsp;返回文件名、文件的保存路径
*
*&amp;nbsp;例子：{&amp;#39;fileName&amp;#39;:13668030896.jpg,&amp;nbsp;&amp;#39;saveDir&amp;#39;:/www/test/img/2013/04/24/}
*/
echo&amp;nbsp;getImage(&amp;#39;https://static.phpsong.com/wp-content/uploads/2015/03/2015032612213097652.jpg&amp;#39;,&amp;nbsp;&amp;#39;&amp;#39;,&amp;nbsp;&amp;#39;d:&amp;#39;,&amp;nbsp;array(&amp;#39;jpg&amp;#39;,&amp;nbsp;&amp;#39;gif&amp;#39;));
function&amp;nbsp;getImage($url,&amp;nbsp;$filename=&amp;#39;&amp;#39;,&amp;nbsp;$dirName,&amp;nbsp;$fileType,&amp;nbsp;$type=0)
{
if($url&amp;nbsp;==&amp;nbsp;&amp;#39;&amp;#39;){return&amp;nbsp;false;}
//获取文件原文件名
$defaultFileName&amp;nbsp;=&amp;nbsp;basename($url);
//获取文件类型
$suffix&amp;nbsp;=&amp;nbsp;substr(strrchr($url,&amp;#39;.&amp;#39;),&amp;nbsp;1);
if(!in_array($suffix,&amp;nbsp;$fileType)){
return&amp;nbsp;false;
}
//设置保存后的文件名
$filename&amp;nbsp;=&amp;nbsp;$filename&amp;nbsp;==&amp;nbsp;&amp;#39;&amp;#39;&amp;nbsp;?&amp;nbsp;time().rand(0,9).&amp;#39;.&amp;#39;.$suffix&amp;nbsp;:&amp;nbsp;$defaultFileName;
//获取远程文件资源
if($type){
$ch&amp;nbsp;=&amp;nbsp;curl_init();
$timeout&amp;nbsp;=&amp;nbsp;5;
curl_setopt($ch,&amp;nbsp;CURLOPT_URL,&amp;nbsp;$url);
curl_setopt($ch,&amp;nbsp;CURLOPT_RETURNTRANSFER,&amp;nbsp;1);
curl_setopt($ch,&amp;nbsp;CURLOPT_CONNECTTIMEOUT,&amp;nbsp;$timeout);
$file&amp;nbsp;=&amp;nbsp;curl_exec($ch);
curl_close($ch);
}else{
ob_start();
readfile($url);
$file&amp;nbsp;=&amp;nbsp;ob_get_contents();
ob_end_clean();
}
//设置文件保存路径
$dirName&amp;nbsp;=&amp;nbsp;$dirName.&amp;#39;/&amp;#39;.date(&amp;#39;Y&amp;#39;,&amp;nbsp;time()).&amp;#39;/&amp;#39;.date(&amp;#39;m&amp;#39;,&amp;nbsp;time()).&amp;#39;/&amp;#39;.date(&amp;#39;d&amp;#39;,time()).&amp;#39;/&amp;#39;;
if(!file_exists($dirName)){
mkdir($dirName,&amp;nbsp;0777,&amp;nbsp;true);
}
//保存文件
$res&amp;nbsp;=&amp;nbsp;fopen($dirName.$filename,&amp;#39;a&amp;#39;);
fwrite($res,$file);
fclose($res);
return&amp;nbsp;&amp;quot;{&amp;#39;fileName&amp;#39;:$filename,&amp;nbsp;&amp;#39;saveDir&amp;#39;:$dirName}&amp;quot;;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','1','2016-11-09 18:14:16','0','0','27','0');/* MySQLReback Separation */
