$str&amp;nbsp;=&amp;nbsp;str_replace(LD.&amp;#39;foo&amp;#39;.RD,&amp;nbsp;&amp;#39;bar&amp;#39;,&amp;nbsp;$str);&lt;/pre&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;TRUE, FALSE, 和 NULL&lt;/p&gt;&lt;p&gt;TRUE, FALSE, 和 NULL 关键字应该总是完全大写的。&lt;/p&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;不当的:&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;if&amp;nbsp;($foo&amp;nbsp;==&amp;nbsp;true)
$bar&amp;nbsp;=&amp;nbsp;false;
