$bar&amp;nbsp;=&amp;nbsp;FALSE;
function&amp;nbsp;foo($bar&amp;nbsp;=&amp;nbsp;NULL)&lt;/pre&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;逻辑操作符&lt;/p&gt;&lt;p&gt;|| 有时让人底气不足，不容易辨识，因为在某些输出设备上它不够清晰(可能看起来像数字11). &amp;amp;&amp;amp; 要优先于 AND ,不过两者都可以被接受, 使用 ! 时要在其前后都加一个空格。&lt;/p&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;不当的:&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;if&amp;nbsp;($foo&amp;nbsp;||&amp;nbsp;$bar)
if&amp;nbsp;($foo&amp;nbsp;AND&amp;nbsp;$bar)&amp;nbsp;//&amp;nbsp;可以，但有时不被常用的语法程序高亮推荐(高亮标识)
if&amp;nbsp;(!$foo)
if&amp;nbsp;(!&amp;nbsp;is_array($foo))&lt;/pre&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;恰当的:&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;if&amp;nbsp;($foo&amp;nbsp;OR&amp;nbsp;$bar)
if&amp;nbsp;($foo&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$bar)&amp;nbsp;//&amp;nbsp;推荐
if&amp;nbsp;(&amp;nbsp;!&amp;nbsp;$foo)
if&amp;nbsp;(&amp;nbsp;!&amp;nbsp;is_array($foo))&lt;/pre&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;比较返回值与类型映射&lt;/p&gt;&lt;p&gt;部分PHP函数执行失败时返回 FALSE, 但也可能有一个有效的返回值 “” 或 0, 它在松散比较中会被计算为FALSE. 在条件语句中使用这些返回值的时候，为了确保返回值是你所预期的类型而不是一个有着松散类型的值，请进行显式的比较。&lt;/p&gt;&lt;p&gt;在返回和检查你自己的变量时也要遵循这种严格的方法，必要时使用=== 和 !==。&lt;/p&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;不当的:&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul class=&quot;ul1 list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;如果&amp;nbsp;&amp;#39;foo&amp;#39;&amp;nbsp;位于此字符串的起始处，strpos将返回&amp;nbsp;0,
//&amp;nbsp;此处条件判断的结果为TRUE
if&amp;nbsp;(strpos($str,&amp;nbsp;&amp;#39;foo&amp;#39;)&amp;nbsp;==&amp;nbsp;FALSE)
