&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;static&amp;nbsp;function&amp;nbsp;checkDate($str){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$dateArr&amp;nbsp;=&amp;nbsp;explode(&amp;quot;-&amp;quot;,&amp;nbsp;$str);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(is_numeric($dateArr[0])&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;is_numeric($dateArr[1])&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;is_numeric($dateArr[2]))&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(($dateArr[0]&amp;nbsp;&amp;gt;=&amp;nbsp;1000&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$timeArr[0]&amp;nbsp;=&amp;nbsp;0&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$dateArr[1]&amp;nbsp;=&amp;nbsp;0&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$dateArr[2]&amp;nbsp;=&amp;nbsp;0&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$timeArr[0]&amp;nbsp;=&amp;nbsp;0&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$timeArr[1]&amp;nbsp;=&amp;nbsp;0&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$timeArr[2]&amp;nbsp;&amp;lt;=&amp;nbsp;59))
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;else
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;false;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;false;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;下面我们以ThinkPHP框架为示例来演示该类使用方法。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;本文以手机号验证作为实例，此验证类库还支持验证用户名、数字、邮箱、url、中文、长度、密码、日期和时间等，你可以很方便的应用到注册、登录等表单提交。&lt;/p&gt;&lt;p&gt;聪明的朋友也可以复制里面的正则，应用到js正则上。&lt;/p&gt;&lt;pre&gt;public&amp;nbsp;function&amp;nbsp;check()&amp;nbsp;{&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;import(&amp;#39;Org.Util.Verify&amp;#39;);&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$verify&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Verify();&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mobile&amp;nbsp;=&amp;nbsp;I(&amp;quot;post.mobile&amp;quot;);&amp;nbsp;
