&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(is_object($model))
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;model&amp;nbsp;=&amp;nbsp;&amp;amp;$model;

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;fields[&amp;#39;cid&amp;#39;]&amp;nbsp;=&amp;nbsp;$fields[&amp;#39;0&amp;#39;]&amp;nbsp;?&amp;nbsp;$fields[&amp;#39;0&amp;#39;]&amp;nbsp;:&amp;nbsp;&amp;#39;id&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;fields[&amp;#39;pid&amp;#39;]&amp;nbsp;=&amp;nbsp;$fields[&amp;#39;1&amp;#39;]&amp;nbsp;?&amp;nbsp;$fields[&amp;#39;1&amp;#39;]&amp;nbsp;:&amp;nbsp;&amp;#39;pid&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;fields[&amp;#39;name&amp;#39;]&amp;nbsp;=&amp;nbsp;$fields[&amp;#39;2&amp;#39;]&amp;nbsp;?&amp;nbsp;$fields[&amp;#39;2&amp;#39;]&amp;nbsp;:&amp;nbsp;&amp;#39;name&amp;#39;;
