	public&amp;nbsp;function&amp;nbsp;getIdArr($cid){
		&amp;nbsp;$cid&amp;nbsp;=&amp;nbsp;!empty($cid)&amp;nbsp;?&amp;nbsp;intval($cid)&amp;nbsp;:&amp;nbsp;0;
