 INSERT INTO `bk_article` VALUES ('139','QRCode PHP生成二维码类库','以下是生成二维码类库','116','&lt;p&gt;使用类库的方法&lt;/p&gt;&lt;pre&gt;include(&amp;quot;Common/QRCode.class.php&amp;quot;);
