&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$savePath.=$fileName;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$outUrl&amp;nbsp;=&amp;nbsp;&amp;quot;http://&amp;quot;&amp;nbsp;.&amp;nbsp;$_SERVER[&amp;#39;HTTP_HOST&amp;#39;]&amp;nbsp;.&amp;nbsp;$this-&amp;gt;path&amp;nbsp;.&amp;nbsp;$fileName;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(file_exists($savePath)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;filesize($savePath)&amp;nbsp;&amp;gt;&amp;nbsp;0)&amp;nbsp;{
