 INSERT INTO `bk_article` VALUES ('63','PHP文件下载','下面我们介绍一下如何使用PHP来下载文件。以下我们主要以thinkphp框架为实例。','96','&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;完整代码：&lt;/p&gt;&lt;pre&gt;public&amp;nbsp;function&amp;nbsp;downloadFile(){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$M&amp;nbsp;=&amp;nbsp;M($this-&amp;gt;tableName);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$map[&amp;#39;id&amp;#39;]&amp;nbsp;=&amp;nbsp;I(&amp;#39;fileId&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$info&amp;nbsp;=&amp;nbsp;$M-&amp;gt;where($map)-&amp;gt;find();
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$filepath&amp;nbsp;=&amp;nbsp;&amp;#39;.&amp;#39;.$info[&amp;#39;path&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(&amp;nbsp;!file_exists($filepath)&amp;nbsp;){
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;#39;文件不存在！&amp;#39;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$M-&amp;gt;where($map)-&amp;gt;setInc(&amp;#39;download&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$file&amp;nbsp;=&amp;nbsp;fopen($filepath,&amp;quot;r&amp;quot;);&amp;nbsp;//&amp;nbsp;打开文件
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;输入文件标签
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Header(&amp;quot;Content-type:&amp;nbsp;application/octet-stream&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Header(&amp;quot;Accept-Ranges:&amp;nbsp;bytes&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Header(&amp;quot;Accept-Length:&amp;nbsp;&amp;quot;.filesize($filepath));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Header(&amp;quot;Content-Disposition:&amp;nbsp;attachment;&amp;nbsp;filename=&amp;quot;&amp;nbsp;.&amp;nbsp;$info[&amp;#39;savename&amp;#39;]);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;输出文件内容
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;fread($file,filesize($filepath));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fclose($file);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;以上代码为thinkphp下的方法&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;$this-&amp;gt;tableName 为存储文件的数据表&lt;/p&gt;&lt;p&gt;根据fileId获取数据库响应文件&lt;/p&gt;&lt;p&gt;通过file_exists判断文件是否存在，如果不存在则输出错误信息&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;通过fopen打开文件&lt;/p&gt;&lt;p&gt;在更具Header方法处理打开文件，以及通过fread()方法把文件输出。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;以上方法如有不明白支出，可以留言一起讨论。&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:56:56','0','0','16','0');/* MySQLReback Separation */
