 INSERT INTO `bk_article` VALUES ('112','php加密算法之实现可逆加密算法和解密分享','对于大部分密码加密，我们可以采用md5、sha1等方法。可以有效防止数据泄露，但是这些方法仅适用于无需还原的数据加密。对于需要还原的信息，则需要采用可逆的加密解密算法,下面一组PHP函数是实现此加密解密的方法','120','&lt;!--/ .post-date--&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;对于大部分密码加密，我们可以采用md5、sha1等方法。可以有效防止数据泄露，但是这些方法仅适用于无需还原的数据加密。对于需要还原的信息，则需要采用可逆的加密解密算法,下面一组PHP函数是实现此加密解密的方法&lt;/p&gt;&lt;p&gt;加密算法如下：&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;encrypt($data,&amp;nbsp;$key)
{
&amp;nbsp;$key&amp;nbsp;=&amp;nbsp;md5($key);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$x&amp;nbsp;&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$len&amp;nbsp;=&amp;nbsp;strlen($data);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;&amp;nbsp;=&amp;nbsp;strlen($key);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$len;&amp;nbsp;$i++)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($x&amp;nbsp;==&amp;nbsp;$l)&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$x&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$char&amp;nbsp;.=&amp;nbsp;$key{$x};
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$x++;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$len;&amp;nbsp;$i++)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;.=&amp;nbsp;chr(ord($data{$i})&amp;nbsp;+&amp;nbsp;(ord($char{$i}))&amp;nbsp;%&amp;nbsp;256);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;base64_encode($str);
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;解密算法如下：&lt;/p&gt;&lt;pre&gt;function&amp;nbsp;decrypt($data,&amp;nbsp;$key)
{
&amp;nbsp;$key&amp;nbsp;=&amp;nbsp;md5($key);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$x&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;base64_decode($data);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$len&amp;nbsp;=&amp;nbsp;strlen($data);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;=&amp;nbsp;strlen($key);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$len;&amp;nbsp;$i++)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($x&amp;nbsp;==&amp;nbsp;$l)&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$x&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$char&amp;nbsp;.=&amp;nbsp;substr($key,&amp;nbsp;$x,&amp;nbsp;1);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$x++;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;$len;&amp;nbsp;$i++)
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(ord(substr($data,&amp;nbsp;$i,&amp;nbsp;1))&amp;nbsp;&amp;lt;&amp;nbsp;ord(substr($char,&amp;nbsp;$i,&amp;nbsp;1)))
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;.=&amp;nbsp;chr((ord(substr($data,&amp;nbsp;$i,&amp;nbsp;1))&amp;nbsp;+&amp;nbsp;256)&amp;nbsp;-&amp;nbsp;ord(substr($char,&amp;nbsp;$i,&amp;nbsp;1)));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;else
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str&amp;nbsp;.=&amp;nbsp;chr(ord(substr($data,&amp;nbsp;$i,&amp;nbsp;1))&amp;nbsp;-&amp;nbsp;ord(substr($char,&amp;nbsp;$i,&amp;nbsp;1)));
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$str;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;上述加密解密的过程均需要用到一个加密密钥（即参数$key）。&lt;/p&gt;&lt;pre&gt;$data&amp;nbsp;=&amp;nbsp;&amp;#39;PHP加密解密算法&amp;#39;;&amp;nbsp;&amp;nbsp;//&amp;nbsp;被加密信息
$key&amp;nbsp;=&amp;nbsp;&amp;#39;123&amp;#39;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;密钥
$encrypt&amp;nbsp;=&amp;nbsp;encrypt($data,&amp;nbsp;$key);
$decrypt&amp;nbsp;=&amp;nbsp;decrypt($encrypt,&amp;nbsp;$key);
echo&amp;nbsp;$encrypt,&amp;nbsp;&amp;quot;n&amp;quot;,&amp;nbsp;$decrypt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;上述将输出类似如下结果：&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;gniCSOzZG+HnS9zcFea7SefNGhXF
PHP加密解密算法&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;从上述结果可以看出，这是一组可逆的加密解密算法，可以用于部分需要还原的数据加密。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:28','0','0','21','0');/* MySQLReback Separation */
