 INSERT INTO `bk_article` VALUES ('116','PHP常用正则表达式','php正则表达式，我们在处理字符串或者出来字符等问题都离不开php正则表达式，我们可以通过正则表达式可以帮助我们很轻松地完成字符串匹配替换问题，下面就让我们来学习一些php正则表达式。','88','&lt;h3&gt;PHP常用正则表达式&lt;/h3&gt;&lt;p&gt;所属分类：PHP基础 编辑： 浏览：72384 发布时间：2014-06-15 14:59&lt;/p&gt;&lt;!--/ .post-date--&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;PHP常用正则表达式&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;header(&amp;quot;Content-Type:text/html;charset=utf-8&amp;quot;); &amp;nbsp;&lt;/p&gt;&lt;p&gt;这一句一般都是用于设置页面的字符集，防止出现乱码，虽然跟本节没多大关系，但也可以当作基础知识。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;//匹配英文域名网址：http,https。域名中没有下划线，后缀为字母&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^(https?://)?([a-zd.-]+).([a-z]+)$/i&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;www.liqingbo.cn&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配url&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^([a-z]+)://([^s]*)/i&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;http://blog.liqingbo.cn&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配IP地址&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?).){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;255.255.255.250&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配一个html标签&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^&amp;lt;([a-z]+)([^(.*)|s+/&amp;gt;)$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;菜根网&amp;#39;;
$res&amp;nbsp;=&amp;nbsp;preg_match_all($preg,&amp;nbsp;$str,&amp;nbsp;$matches);
var_dump($matches);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//从一段html中提取一张图片&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/]+(src=&amp;quot;([^&amp;quot;&amp;lt;&amp;gt;&amp;#39;]+)&amp;quot;|src=&amp;#39;([^&amp;quot;&amp;lt;&amp;gt;&amp;#39;]+)&amp;#39;)[^&amp;lt;&amp;gt;]*&amp;gt;/&amp;#39;;
$html&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;
$res&amp;nbsp;=&amp;nbsp;preg_match_all($preg,&amp;nbsp;$html,&amp;nbsp;$matches,&amp;nbsp;PREG_PATTERN_ORDER);
//var_dump($matches);&amp;nbsp;
echo&amp;nbsp;$matches[2][0];&amp;nbsp;//src&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配电子邮箱&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^([a-z0-9_.-]+)@([a-z0-9.-]+).([a-z]+)$/i&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;jeddy_liu-jin@gmail.com&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配密码&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^[a-z0-9@_.-]{6,18}$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;liujin@1234.com&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配用户名&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^[a-z0-9_-]{3,16}$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;liujin-88&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//国内座机&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^(0d{2,3})-?(d{7,8})$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;015-5415488&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//国内手机&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^1[3|4|5|8]d{9}$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;18012345678&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配邮编&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^[1-9]d{5}$/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;415000&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配身份证号&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/(^d{15}$)|(^d{18}$)/&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;430701198806520&amp;#39;;
echo&amp;nbsp;preg_match($preg,&amp;nbsp;$str);&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;//匹配汉字&lt;/p&gt;&lt;pre&gt;$preg&amp;nbsp;=&amp;nbsp;&amp;#39;/^[x{4e00}-x{9fa5}]+$/u&amp;#39;;
$str&amp;nbsp;=&amp;nbsp;&amp;#39;PHP博客&amp;#39;;
preg_match($preg,&amp;nbsp;$str,&amp;nbsp;$match);
var_dump($match);&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:29','0','0','19','0');/* MySQLReback Separation */
