 INSERT INTO `bk_article` VALUES ('117','php获取当前页面完整URL的代码','PHP或js获取当前页面完整URL的代码。
整理和收集：','87','&lt;p&gt;PHP或js获取当前页面完整URL的代码。&lt;/p&gt;&lt;p&gt;1，javascript代码：&lt;/p&gt;&lt;pre&gt;//顶级窗口的地址&amp;nbsp;
top.location.href&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;//&amp;nbsp;当前窗口的地址
this.location.href&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2，php代码：&lt;/p&gt;&lt;pre&gt;//获取域名或主机地址&amp;nbsp;
echo&amp;nbsp;$_SERVER[&amp;#39;HTTP_HOST&amp;#39;].&amp;quot;&amp;quot;;&lt;/pre&gt;&lt;p&gt;#localhost&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;//获取网页地址&amp;nbsp;
echo&amp;nbsp;$_SERVER[&amp;#39;PHP_SELF&amp;#39;].&amp;quot;&amp;quot;;&lt;/pre&gt;&lt;p&gt;#/blog/testurl.php&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;//获取网址参数&amp;nbsp;
echo&amp;nbsp;$_SERVER[&amp;quot;QUERY_STRING&amp;quot;].&amp;quot;&amp;quot;;&lt;/pre&gt;&lt;p&gt;#id=5&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;//获取用户代理&amp;nbsp;
echo&amp;nbsp;$_SERVER[&amp;#39;HTTP_REFERER&amp;#39;].&amp;quot;&amp;quot;;&lt;/pre&gt;&lt;pre&gt;//获取完整的url
echo&amp;nbsp;&amp;#39;http://&amp;#39;.$_SERVER[&amp;#39;HTTP_HOST&amp;#39;].$_SERVER[&amp;#39;REQUEST_URI&amp;#39;];
echo&amp;nbsp;&amp;#39;http://&amp;#39;.$_SERVER[&amp;#39;HTTP_HOST&amp;#39;].$_SERVER[&amp;#39;PHP_SELF&amp;#39;].&amp;#39;?&amp;#39;.$_SERVER[&amp;#39;QUERY_STRING&amp;#39;];&lt;/pre&gt;&lt;p&gt;#http://localhost/liqingbo/news.php?id=5&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;//包含端口号的完整url
echo&amp;nbsp;&amp;#39;http://&amp;#39;.$_SERVER[&amp;#39;SERVER_NAME&amp;#39;].&amp;#39;:&amp;#39;.$_SERVER[&amp;quot;SERVER_PORT&amp;quot;].$_SERVER[&amp;quot;REQUEST_URI&amp;quot;];&lt;/pre&gt;&lt;p&gt;#http://localhost:80/liqingbo/news.php?id=5&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;//只取路径
$url=&amp;#39;http://&amp;#39;.$_SERVER[&amp;#39;SERVER_NAME&amp;#39;].$_SERVER[&amp;quot;REQUEST_URI&amp;quot;];&amp;nbsp;
echo&amp;nbsp;dirname($url);&lt;/pre&gt;&lt;p&gt;#http://localhost/liqingbo&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br class=&quot;Apple-interchange-newline&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:52','0','0','25','0');/* MySQLReback Separation */
