 INSERT INTO `bk_article` VALUES ('118','PHP：XML操作','XML 是一个很实用的东西，存储数据、交换数据都很方便，但如何对 XML 进行基本的操作呢？
对 XML 的操作包括创建 XML 文档，添加、查看、修改、删除数据，操作方法有很多种，可以使用 DOMDocument 类、other、other 等都可以实现，本文介绍的是通过 DOMDocument 类进行操作。','86','&lt;h3&gt;PHP：XML操作&lt;/h3&gt;&lt;p&gt;所属分类：PHP基础 编辑： 浏览：56152 发布时间：2014-06-17 18:04&lt;/p&gt;&lt;!--/ .post-date--&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;h1&gt;PHP：XML操作&lt;/h1&gt;&lt;p&gt;XML 是一个很实用的东西，存储数据、交换数据都很方便，但如何对 XML 进行基本的操作呢？&lt;/p&gt;&lt;p&gt;对 XML 的操作包括创建 XML 文档，添加、查看、修改、删除数据，操作方法有很多种，可以使用 DOMDocument 类、other、other 等都可以实现，本文介绍的是通过 DOMDocument 类进行操作。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;h2&gt;DOMDocument 类&lt;/h2&gt;&lt;table class=&quot;tb_style4&quot; width=&quot;760&quot; style=&quot;width: 601px;&quot;&gt;&lt;tbody style=&quot;margin: 0px; padding: 0px; word-break: break-all;&quot;&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot; class=&quot;firstRow&quot;&gt;&lt;th style=&quot;margin: 0px; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); line-height: 2em; background-color: rgb(255, 230, 206);&quot;&gt;属性&lt;/th&gt;&lt;th style=&quot;margin: 0px; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); line-height: 2em; background-color: rgb(255, 230, 206);&quot;&gt;类型&lt;/th&gt;&lt;th style=&quot;margin: 0px; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); line-height: 2em; background-color: rgb(255, 230, 206);&quot;&gt;说明&lt;/th&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;documentElement&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMElement&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;XML 文档的根节点，允许直接访问文档各个子元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;documentURI&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;string&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;XML 文档的真实物理路径&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;encoding&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;string&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;XML 文档的编码方式&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;formatOutput&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;bool&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;格式化输出，友好地保留缩进和空格&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;preserveWhiteSpace&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;bool&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;文档保存时保留多余的空格输出&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;validateOnParse&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;bool&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;解析 XML 文档时是否验证文档格式&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;table class=&quot;tb_style4&quot; width=&quot;760&quot; style=&quot;width: 601px;&quot;&gt;&lt;tbody style=&quot;margin: 0px; padding: 0px; word-break: break-all;&quot;&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot; class=&quot;firstRow&quot;&gt;&lt;th style=&quot;margin: 0px; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); line-height: 2em; background-color: rgb(255, 230, 206);&quot;&gt;方法&lt;/th&gt;&lt;th style=&quot;margin: 0px; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); line-height: 2em; background-color: rgb(255, 230, 206);&quot;&gt;返回值类型&lt;/th&gt;&lt;th style=&quot;margin: 0px; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); line-height: 2em; background-color: rgb(255, 230, 206);&quot;&gt;说明&lt;/th&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;load(xmlfile)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;int&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;打开一个 xml 文档&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;save(xmlfile)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;int&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;保存一个 xml 文档&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;loadHTMLFILE(htmlfile)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;int&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;打开一个 html 文档&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;saveHTMLFILE(htmlfile)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;int&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;保存一个 html 文档&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;getElementById(id)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMNodeList&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;获取拥有指定 ID 的元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;getElementsByTagName(tag)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMNodeList&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;获取指定标签名称的元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;createElement(TagName[,Value])&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMElement&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;创建一个元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;getAttribute(AttrName)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMAttr&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;获取一个元素节点的属性&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;createAttribute(AttrName)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMAttr&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;创建一个元素节点的属性&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;setAttribute(AttrName,AttrValue)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMAttr&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;设置元素节点的属性和值&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;createTextNode(Value)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMText&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;创建一个文本内容&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;appendChild(DOMNode)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMNode&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;添加一个元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;removeChild(DOMNode)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMNode&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;移除一个元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;replaceChild(DOMNode)&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;DOMNode&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;替换一个元素节点&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;hasAttributes()&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;bool&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;判断一个元素节点是否有属性&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; word-break: break-all; background-color: rgb(253, 236, 219); background-position: initial initial; background-repeat: initial initial;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;hasChildNodes()&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;bool&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px 0px 0px 1em; word-break: break-all; color: rgb(67, 44, 35); line-height: 1.6em;&quot;&gt;判断一个元素节点是否有子节点&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;注意：以上的属性和方法只是此类的一部分，比较常用和实用，点此&lt;a href=&quot;http://www.php.net/manual/zh/class.domdocument.php#domdocument.props.documentelement&quot; target=&quot;_blank&quot;&gt;&amp;nbsp;查看全部&amp;nbsp;&lt;/a&gt;。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h2&gt;创建XML文档&lt;/h2&gt;&lt;p&gt;说明：创建XML文档之前必须对XML有所了解，本文不做赘述，请参考相关知识。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;PHP：create.php&lt;/p&gt;&lt;pre&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML=new&amp;nbsp;DOMDocument(&amp;quot;1.0&amp;quot;,&amp;quot;utf-8&amp;quot;);	//&amp;nbsp;实例化一个对象，并设置&amp;nbsp;XML&amp;nbsp;版本和编码
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;formatOutput=true;			//&amp;nbsp;格式化输出，保留缩进
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;preservaWhiteSpace=false;		//&amp;nbsp;不保留空格，这个是辅助格式化输出的
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root=$XML-&amp;gt;createElement(&amp;quot;Languages&amp;quot;);		//&amp;nbsp;创建根节点，有且只能有一个
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang1=$XML-&amp;gt;createElement(&amp;quot;Lang&amp;quot;,&amp;quot;Chinese&amp;quot;);	//&amp;nbsp;创建一个子节点，这是方法一
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang1-&amp;gt;setAttribute(&amp;quot;id&amp;quot;,&amp;quot;1001&amp;quot;);			//&amp;nbsp;设置子节点的属性
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2=$XML-&amp;gt;createElement(&amp;quot;Lang&amp;quot;);			//&amp;nbsp;创建一个子节点，这是方法二
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2_text=$XML-&amp;gt;createTextNode(&amp;quot;English&amp;quot;);	//&amp;nbsp;创建子节点的内容
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2_Attr_n=$XML-&amp;gt;createAttribute(&amp;quot;id&amp;quot;);		//&amp;nbsp;创建子节点的属性名称
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2_Attr_v=$XML-&amp;gt;createTextNode(&amp;quot;1002&amp;quot;);		//&amp;nbsp;创建子节点的属性内容
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2_Attr_n-&amp;gt;appendChild($lang2_Attr_v);		//&amp;nbsp;将属性内容赋值给属性名称
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2-&amp;gt;appendChild($lang2_text);			//&amp;nbsp;为创建的空子节点添加内容
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang2-&amp;gt;appendChild($lang2_Attr_n);			//&amp;nbsp;为创建的空子节点添加属性

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root-&amp;gt;appendChild($lang1);				//&amp;nbsp;添加子节点，不添加将不能显示&amp;nbsp;$lang1
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root-&amp;gt;appendChild($lang2);				//&amp;nbsp;添加子节点，不添加将不能显示&amp;nbsp;$lang2
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;appendChild($root);				//&amp;nbsp;最重要的一步：将根节点添加到文档里面
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;save(&amp;quot;language.xml&amp;quot;);				//&amp;nbsp;保存&amp;nbsp;XML&amp;nbsp;文档，路径是相对路径&lt;/pre&gt;&lt;p&gt;&lt;a style=&quot;margin: 0px 0px 0px 2em; padding: 0px; word-break: break-all; color: rgb(67, 44, 35); font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot; name=&quot;language_xml&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;XML：language.xml&lt;/p&gt;&lt;p&gt;&lt;!--?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;--&gt;&lt;!--?xml--&gt;&lt;/p&gt;&lt;p&gt;&lt;languages&gt;&lt;/languages&gt;&lt;/p&gt;&lt;p&gt;&lt;lang id=&quot;1001&quot;&gt;Chinese&lt;/lang&gt;&lt;/p&gt;&lt;p&gt;&lt;lang id=&quot;1002&quot;&gt;English&lt;/lang&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;提示：如果不格式化输出，XML 将会显示如下。&lt;/p&gt;&lt;p&gt;&lt;!--?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;--&gt;&lt;!--?xml--&gt;&lt;/p&gt;&lt;p&gt;&lt;languages&gt;&lt;lang id=&quot;1001&quot;&gt;Chinese&lt;/lang&gt;&lt;lang id=&quot;1002&quot;&gt;English&lt;/lang&gt;&lt;/languages&gt;&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h2&gt;读取XML数据&lt;/h2&gt;&lt;p&gt;说明：读取 XML 数据是通过获取 XML 中的节点然后再读取里面的内容的方式来实现的，获取节点有两种方法：getElementById() 和 getElementsByTagName()。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h3&gt;①. getElementById()&lt;/h3&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;XML：book.xml（打开）&lt;/p&gt;&lt;p&gt;PHP：read.php&lt;/p&gt;&lt;pre&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML=new&amp;nbsp;DOMDocument(&amp;quot;1.0&amp;quot;,&amp;quot;utf-8&amp;quot;);	//&amp;nbsp;实例化一个对象，并设置&amp;nbsp;XML&amp;nbsp;版本和编码
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;validateOnParse=true;			//&amp;nbsp;开启验证，DTD&amp;nbsp;验证文档格式
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;load(&amp;quot;language.xml&amp;quot;);			//&amp;nbsp;打开一个&amp;nbsp;XML&amp;nbsp;文件
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$langs=$XML-&amp;gt;getElementById(&amp;quot;b002&amp;quot;);	//&amp;nbsp;获取&amp;nbsp;id&amp;nbsp;为&amp;nbsp;&amp;quot;b002&amp;quot;&amp;nbsp;的节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$langs-&amp;gt;nodeValue;			//&amp;nbsp;输出&amp;nbsp;《西游记》&lt;/pre&gt;&lt;p&gt;注意！！！！！！这个函数比较特殊，需要注意以下几点：&lt;/p&gt;&lt;p&gt;第一：请仔细看 book.xml，这个文档是含有 DTD（文档类型定义），第 2-5 行，不然函数无法识别 ID。&lt;/p&gt;&lt;p&gt;第二：再次观看 book.xml，元素属性值必须以字母开头，否则此函数会闹情绪出现小错误。&lt;/p&gt;&lt;p&gt;第三：要想使用此函数必须开启 XML 验证，即将参数 validateOnParse 设置为 true 。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h3&gt;②. getElementsByTagName()&lt;/h3&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;XML：language.xml（打开）&lt;/p&gt;&lt;p&gt;PHP：read.php&lt;/p&gt;&lt;pre&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML=new&amp;nbsp;DOMDocument(&amp;quot;1.0&amp;quot;,&amp;quot;utf-8&amp;quot;);	//&amp;nbsp;实例化一个对象，并设置&amp;nbsp;XML&amp;nbsp;版本和编码
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;load(&amp;quot;language.xml&amp;quot;);			//&amp;nbsp;打开一个&amp;nbsp;XML&amp;nbsp;文件
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$langs=$XML-&amp;gt;getElementsByTagName(&amp;quot;Lang&amp;quot;);	//&amp;nbsp;通过标签名获取指定的节点集合
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang=$langs-&amp;gt;item(1);			//&amp;nbsp;通过&amp;nbsp;item()&amp;nbsp;获取指定节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$lang-&amp;gt;nodeValue;			//&amp;nbsp;显示节点的文本内容，English&lt;/pre&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h2&gt;修改XML数据&lt;/h2&gt;&lt;p&gt;说明：修改 XML 数据是通过获取 XML 中的节点然后再对节点赋值的方式来实现的。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;XML：media.xml（打开）&lt;/p&gt;&lt;p&gt;PHP：edit.php&lt;/p&gt;&lt;pre&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML=new&amp;nbsp;DOMDocument(&amp;quot;1.0&amp;quot;,&amp;quot;utf-8&amp;quot;);	//&amp;nbsp;实例化一个对象，并设置&amp;nbsp;XML&amp;nbsp;版本和编码
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;load(&amp;quot;language.xml&amp;quot;);			//&amp;nbsp;打开一个&amp;nbsp;XML&amp;nbsp;文件
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$langs=$XML-&amp;gt;getElementsByTagName(&amp;quot;Lang&amp;quot;);	//&amp;nbsp;通过标签名获取指定的节点集合
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang=$langs-&amp;gt;item(1);			//&amp;nbsp;通过&amp;nbsp;item()&amp;nbsp;获取指定节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang-&amp;gt;nodeValue=&amp;quot;Japanese&amp;quot;;		//&amp;nbsp;为节点赋值，Japanese
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;save(&amp;quot;language.xml&amp;quot;);			//&amp;nbsp;亲，记得保存&lt;/pre&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h2&gt;删除XML数据&lt;/h2&gt;&lt;p&gt;说明：删除 XML 数据是通过获取 XML 中的节点集合然后再移除子节点的方式来实现的。&lt;/p&gt;&lt;p&gt;注意：在执行删除操作之前，一定得弄清楚一点，只有父节点才能删除子节点，其他的都无法删除。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;h3&gt;①. 删除单行嵌套的数据&lt;/h3&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;XML：language.xml（打开）&lt;/p&gt;&lt;p&gt;PHP：delete.php&lt;/p&gt;&lt;pre&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML=new&amp;nbsp;DOMDocument(&amp;quot;1.0&amp;quot;,&amp;quot;utf-8&amp;quot;);	//&amp;nbsp;实例化一个对象，并设置&amp;nbsp;XML&amp;nbsp;版本和编码
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;load(&amp;quot;language.xml&amp;quot;);			//&amp;nbsp;打开一个&amp;nbsp;XML&amp;nbsp;文件
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root=$XML-&amp;gt;documentElement;		//&amp;nbsp;获取根节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$parent=$XML-&amp;gt;getElementsByTagName(&amp;quot;Languages&amp;quot;)-&amp;gt;item(0);	//&amp;nbsp;获取指定的节点集合的父元素
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$langs=$XML-&amp;gt;getElementsByTagName(&amp;quot;Lang&amp;quot;);			//&amp;nbsp;获取指定的节点集合
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lang=$langs-&amp;gt;item(1);					//&amp;nbsp;通过&amp;nbsp;item()&amp;nbsp;获取指定节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;下面的三行代码用于测试，测试三次，每次只使用其中一行，通过对比来验证结论！
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;removeChild($lang);			//&amp;nbsp;错误，$XML&amp;nbsp;不是父节点（删除此行程序会正常）
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root-&amp;gt;removeChild($lang);			//&amp;nbsp;正确，$root&amp;nbsp;恰巧是父节点，因为嵌套只有一层
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$parent-&amp;gt;removeChild($lang);		//&amp;nbsp;正确，$parent&amp;nbsp;是真正的父节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;save(&amp;quot;language.xml&amp;quot;);			//&amp;nbsp;亲，记得保存&lt;/pre&gt;&lt;h3&gt;②. 删除多行嵌套的数据&lt;/h3&gt;&lt;p&gt;&lt;br style=&quot;margin: 0px; padding: 0px; word-break: break-all; font-family: 微软雅黑, 幼圆; font-size: medium; white-space: normal;&quot;/&gt;&lt;/p&gt;&lt;p&gt;XML：media.xml（打开）&lt;/p&gt;&lt;p&gt;PHP：delete.php&lt;/p&gt;&lt;pre&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML=new&amp;nbsp;DOMDocument(&amp;quot;1.0&amp;quot;,&amp;quot;utf-8&amp;quot;);	//&amp;nbsp;实例化一个对象，并设置&amp;nbsp;XML&amp;nbsp;版本和编码
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;load(&amp;quot;media.xml&amp;quot;);			//&amp;nbsp;打开一个&amp;nbsp;XML&amp;nbsp;文件
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root=$XML-&amp;gt;documentElement;		//&amp;nbsp;获取根节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$parent=$XML-&amp;gt;getElementsByTagName(&amp;quot;music&amp;quot;)-&amp;gt;item(0);	//&amp;nbsp;获取指定的节点集合的父元素
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$songs=$XML-&amp;gt;getElementsByTagName(&amp;quot;song&amp;quot;);			//&amp;nbsp;获取指定的节点集合
	
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach($songs&amp;nbsp;as&amp;nbsp;$song)				//&amp;nbsp;遍历要操作的整个节点集合
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($song-&amp;gt;getAttribute(&amp;quot;id&amp;quot;)==&amp;quot;102&amp;quot;)		//&amp;nbsp;删除&amp;nbsp;id&amp;nbsp;为102的节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;调试四次，每次只使用其中一行！
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;removeChild($song);			//&amp;nbsp;错误，$XML&amp;nbsp;不是父节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$root-&amp;gt;removeChild($song);			//&amp;nbsp;错误，$root&amp;nbsp;不是父节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$songs-&amp;gt;removeChild($song);			//&amp;nbsp;错误，$songs&amp;nbsp;不是父节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$parent-&amp;gt;removeChild($song);		//&amp;nbsp;正确，$parent&amp;nbsp;是真正的父节点
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$XML-&amp;gt;save(&amp;quot;media.xml&amp;quot;);			//&amp;nbsp;亲，全部讲完了，记得好评呢~&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','2016-11-15 10:57:52','0','0','22','0');/* MySQLReback Separation */
