 INSERT INTO `bk_article` VALUES ('136','A​p​a​c​h​e​2​.​4​.​7​_​+​_​P​H​P​5​.​5​.​7​_​+​_​M​y​S​Q​L​','到官网下载最新版的：
http://www.apache.org/    httpd-2.4.7-win32-VC11.zip
http://www.php.net/       php-5.5.7-Win32-VC11-x86.zip
http://www.mysql.com/     mysql-5.6.15-win32.zip','72','&lt;p&gt;到官网下载最新版的：&lt;/p&gt;&lt;p&gt;http://www.apache.org/&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;httpd-2.4.7-win32-VC11.zip&lt;/p&gt;&lt;p&gt;http://www.php.net/&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;php-5.5.7-Win32-VC11-x86.zip&lt;/p&gt;&lt;p&gt;http://www.mysql.com/&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;mysql-5.6.15-win32.zip&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;#################&lt;/p&gt;&lt;p&gt;Apache配置&lt;/p&gt;&lt;p&gt;#################&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;在Apache根目录中的conf目录中找到&lt;span style=&quot;font-family: sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;httpd.conf文件。复制备份到当前目录。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;用记事本或Editplus打开httpd.conf。&lt;/p&gt;&lt;p&gt;（注意：在下面配置中涉及到路径时。记得把&amp;quot;&amp;quot;全部换成&amp;quot;/&amp;quot;）&lt;span style=&quot;font-family: sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;ServerRoot&amp;nbsp;&lt;/p&gt;&lt;p&gt;后面的&amp;nbsp;&amp;quot;c:/Apache24&amp;quot;&amp;nbsp;&lt;/p&gt;&lt;p&gt;改成&amp;nbsp;安装apache的目录。（如：&amp;quot;D:/apache&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;#LoadModule&lt;/p&gt;&lt;p&gt;在一系列#LoadModule行的最后一行下面加上&lt;/p&gt;&lt;p&gt;LoadModule&amp;nbsp;php5_module&amp;nbsp;D:/php/php5apache2_4.dll&amp;nbsp;&amp;nbsp;#注意：php5apache2_4.dll中的2_4是对应apache的版本号的，如果是apache2.2版本则是php5apache2_2.dll&lt;/p&gt;&lt;p&gt;PHPIniDir&amp;nbsp;D:/php&amp;nbsp;&amp;nbsp;#该目录是php.ini所在目录即安装PHP的根目录。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;#ServerName&lt;/p&gt;&lt;p&gt;把后面的www.example.com:80&lt;/p&gt;&lt;p&gt;改为：127.0.0.1:80&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;DocumentRoot&lt;/p&gt;&lt;p&gt;把后面的&amp;quot;c:/Apache24/htdocs&amp;quot;&lt;/p&gt;&lt;p&gt;改为你的网站根目录（如：&amp;quot;D:/Workspace/MyWeb/htdocs&amp;quot;）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;&amp;lt;Directory&lt;/p&gt;&lt;p&gt;把后面的&amp;quot;c:/Apache24/htdocs&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;改为你的网站根目录（如：&amp;quot;D:/Workspace/MyWeb/htdocs&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;&lt;ifmodule dir_module=&quot;&quot;&gt;&lt;/ifmodule&gt;&lt;/p&gt;&lt;p&gt;在&amp;nbsp;DirectoryIndex&amp;nbsp;index.html　后面加上index.php&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;ScriptAlias&lt;/p&gt;&lt;p&gt;把后面的路径&amp;quot;c:/Apache24/cgi-bin/&amp;quot;&lt;/p&gt;&lt;p&gt;改为安装apache的cgi-bin目录（如：&amp;quot;D:/apache/cgi-bin/&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;AddType&amp;nbsp;application/x-gzip&amp;nbsp;.gz&amp;nbsp;.tgz&lt;/p&gt;&lt;p&gt;在下面一行加上&lt;/p&gt;&lt;p&gt;AddType&amp;nbsp;application/x-httpd-php&amp;nbsp;.php&lt;/p&gt;&lt;p&gt;;;;;;;;;;;;;;;;;;;;;;;;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PHP配置&lt;/p&gt;&lt;p&gt;;;;;;;;;;;;;;;;;;;;;;;;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到PHP根目录里的php.ini-development文件，复制备份到根目录。&lt;/p&gt;&lt;p&gt;把备份文件重命名为php.ini&lt;/p&gt;&lt;p&gt;用记事本或Editplus打开。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;;extension=php_mysql.dll&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;使PHP可使用mysql&lt;/p&gt;&lt;p&gt;;extension=php_mysqli.dll&amp;nbsp;&amp;nbsp;&amp;nbsp;;使PHP可使用mysqli&lt;/p&gt;&lt;p&gt;去掉前边的&amp;nbsp;;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;;&amp;nbsp;extension_dir&lt;/p&gt;&lt;p&gt;去掉前面的;并把后面的路径改成php的ext目录&lt;/p&gt;&lt;p&gt;（如：&amp;quot;D:phpext&amp;quot;）&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;;date.timezone&lt;/p&gt;&lt;p&gt;改为：&lt;/p&gt;&lt;p&gt;date.timezone&amp;nbsp;=&amp;nbsp;Asia/Shanghai&amp;nbsp;&amp;nbsp;;如果不改为这个出现日期会报错&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;error_reporting&amp;nbsp;=&amp;nbsp;E_ALL&amp;nbsp;&lt;/p&gt;&lt;p&gt;加上前边的&amp;nbsp;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;这个是错误报告。如果不注释掉总是报错（此处参考APMServ的配置）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;######################&lt;/p&gt;&lt;p&gt;MySQL配置&lt;/p&gt;&lt;p&gt;######################&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;在mysql的跟目录中找到my-default.ini文件&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;复制备份到当前目录。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;把备份文件重命名为my.ini，&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;用记事本或Editplus打开。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;找到：&lt;/p&gt;&lt;p&gt;#&amp;nbsp;&amp;nbsp;basedir&amp;nbsp;=&amp;nbsp;.....&lt;/p&gt;&lt;p&gt;#&amp;nbsp;&amp;nbsp;datadir&amp;nbsp;=&amp;nbsp;.....&lt;/p&gt;&lt;p&gt;#&amp;nbsp;&amp;nbsp;port&amp;nbsp;=&amp;nbsp;.....&lt;/p&gt;&lt;p&gt;改为：&lt;/p&gt;&lt;p&gt;basedir&amp;nbsp;=&amp;nbsp;D:/mysql&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql的根目录&lt;/p&gt;&lt;p&gt;datadir&amp;nbsp;=&amp;nbsp;D:/mysql/data&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql的数据目录&lt;/p&gt;&lt;p&gt;port&amp;nbsp;=&amp;nbsp;3306&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql的端口&lt;/p&gt;&lt;p&gt;并添加：&lt;/p&gt;&lt;p&gt;character_set_server&amp;nbsp;=&amp;nbsp;utf8&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql字符集&lt;/p&gt;&lt;p&gt;[mysql]&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;default-character-set&amp;nbsp;=&amp;nbsp;utf8&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#mysql字符集&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;原文来自：&lt;a href=&quot;http://wenku.baidu.com/view/d9490f1b804d2b160b4ec0a3.html&quot;&gt;http://wenku.baidu.com/view/d9490f1b804d2b160b4ec0a3.html&lt;/a&gt;&lt;/p&gt;','19','2016-11-15 10:57:54','0','0','29','0');/* MySQLReback Separation */
