 INSERT INTO `bk_article` VALUES ('334','PHP将一个字符串转换成数组，支持中文','/**
 * 将一个字符串转换成数组，支持中文
 * @param string    $string   待转换成数组的字符串
 * @return string   转换后的数组
 */','119','&lt;p&gt;PHP将一个字符串转换成数组，支持中文&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**
&amp;nbsp;*&amp;nbsp;将一个字符串转换成数组，支持中文
&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;&amp;nbsp;&amp;nbsp;待转换成数组的字符串
&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;转换后的数组
&amp;nbsp;*/
function&amp;nbsp;strToArray($string)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$strlen&amp;nbsp;=&amp;nbsp;mb_strlen($string);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;($strlen)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$array[]&amp;nbsp;=&amp;nbsp;mb_substr($string,&amp;nbsp;0,&amp;nbsp;1,&amp;nbsp;&amp;quot;utf8&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$string&amp;nbsp;=&amp;nbsp;mb_substr($string,&amp;nbsp;1,&amp;nbsp;$strlen,&amp;nbsp;&amp;quot;utf8&amp;quot;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$strlen&amp;nbsp;=&amp;nbsp;mb_strlen($string);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$array;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;用法：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;$string&amp;nbsp;=&amp;nbsp;&amp;#39;这里就是要转换成数组的字符串，www.liqingbo.cn&amp;#39;;
$arr&amp;nbsp;=&amp;nbsp;strToArray($string);
print_r($arr);&lt;/pre&gt;&lt;p&gt;输出：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;Array
(
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[0]&amp;nbsp;=&amp;gt;&amp;nbsp;这
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[1]&amp;nbsp;=&amp;gt;&amp;nbsp;里
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[2]&amp;nbsp;=&amp;gt;&amp;nbsp;就
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[3]&amp;nbsp;=&amp;gt;&amp;nbsp;是
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[4]&amp;nbsp;=&amp;gt;&amp;nbsp;要
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[5]&amp;nbsp;=&amp;gt;&amp;nbsp;转
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[6]&amp;nbsp;=&amp;gt;&amp;nbsp;换
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[7]&amp;nbsp;=&amp;gt;&amp;nbsp;成
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[8]&amp;nbsp;=&amp;gt;&amp;nbsp;数
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[9]&amp;nbsp;=&amp;gt;&amp;nbsp;组
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[10]&amp;nbsp;=&amp;gt;&amp;nbsp;的
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[11]&amp;nbsp;=&amp;gt;&amp;nbsp;字
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[12]&amp;nbsp;=&amp;gt;&amp;nbsp;符
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[13]&amp;nbsp;=&amp;gt;&amp;nbsp;串
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[14]&amp;nbsp;=&amp;gt;&amp;nbsp;，
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[15]&amp;nbsp;=&amp;gt;&amp;nbsp;w
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[16]&amp;nbsp;=&amp;gt;&amp;nbsp;w
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[17]&amp;nbsp;=&amp;gt;&amp;nbsp;w
