 INSERT INTO `bk_article` VALUES ('331','PHP字符串截取长度自定义方法cut_带省略号','我们下面就来介绍一下如何通过PHP自定义函数来截取我们想要截取的字符长度，超出部分用省略号代替或者隐藏。','119','&lt;p&gt;我们下面就来介绍一下如何通过PHP自定义函数来截取我们想要截取的字符长度，超出部分用省略号代替或者隐藏。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;字符串截取方法：&lt;/p&gt;&lt;pre&gt;//截取字符串长度
function&amp;nbsp;cut($Str,&amp;nbsp;$Length,$more=true)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$Str为截取字符串，$Length为需要截取的长度
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;global&amp;nbsp;$s;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$i&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;=&amp;nbsp;0;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$ll&amp;nbsp;=&amp;nbsp;strlen($Str);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$s&amp;nbsp;=&amp;nbsp;$Str;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$f&amp;nbsp;=&amp;nbsp;true;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;while&amp;nbsp;($i&amp;nbsp;&amp;lt;=&amp;nbsp;$ll)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(ord($Str{$i})&amp;nbsp;&amp;lt;&amp;nbsp;0x80)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l++;&amp;nbsp;$i++;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;(ord($Str{$i})&amp;nbsp;&amp;lt;&amp;nbsp;0xe0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l++;&amp;nbsp;$i&amp;nbsp;+=&amp;nbsp;2;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;(ord($Str{$i})&amp;nbsp;&amp;lt;&amp;nbsp;0xf0)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;+=&amp;nbsp;2;&amp;nbsp;$i&amp;nbsp;+=&amp;nbsp;3;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;(ord($Str{$i})&amp;nbsp;&amp;lt;&amp;nbsp;0xf8)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;+=&amp;nbsp;1;&amp;nbsp;$i&amp;nbsp;+=&amp;nbsp;4;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;(ord($Str{$i})&amp;nbsp;&amp;lt;&amp;nbsp;0xfc)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;+=&amp;nbsp;1;&amp;nbsp;$i&amp;nbsp;+=&amp;nbsp;5;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;(ord($Str{$i})&amp;nbsp;&amp;lt;&amp;nbsp;0xfe)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$l&amp;nbsp;+=&amp;nbsp;1;&amp;nbsp;$i&amp;nbsp;+=&amp;nbsp;6;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(($l&amp;nbsp;&amp;gt;=&amp;nbsp;$Length&amp;nbsp;-&amp;nbsp;1)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$f)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$s&amp;nbsp;=&amp;nbsp;substr($Str,&amp;nbsp;0,&amp;nbsp;$i);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$f&amp;nbsp;=&amp;nbsp;false;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(($l&amp;nbsp;&amp;gt;&amp;nbsp;$Length)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;($i&amp;nbsp;&amp;lt;&amp;nbsp;$ll)&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$more)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$s&amp;nbsp;=&amp;nbsp;$s&amp;nbsp;.&amp;nbsp;&amp;#39;...&amp;#39;;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;&amp;nbsp;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//如果进行了截取，字符串末尾加省略符号“...”
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$s;
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;使用方法：&lt;/p&gt;&lt;pre&gt;$str&amp;nbsp;=&amp;nbsp;&amp;#39;看看截取到哪里？&amp;#39;;
echo&amp;nbsp;cut($str,1);
echo&amp;nbsp;&amp;#39;
&amp;#39;;
echo&amp;nbsp;cut($str,4);
echo&amp;nbsp;&amp;#39;
&amp;#39;;
echo&amp;nbsp;cut($str,5);
echo&amp;nbsp;&amp;#39;
&amp;#39;;
echo&amp;nbsp;cut($str,5,false);
echo&amp;nbsp;&amp;#39;
&amp;#39;;

$str&amp;nbsp;=&amp;nbsp;&amp;#39;中英文混合看看hello?&amp;#39;;
echo&amp;nbsp;cut($str,18);
echo&amp;nbsp;&amp;#39;
&amp;#39;;
echo&amp;nbsp;cut($str,50);&lt;/pre&gt;&lt;p&gt;输出：&lt;/p&gt;&lt;pre&gt;看...
看看...
看看...
看看
中英文混合看看hel...
中英文混合看看hello?&lt;/pre&gt;&lt;p&gt;解释：一般UTF-8格式为3个字节，而GBK兼容gb2312一般都是2个字节，以上以UTF-8编码为实例。&lt;/p&gt;&lt;p&gt;通过第三个参数$more可以开关省略号模式，默认为true为带省略号，false为没有省略号。&lt;/p&gt;','21','2016-11-15 15:09:47','0','0','26','0');/* MySQLReback Separation */
