 INSERT INTO `bk_article` VALUES ('327','PHP格式化字节大小_自定义方法','PHP格式化字节大小的方法。','119','&lt;p&gt;PHP格式化字节大小的方法。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre&gt;/**
*&amp;nbsp;格式化字节大小
*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;number&amp;nbsp;$size&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;字节数
*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$delimiter&amp;nbsp;数字和单位分隔符
*&amp;nbsp;@return&amp;nbsp;string&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;格式化后的带单位的大小
&amp;nbsp;*/
function&amp;nbsp;get_byte($size,&amp;nbsp;$delimiter&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$units&amp;nbsp;=&amp;nbsp;array(&amp;#39;B&amp;#39;,&amp;nbsp;&amp;#39;KB&amp;#39;,&amp;nbsp;&amp;#39;MB&amp;#39;,&amp;nbsp;&amp;#39;GB&amp;#39;,&amp;nbsp;&amp;#39;TB&amp;#39;,&amp;nbsp;&amp;#39;PB&amp;#39;);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;($i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;$size&amp;nbsp;&amp;gt;=&amp;nbsp;1024&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$i&amp;nbsp;&amp;lt;&amp;nbsp;5;&amp;nbsp;$i++)&amp;nbsp;$size&amp;nbsp;/=&amp;nbsp;1024;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;$delimiter&amp;nbsp;.&amp;nbsp;$units[$i];
}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;用法：&lt;/p&gt;&lt;pre&gt;$size&amp;nbsp;=&amp;nbsp;&amp;#39;5454646&amp;#39;;
echo&amp;nbsp;get_byte($size);&lt;/pre&gt;&lt;p&gt;输出：&lt;/p&gt;&lt;pre&gt;5.2MB&lt;/pre&gt;&lt;p&gt;解释：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;参数$size传入字节数，通过方法get_byte将单位为kb的字节转换成MB并返回。&lt;/p&gt;','21','2016-11-15 15:09:47','0','0','36','0');/* MySQLReback Separation */
