&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Public&amp;nbsp;function&amp;nbsp;MyScandir($FilePath&amp;nbsp;=&amp;nbsp;&amp;#39;./&amp;#39;,&amp;nbsp;$Order&amp;nbsp;=&amp;nbsp;0){
