//获取文件的大小
function&amp;nbsp;getfilesize($file,&amp;nbsp;$DataDir)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$perms&amp;nbsp;=&amp;nbsp;stat($DataDir&amp;nbsp;.&amp;nbsp;$file);
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$size&amp;nbsp;=&amp;nbsp;$perms[&amp;#39;size&amp;#39;];
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;单位自动转换函数
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$kb&amp;nbsp;=&amp;nbsp;1024;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Kilobyte
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$mb&amp;nbsp;=&amp;nbsp;1024&amp;nbsp;*&amp;nbsp;$kb;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Megabyte
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$gb&amp;nbsp;=&amp;nbsp;1024&amp;nbsp;*&amp;nbsp;$mb;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Gigabyte
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tb&amp;nbsp;=&amp;nbsp;1024&amp;nbsp;*&amp;nbsp;$gb;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;Terabyte

&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$kb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$size&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;B&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$mb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$kb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;KB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$gb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$mb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;MB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if&amp;nbsp;($size&amp;nbsp;&amp;lt;&amp;nbsp;$tb)&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$gb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;GB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;round($size&amp;nbsp;/&amp;nbsp;$tb,&amp;nbsp;2)&amp;nbsp;.&amp;nbsp;&amp;quot;&amp;nbsp;TB&amp;quot;;
&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}
}

