(
[myint1] =&gt; 80
[myint2] =&gt; 72
[myint3] =&gt; 80
)</pre>
<h3></h3>
<h3>例子 3</h3>
<pre class=\"brush:php;toolbar:false;\">&lt;?php
$bin = pack(\"c2n2\",0x1234,0x5678,65,66);
